INSERT INTO `txp_form` (`name`, `type`, `Form`) VALUES
('Links', 'link', '<txp:zem_prblock target="nofollow">\r\n<p><txp:link /><br />\r\n<txp:link_description /></p>\r\n</txp:zem_prblock>'),
('comments', 'comment', '<div class="comment"><txp:comment_message /><small>From <txp:comment_name /> &#183; <txp:comment_time /></small></div>'),
('default', 'article', '<txp:zem_prblock type="nofollow">\r\n<div class="title">\r\n<h4 class="sifr"><txp:title /></h4>\r\n</div>\r\n<txp:body />\r\n</txp:zem_prblock>'),
('comment_form', 'comment', '<txp:comments_error wraptag="ul" break="li" />\r\n\r\n<txp:comments_error wraptag="ul" break="li" />\r\n\r\n<a name="cpreview"></a>\r\n<txp:if_comments_preview>\r\n<div id="cpreview">\r\n<h5>Comment Preview</h5>\r\n<txp:comments_preview />\r\n</div>\r\n</txp:if_comments_preview><div style="padding-top:20px;padding-bottom:12px;"><h4><txp:image id="43" style="position:relative;top:3px;padding-right:5px;"/>Add Your Comments To This Article</h4></div>\r\n<txp:comments_error wraptag="ul" break="li" />\r\n<table cellpadding="4" cellspacing="0" border="0">\r\n<tr>\r\n<td align="right">\r\n<label><txp:text item="comment_name" /></label>\r\n</td>\r\n<td>\r\n<txp:comment_name_input />\r\n</td>\r\n<td>\r\n<txp:comment_remember />\r\n</td> \r\n</tr>\r\n<tr>\r\n<td align="right">\r\n<label><txp:text item="comment_email" /></label>\r\n</td>\r\n<td colspan="2">\r\n<txp:comment_email_input />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td align="right">\r\n<label><txp:text item="comment_message" /></label>\r\n</td>\r\n<td colspan="2">\r\n<txp:comment_message_input />\r\n</td>\r\n</tr>\r\n<tr>\r\n<td></td>\r\n<td align="left"><txp:comment_preview />\r\n<txp:comment_submit /></td>\r\n<td align="left">\r\n</td>\r\n</tr>\r\n</table>'),
('popup_comments', 'comment', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN"\n    "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n	<meta http-equiv="content-type" content="text/html; charset=utf-8" />\n	<link rel="Stylesheet" href="<txp:css />" type="text/css" />\n	<title><txp:page_title /></title>\n</head>\n<body>\n<div style="text-align: left; padding: 1em; width:300px">\n\n	<txp:popup_comments />\n\n	</div>\n</body>\n</html>'),
('files', 'file', '<txp:text item="file" />: \n<txp:file_download_link>\n<txp:file_download_name /> [<txp:file_download_size format="auto" decimals="2" />]\n</txp:file_download_link>\n<br />\n<txp:text item="category" />: <txp:file_download_category /><br />\n<txp:text item="download" />: <txp:file_download_downloads />'),
('search_results', 'article', '<h2><a style="border:none;" href="<txp:permlink/>/?c=<txp:category1 link="0" title="0"/>" title="<txp:title />"><txp:title /></a></h2><txp:search_result_excerpt /><br/>\r\n<p style="font-size:80%;margin-bottom:12px;"><a href="<txp:permlink/>/?c=<txp:category1 link="0" title="0"/>" title="<txp:title />"><txp:permlink/></a>\r\n</p>\r\n\r\n\r\n'),
('comments_display', 'article', '<h4 id="comment"><txp:comments_invite textonly="1" showalways="1" showcount="1" /></h4>\r\n\r\n<txp:comments />\r\n\r\n<txp:if_comments_preview>\r\n<div id="cpreview">\r\n<txp:comments_preview />\r\n</div>\r\n</txp:if_comments_preview>\r\n\r\n<txp:if_comments_allowed>\r\n<txp:comments_form />\r\n<txp:else />\r\n<p><txp:text item="comments_closed" /></p>\r\n</txp:if_comments_allowed>'),
('html_head', 'misc', '<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN"\r\n        "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">\r\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\r\n<head>\r\n<meta http-equiv="content-type" content="text/html; charset=utf-8" />\r\n<meta name="Geography" content="Shark Design &amp; Marketing Ltd, 1st Floor, Unit B3, Lowerfields Close, Elland, HX5 9DX" />\r\n<meta name="Language" content="English" />\r\n<meta http-equiv="Expires" content="never" />\r\n<meta name="Copyright" content="Shark Design &amp; Marketing Ltd" />\r\n<meta name="Designer" content="Shark Design &amp; Marketing Ltd" />\r\n<meta name="Publisher" content="Shark Design &amp; Marketing Ltd" />\r\n<meta name="Revisit-After" content="14 days" />\r\n<meta name="distribution" content="Global" />\r\n<meta name="Robots" content="INDEX,FOLLOW" />\r\n<meta name="city" content="Elland" />\r\n<meta name="country" content="United Kingdom" />\r\n<link rel="stylesheet" href="<txp:css />" type="text/css" media="screen" />\r\n<txp:output_form form="html_javascript" />\r\n<txp:output_form form="html_title" />\r\n</head>\r\n'),
('html_title', 'misc', '<title>\r\n<txp:if_individual_article>\r\n	<txp:site_name /> | <txp:section link="0" title="1" /> | <txp:title />\r\n<txp:else />\r\n<txp:if_category >\r\n	<txp:site_name /> | <txp:category link="0" title="1" />\r\n<txp:else />\r\n	<txp:if_section name="" >\r\n	<txp:site_name />\r\n	<txp:else />\r\n	<txp:site_name /> | <txp:section link="0" title="1" />\r\n	</txp:if_section>\r\n</txp:if_category>\r\n</txp:if_individual_article>\r\n</title>'),
('layout_header', 'misc', '<div id="header"><h1><a href="<txp:site_url/>" title="Shark Design &amp; Marketing Ltd"><span>Shark Design &amp; Marketing Ltd</span></a></h1>\r\n<h2><span>Design &amp; Marketing</span></h2></div>'),
