('section_case_studies', 'misc', '<txp:if_section name="case-studies">\r\n<txp:if_individual_article>\r\n<txp:chh_if_data>\r\n<txp:article limit="1" />\r\n<txp:section name="case-studies">Back To The Case Studies Archive</txp:section>\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n<txp:else />\r\n<txp:chh_if_data>\r\n<div class="title">\r\n<h4 class="sifr">Case Studies</h4>\r\n</div>\r\n<txp:article_custom section="case-studies"  limit="12" listform="article_case_study_listings"/>\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n</txp:if_individual_article>\r\n</txp:if_section>\r\n'),
('section_news', 'misc', '<txp:if_section name="news">\r\n<txp:if_individual_article>\r\n<txp:chh_if_data>\r\n<txp:article limit="1" />\r\n<txp:section name="news">Back To The News Archive</txp:section>\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n<txp:else />\r\n<txp:chh_if_data>\r\n<div class="title">\r\n<h4 class="sifr">News Archive</h4>\r\n</div>\r\n<txp:article limit="99" listform="article_news_listing"/>\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n</txp:if_individual_article>\r\n</txp:if_section>\r\n'),
