('html_metadata', 'misc', '<meta name="description" content="Shark! is a Yorkshire based design and marketing agency specialising in branding, design for print, marketing strategy, and web design & development." />\r\n<meta name="keywords" content="shark!, shark, graphic, design, creative, marketing, branding, digital, logo, strategy, web, website, e-commerce, SEO, advertising, agency, agencies, new media, online, poster, take one, flyer, folder, mailing, mail pack, mail shot, POS, brochure, signage." />'),
('panel_banner', 'misc', '<txp:php>// HOME //</txp:php>\r\n\r\n<txp:if_section name=",blog">\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<h3><txp:image id="41" /></h3>\r\n<span></span>\r\n</div></div>\r\n</txp:if_section>\r\n\r\n<txp:php>// NEWS //</txp:php>\r\n\r\n<txp:if_section name="news">\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:chh_if_data>\r\n<txp:hak_article_image link="0" offset="0" limit="1" />\r\n<txp:else />\r\n    <txp:image id="41" />\r\n</txp:chh_if_data>\r\n<span></span>\r\n</div></div>\r\n</txp:if_section>\r\n\r\n\r\n<txp:php>// ABOUT //</txp:php>\r\n\r\n<txp:if_section name="about">\r\n<txp:if_article_id id="54">\r\n<txp:output_form form="panel_key_staff" />\r\n<txp:else />\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:chh_if_data>\r\n<txp:hak_article_image link="0" offset="0" limit="1" />\r\n<txp:else />\r\n    <txp:image id="41" />\r\n</txp:chh_if_data>\r\n<span></span>\r\n</div></div>\r\n</txp:if_article_id>\r\n</txp:if_section>\r\n\r\n<txp:php>// WHAT WE DO //</txp:php>\r\n\r\n<txp:if_section name="what-we-do">\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:chh_if_data>\r\n<txp:hak_article_image link="0" offset="0" limit="1" />\r\n<txp:else />\r\n    <txp:image id="41" />\r\n</txp:chh_if_data>\r\n<span></span>\r\n</div></div>\r\n</txp:if_section>\r\n\r\n\r\n<txp:php>// CASE STUDIES //</txp:php>\r\n\r\n<txp:if_section name="case-studies">\r\n<txp:if_individual_article>\r\n<txp:chh_if_data>\r\n<txp:article limit="1" form="article_slider" />\r\n<txp:else />\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:image id="105" />\r\n<span></span>\r\n</div></div>\r\n</txp:chh_if_data>\r\n<txp:else />\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:image id="105" />\r\n<span></span>\r\n</div></div>\r\n</txp:if_individual_article>\r\n</txp:if_section>\r\n\r\n<txp:php>// PORTFOLIO //</txp:php>\r\n\r\n<txp:if_section name="portfolio">\r\n<txp:if_individual_article>\r\n<txp:chh_if_data>\r\n<txp:article limit="1" form="article_slideshow" />\r\n<txp:else />\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:image id="41" />\r\n<span></span>\r\n</div></div>\r\n</txp:chh_if_data>\r\n<txp:else />\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:image id="41" />\r\n<span></span>\r\n</div></div>\r\n</txp:if_individual_article>\r\n</txp:if_section>\r\n\r\n<txp:php>// CONTACT //</txp:php>\r\n\r\n<txp:if_section name="contact">\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:chh_if_data>\r\n<txp:hak_article_image link="0" offset="0" limit="1" />\r\n<txp:else />\r\n    <txp:image id="50" />\r\n</txp:chh_if_data>\r\n<span></span>\r\n</div></div>\r\n</txp:if_section>\r\n\r\n<txp:php>// GET INFO //</txp:php>\r\n\r\n<txp:if_section name="get-info">\r\n<div id="left_cont_header">\r\n<div class="main_image">\r\n<txp:chh_if_data>\r\n<txp:hak_article_image link="0" offset="0" limit="1" />\r\n<txp:else />\r\n    <txp:image id="41" />\r\n</txp:chh_if_data>\r\n<span></span>\r\n</div></div>\r\n</txp:if_section>'),
