('article_slideshow', 'article', '<div id="left_cont_header">\r\n<div id="slideshowContainer" class="slideshowContainer" style="position:relative;z-index:10;"><span></span></div>\r\n<div id="buttons_on">\r\n	<div id="navi_gallery">\r\n			<div id="prev"><a href="#" onclick="javascript:show.previous(); return false;"></a></div>\r\n			<div id="play_stop"><a href="#" onclick="javascript:show.stop(); return false;" class="play"></a><a href="#" onclick="javascript:show.play(); return false;" class="pause"></a></div>\r\n			<div id="next"><a href="#" onclick="javascript:show.next(); return false;"></a></div>\r\n		</div>\r\n	</div>\r\n<div id="thumbnails">\r\n<txp:hak_article_thumb link="1" offset="0" limit="20" linkclass="slideshowThumbnail"/>\r\n\r\n</div>\r\n<script type="text/javascript">\r\n           window.addEvent(''load'',function(){\r\n            var obj = {\r\n               wait: 5000,\r\n               effect: ''fade'',\r\n               duration: 1000,\r\n               loop: true,\r\n               thumbnails: true,\r\n               backgroundSlider: true,\r\n               onClick: function(i){var a = new Array()\r\n         }\r\n\r\n            }\r\n            show = new SlideShow(''slideshowContainer'',''slideshowThumbnail'',obj);\r\n            show.play();\r\n         });\r\n</script>\r\n</div>	'),
('section_contact', 'misc', '<txp:if_section name="contact">\r\n<txp:if_individual_article>\r\n<txp:chh_if_data>\r\n<txp:article limit="1" />\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n<txp:else />\r\n<txp:chh_if_data>\r\n<txp:article_custom section="contact" Menu_Index="00" limit="1" />\r\n<txp:else />\r\n    <txp:output_form form="no_content" />\r\n</txp:chh_if_data>\r\n</txp:if_individual_article>\r\n</txp:if_section>\r\n'),
('article_case_study_listings', 'article', '<div class="listing" style="<txp:if_last_article>border-bottom:none;</txp:if_last_article>">\r\n<span class="date"><txp:posted /></span>\r\n<h5><a class="no-border" href="<txp:permlink />" title="<txp:title />"><txp:title /></a></h5>\r\n<txp:excerpt />\r\n</div>'),
('form_contact_subscribe', 'misc', '<fieldset>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_text required="yes" size="32" label="Your Name" /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_text required="yes" size="32" label="Company Name" /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_email required="yes" size="32" label="Email Address" /></div>\r\n</fieldset>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_submit label="Submit"/></div>\r\n'),
('article_news_listing', 'article', '<div class="listing" style="<txp:if_last_article>border-bottom:none;</txp:if_last_article>">\r\n<span class="date"><txp:posted /></span>\r\n<h5><a class="no-border" href="<txp:permlink />" title="<txp:title />"><txp:title /></a></h5>\r\n<txp:excerpt />\r\n</div>'),
('form_contact', 'misc', '<fieldset>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_text required="yes" size="32" label="Your Name" /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_text required="yes" size="32" label="Company Name" /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_text required="yes" size="32" label="Telephone No." /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_email required="yes" size="32" label="Email Address" /></div>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_textarea required="yes" cols="36" rows="5" label="Enquiry"/></div>\r\n</fieldset>\r\n<div class="form-row-contact">\r\n<txp:zem_contact_submit label="Submit"/></div>\r\n'),
('html_javascript', 'misc', '<txp:if_section name=",news,what-we-do,get-info,contact">\r\n<script src="<txp:site_url/>js/menu.js" type="text/javascript"></script>\r\n<script src="<txp:site_url/>sifr/sifr.js" type="text/javascript"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/prototype.lite.js"></script>\r\n</txp:if_section>\r\n\r\n<txp:if_section name="about">\r\n<script src="<txp:site_url/>js/menu.js" type="text/javascript"></script>\r\n<script src="<txp:site_url/>sifr/sifr.js" type="text/javascript"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/prototype.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/effects.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/accordion.js"></script>\r\n<script type="text/javascript">\r\n\r\nEvent.observe(window, ''load'', loadAccordions);\r\n	\r\nfunction loadAccordions() {\r\nvar topAccordion = new accordion(''horizontal_container'', {\r\nclassNames : {\r\ntoggle : ''horizontal_accordion_toggle'',\r\ntoggleActive : ''horizontal_accordion_toggle_active'',\r\ncontent : ''horizontal_accordion_content''\r\n},\r\ndefaultSize : {width : 542},\r\ndirection : ''horizontal''\r\n});\r\n			topAccordion.activate($$(''#horizontal_container .horizontal_accordion_toggle'')[0]);\r\n}\r\n</script>\r\n</txp:if_section>\r\n\r\n<txp:if_section name="case-studies">\r\n<script src="<txp:site_url/>js/menu.js" type="text/javascript"></script>\r\n<script src="<txp:site_url/>sifr/sifr.js" type="text/javascript"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/prototype.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/effects.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/accordion.js"></script>\r\n<script type="text/javascript">\r\n\r\nEvent.observe(window, ''load'', loadAccordions, false);\r\n	\r\nfunction loadAccordions() {\r\nvar topAccordion = new accordion(''horizontal_container'', {\r\nclassNames : {\r\ntoggle : ''horizontal_accordion_toggle'',\r\ntoggleActive : ''horizontal_accordion_toggle_active'',\r\ncontent : ''horizontal_accordion_content''\r\n},\r\ndefaultSize : {width : 492},\r\ndirection : ''horizontal''\r\n});\r\n			topAccordion.activate($$(''#horizontal_container .horizontal_accordion_toggle'')[0]);\r\n}\r\n</script>\r\n</txp:if_section>\r\n\r\n<txp:if_section name="portfolio">\r\n<script src="<txp:site_url/>js/menu.js" type="text/javascript"></script>\r\n<script src="<txp:site_url/>sifr/sifr.js" type="text/javascript"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/prototype.lite.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/moo.fx.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/moo.fx.pack.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/mootools.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/backgroundSlider.js"></script>\r\n<script type="text/javascript" src="<txp:site_url/>js/slideshow.js"></script>\r\n</txp:if_section>'),
('form_contact_thanks', 'misc', '<h3>Thank you, your request has been sent!</h3>'),
('panel_sitemap', 'misc', '<div id="sitemap">\r\n<h5><a style="border:none;" href="<txp:site_url/>">Homepage</a>\r\n</h5>\r\n\r\n<h5>About</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="about" offset="0" limit="99" form="sidebar_link" sort="custom_1 asc"/>\r\n</ul>\r\n\r\n<h5><a style="border:none;" href="<txp:site_url/>what-we-do">What We Do</a>\r\n</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="what-we-do" offset="1" limit="99" form="sidebar_link" sort="custom_1 asc"/>\r\n</ul>\r\n\r\n<h5>Case Studies</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="case-studies" offset="0" limit="99" form="sidebar_link" />\r\n</ul>\r\n\r\n<h5>Portfolio</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="portfolio" offset="0" limit="99" form="sidebar_link" sort="custom_1 asc"/>\r\n</ul>\r\n\r\n<h5>Latest News</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="news" offset="0" limit="12" form="sidebar_link" />\r\n</ul>\r\n\r\n<h5>Get In Touch</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="contact" offset="0" limit="99" form="sidebar_link" sort="custom_1 asc"/>\r\n</ul>\r\n\r\n<h5>Get Info</h5>\r\n<ul class="footerlist">\r\n<txp:article_custom section="get-info" offset="0" limit="99" form="sidebar_link" sort="custom_1 asc"/>\r\n</ul>\r\n\r\n</div>'),
('form_contact_subscribe_thanks', 'misc', '<h3>Thank you, you have been added to our mailing list!</h3>'),
('article_what_we_do', 'article', '<div class="col-left">\r\n<div class="title">\r\n<h4 class="sifr"><txp:title /></h4>\r\n</div>\r\n<txp:body />\r\n</div>\r\n\r\n<div class="col-left" style="padding-left:40px;">\r\n<txp:output_form form="panel_branding_menu" />\r\n</div>\r\n\r\n');
