INSERT INTO `txp_page` (`name`, `user_html`) VALUES
('default', '<txp:output_form form="html_head" />\r\n<body>\r\n<div id="container">\r\n<div id="wrapper">\r\n<txp:output_form form="layout_header" />\r\n<txp:output_form form="panel_banner" />\r\n<txp:output_form form="panel_navigation" />\r\n<txp:output_form form="layout_col_main" />\r\n<txp:output_form form="layout_col_rhs" />\r\n</div>\r\n<txp:output_form form="layout_footer" />\r\n</div>\r\n\r\n<script type="text/javascript">\r\n//<![CDATA[\r\n\r\nif(typeof sIFR == "function"){\r\nsIFR.replaceElement(named({sSelector:"h4.sifr", sFlashSrc:"<txp:site_url/>sifr/AvantGarde_Md_BT.swf", sColor:"#FFFFFF", sLinkColor:"#FFFFFF", sBgColor:"#00a1e0", sHoverColor:"#85c7ee", nPaddingTop:0, nPaddingBottom:0, sFlashVars:"textalign=left&offsetTop=0"}));\r\n\r\n};\r\n\r\n//]]>\r\n</script>\r\n<txp:if_section name="about,case-studies">\r\n<script type="text/javascript" >\r\nvar horizontalAccordions = $$(''_accordion_toggle'');\r\nhorizontalAccordions.each(function(accordion) {\r\n$(accordion.next(0)).setStyle({\r\nheight: ''auto''\r\n});\r\n});\r\n\r\nvar horizontalAccordions = $$(''.accordion_toggle'');\r\nhorizontalAccordions.each(function(accordion) {\r\n    $(accordion.next(0)).setStyle({\r\n        height: ''auto''\r\n    });\r\n});\r\n\r\n</script>\r\n\r\n\r\n</txp:if_section>\r\n<script src="http://www.google-analytics.com/urchin.js" type="text/javascript">\r\n</script>\r\n<script type="text/javascript">\r\n_uacct = "UA-2334835-8";\r\nurchinTracker();\r\n\r\n</script>\r\n</body>\r\n</html>'),
('error_default', '<txp:output_form form="html_head" />\r\n<body>\r\n<div id="container">\r\n<div id="wrapper">\r\n<txp:output_form form="layout_header" />\r\n<txp:output_form form="panel_banner" />\r\n<txp:output_form form="panel_navigation" />\r\n\r\n<div id="left_cont_main">\r\n<div class="title">\r\n<h4 class="sifr"><txp:error_status /></h4>\r\n</div>\r\n<p>Sorry! <txp:error_message /></p>\r\n</div>\r\n<txp:output_form form="layout_col_rhs" />\r\n</div>\r\n<txp:output_form form="layout_footer" />\r\n</div>\r\n\r\n\r\n<script type="text/javascript">\r\n//<![CDATA[\r\n\r\nif(typeof sIFR == "function"){\r\nsIFR.replaceElement(named({sSelector:"h4.sifr", sFlashSrc:"<txp:site_url/>sifr/AvantGarde_Md_BT.swf", sColor:"#FFFFFF", sLinkColor:"#FFFFFF", sBgColor:"#00a1e0", sHoverColor:"#85c7ee", nPaddingTop:0, nPaddingBottom:0, sFlashVars:"textalign=left&offsetTop=0"}));\r\n\r\n};\r\n\r\n//]]>\r\n</script>\r\n<txp:if_section name="about,case-studies">\r\n<script type="text/javascript" >\r\nvar horizontalAccordions = $$(''_accordion_toggle'');\r\nhorizontalAccordions.each(function(accordion) {\r\n$(accordion.next(0)).setStyle({\r\nheight: ''auto''\r\n});\r\n});\r\n\r\nvar horizontalAccordions = $$(''.accordion_toggle'');\r\nhorizontalAccordions.each(function(accordion) {\r\n    $(accordion.next(0)).setStyle({\r\n        height: ''auto''\r\n    });\r\n});\r\n\r\n</script>\r\n\r\n\r\n</txp:if_section>\r\n<script src="http://www.google-analytics.com/urchin.js" type="text/javascript">\r\n</script>\r\n<script type="text/javascript">\r\n_uacct = "UA-2334835-8";\r\nurchinTracker();\r\n</script>\r\n</body>\r\n</html>');
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('ako_nav', 1, 'Adrian Kostrubiak', 'http://www.ako-k.com/', '1.0', 'This plugin produces a dynamic navigation bar based on a few specified attributes.', '	<h2>ako_nav Help File</h2>\n\n	<h3>A dynamic portable navigation creator.</h3>\n\n	<h4>What does it do?</h4>\n\n	<ul>\n		<li>Creates a primary <code>div</code> (optionally).</li>\n		<li>Inside the <code>div</code>, a heading is added for the nav bar (optionally).</li>\n		<li>Creates a <code>ul</code> to hold the nav bar.</li>\n		<li>Goes through given urls and names of pages and dynmically adds them as <code>li</code>&#8217;s</li>\n		<li><code>class="active"</code> is automatically added to the current page&#8217;s <code>a</code> tag (or optionally, <code>li</code> tag).</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>The simplest form is this:</p>\n\n	<blockquote>\n		<p><code>&#60;txp:ako_nav name="home,about,contact" url="/,/about,/contact" /&#62;</code></p>\n	</blockquote>\n\n	<p>it will produce something like this:</p>\n\n	<blockquote>\n		<p>&#60;pre&#62;&#60;div&#62;<br />\n&#60;h2&#62;Navigation&#60;/h2&#62;\n	&#60;ul&#62;\n		&#60;li&#62;&#60;a href=&#8221;/&#8221;&#62;home&#60;/a&#62;&#60;/li&#62;\n		&#60;li&#62;&#60;a href=&#8221;/about&#8221;&#62;about&#60;/a&#62;&#60;/li&#62;\n		&#60;li&#62;&#60;a href=&#8221;/contact&#8221;&#62;contact&#60;/a&#62;&#60;/li&#62;\n	&#60;/ul&#62;<br />\n&#60;/div&#62;&#60;/pre&#62;</p>\n	</blockquote>\n\n	<p>Add some of the tags below to get a lot more creative.</p>\n\n	<h4>Please note:</h4>\n\n	<p>The url attributes should be relative to the root of your site.  That is, if you are running your site out of a subdirectory, you do <strong>not</strong> need to include the subdirectory in the url attribute.  (In fact, don&#8217;t include it.)<br />\nFor example, if you are running your site at http://www.ako-k.com/wow/, and you wanted to use ako_nav to make a navigation bar to the about and links page, the code would be</p>\n\n	<blockquote>\n		<p><code>&#60;txp:ako_nav name="home,about,contact" url="/,/about,/links" /&#62;</code><br />\nand not<br />\n<del>@&#60;txp:ako_nav name=&#8220;home,about,contact&#8221; url=&#8221;/wow/,/wow/about,/wow/links&#8221; /&#62;@</del></p>\n	</blockquote>\n\n	<h4>Tags</h4>\n\n	<ul>\n		<li><em>Required</em>\n	<ul>\n		<li><strong>name</strong> &#8211; This is a list of page names, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n		<li><strong>url</strong> &#8211; This is a list of page url&#8217;s, following the same order as the page names, and also delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n	</ul></li>\n		<li><em>Optional</em>\n	<ul>\n		<li><strong>title</strong> &#8211; This is a list of <code>title</code> attributes for the <code>a</code> tags, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n		<li><strong>ulid</strong> &#8211; This is an id for the <code>ul</code> tag.</li>\n		<li><strong>ulclass</strong> &#8211; This is a class for the <code>ul</code>tag.</li>\n		<li><strong>liids</strong> &#8211; This is a list of id attributes for the <code>li</code> tag, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n		<li><strong>liclass</strong> &#8211; This is a class for the <code>li</code>tag.</li>\n		<li><strong>afchild</strong> &#8211; <span>{color:red} New in 1.0!</span> This is an additional class attribute for the <code>li</code> tag, only applied to the <em>first</em> item in the list.</li>\n		<li><strong>alchild</strong> &#8211; <span>{color:red} New in 1.0!</span> This is an additional class attribute for the <code>li</code> tag, only applied to the <em>last</em> item in the list.</li>\n		<li><strong>aids</strong> &#8211; This is a list of id attributes for the <code>a</code> tags, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n		<li><strong>aclass</strong> &#8211; This is a class for the <code>a</code>tag.</li>\n		<li><strong>afchild</strong> &#8211; <span>{color:red} New in 1.0!</span> This is an additional class attribute for the <code>a</code> tag, only applied to the <em>first</em> item in the list.</li>\n		<li><strong>alchild</strong> &#8211; <span>{color:red} New in 1.0!</span> This is an additional class attribute for the <code>a</code> tag, only applied to the <em>last</em> item in the list.</li>\n		<li><strong>spanwrap</strong> &#8211; This is for whether or not the text is wrapped in <code>span</code> tags.  0 = not wrapped, 1 = wrapped. By default, it is not wrapped (0).</li>\n		<li><strong>spanclass</strong> &#8211; This is a class for the <code>span</code>tag.</li>\n		<li><strong>spanids</strong> &#8211; <span>{color:red} New in 1.0!</span>  This is a list of id attributes for the <code>span</code> tag, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n		<li><strong>divid</strong> &#8211; This is an id for the <code>div</code> container.</li>\n		<li><strong>divclass</strong> &#8211; This is a class for the <code>div</code> container.</li>\n		<li><strong>divon</strong> &#8211; This is for whether or not the <code>div</code> is displayed.  0 = not displayed, 1 = displayed. By default, it is displayed (1).</li>\n		<li><strong>headid</strong> &#8211; This is an id for the header tag.</li>\n		<li><strong>headclass</strong> &#8211; This is a class for the header tag.</li>\n		<li><strong>headon</strong> &#8211; This is for whether or not the header text is displayed.  0 = not displayed, 1 = displayed. By default, it is displayed (1).</li>\n		<li><strong>headtxt</strong> &#8211; This is the text inside the header its self.  By default, this is &#8220;Navigation&#8221;.</li>\n		<li><strong>headlvl</strong> &#8211; This is the level that the <code>h</code> tag is, between 1 and 6.  By default, this is 2.</li>\n		<li><strong>curlink</strong> &#8211; This is an option as to whether or not the current section is linked.  0 = not linked, 1 = linked.  By default, it is linked (1).</li>\n		<li><strong>classtag</strong> &#8211; This is an option which allows you to add the <code>class="active"</code> to either the <code>a</code> or the <code>li</code> tag.  0 = it is added to the <code>a</code> tag, 1 = it is added to the <code>li</code> tag.  By default, it is added to the <code>a</code> tag (0).</li>\n		<li><strong>acticlass</strong> &#8211; <span>{color:red} New in 1.0!</span> This is an option to change the the <code>class="active"</code> to whatever you would like. By default, this is &#8220;active&#8221;.</li>\n		<li><strong>delim</strong> &#8211; <span>{color:red} New in 1.0!</span> This is the character(s) which will act as a delimiter for the other attributes which take a list, such as &#8220;name,&#8221; &#8220;url,&#8221; etc.  By default, this is a comma (&#8221;,&#8221;) but can be changed to any other character(s).</li>\n		<li><strong>accesskeys</strong> &#8211; <span>{color:red} New in 1.0!</span> This is a list of accesskey attributes for the <code>a</code> tags, delimited by commas (and no spaces), unless the &#8220;delim&#8221; attribute is set as something else.</li>\n	</ul></li>\n	</ul>\n\n	<p>&#60;hr /&#62;<br />\nThanks for using <strong>ako_nav</strong>, and have fun!</p>', '// ------------------------------------------------------------------\n// ako_nav plugin\n// Dynamic Navigation Bars\n// by Adrian Kostrubiak\n// http://www.ako-k.com/\n// http://www.wereallafraid.com/\n// ------------------------------------------------------------------\n// ------------------------------------------------------------------\n//\n// Version History:\n// Version 0.1 - 02-13-2005 - Initial release\n// Version 0.2 - 02-13-2005 - Added ability to turn off header text.\n// Version 0.3 - 04-12-2005 - Actually added ability to turn off header text.\n//                            (Something was wrong last time, and it took me\n//                            a while to pick up on it).\n// Version 0.4 - 05-21-2005 - Removed dependance on URLs.  Now we go by the\n//                            section variable in $pretext ($pretext[''s'']).\n// Version 0.5 - 06-24-2005 - Fixed an issue in which the class="active" was\n//                            not being added when the site was being run out of a\n//                            subdirectory.\n// Version 0.6 - 06-24-2005 - Added the curlink attribute.  Allows the ability to\n//                            have the current section not be linked.\n// Version 0.7 - 07-06-2005 - Added ability to have extra names that, if there are\n//                            not corresponding links, will not link.  They will\n//                            just reside in the list as plain text.\n// Version 0.8 - 09-06-2005 - Added ability to set class="active" to li tag\n//                            instead of a tag\n// Version 0.9 - 10-09-2005 - Dubbed "super-mega-cool instead-of-comp-sci-paper\n//                            release."  Added the following attributes: aids,\n//                            aclass, liids, liclass, divon, spanwrap, spanclass.\n// Version 0.9.1 -10-10-2005- Dubbed "ok so the last one wasn''t so super-mega-cool\n//                            but this one is" release.  Fixed an issue where\n//                            class="active" wasn''t being added when aclass was not\n//                            specified.\n//\n// Planned:\n// Version 1.0 - 10-16-2006 - Addition of accesskeys and spanids attributes.  Support for\n//                            https://, ftp:// in url atts, https:// as the txp PROTOCAL.\n//                            acticlass, delim, <a> and <li> first / last child support\n\nfunction ako_nav($atts) {\n	// get some attributes\n	global $acticlass;\n\n	extract(lAtts(array(\n					// main important attributes\n					''name''  => '''',\n					''url''   => '''',\n					''title'' => '''',\n\n					// ul attributes\n					''ulid''    => '''',\n					''ulclass'' => '''',\n\n					// li attributes\n					''liid''    => '''',\n					''liclass'' => '''',\n\n					// a attributes\n					''aids''   => '''',\n					''aclass'' => '''',\n\n					// span attributes\n					''spanwrap''  => ''0'',\n					''spanclass'' => '''',\n					''spanids''   => '''',\n\n					// div attributes\n					''divid''    => ''nav'',\n					''divclass'' => '''',\n					''divon''    => ''1'',\n\n					// heading attributes\n					''headid''    => '''',\n					''headclass'' => '''',\n					''headon''    => ''1'',\n					''headtxt''   => ''Navigation'',\n					''headlvl''   => ''2'',\n\n					// first / last child support\n					''afchild''    => '''',\n					''lifchild''   => '''',\n					//''fchildact'' => '''',\n					''alchild''    => '''',\n					''lilchild''   => '''',\n					//''lchildact'' => '''',\n\n					// random\n					''curlink''    => ''1'',\n					''classtag''   => ''0'',\n					''acticlass''  => ''active'',\n					''delim''      => '','',\n					''accesskeys'' => '''',\n					),$atts));\n	// end attributes\n\n	// make sure we''re doing _something_ here..\n	if (!$name && !$url) return(ako_nav_help("name&#8217;s and URL"));\n	if (!$name) return(ako_nav_help("name"));\n	if (!$url)  return(ako_nav_help("URL"));\n\n	$ako_out = "";\n\n	// parse the uri''s, title''s, name''s of pages, <a> class, <li> class, first & last child support..\n	$pages[] = explode($delim, $name);\n	$pages[] = explode($delim, $url);\n	if (!empty($title)) { $pages[] = explode($delim, $title); } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	if (!empty($aids))  { $pages[] = explode($delim, $aids);  } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	if (!empty($liid))  { $pages[] = explode($delim, $liid);  } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	$pages[] = $aclass;     // index: 5\n	$pages[] = $liclass;    // index: 6\n	$pages[] = $afchild;    // index: 7\n	$pages[] = $lifchild;   // index: 8\n	$pages[] = $alchild;    // index: 9\n	$pages[] = $lilchild;   // index: 10\n\n	// accesskeys...\n	if (!empty($accesskeys)) {\n		$pages[] = explode($delim, $accesskeys);\n	} else {\n		for ($i = 0; $i < count($pages[0]); $i++) {\n			$pages[11][$i] = ($i+1);\n		}\n	}\n\n	// start putting together a navbar...\n	$ako_out = ($headon == 1) ? n.hed($headtxt, $headlvl, ako_parse_id($headid) . ako_parse_class($headclass, false)) : '''';\n	$ako_out .= n."<ul". ako_parse_id($ulid) . ako_parse_class($ulclass, false) .">".n;\n\n    // check spanwrap..\n	if ($spanwrap == 1) {\n		$spanidArr = explode($delim, $spanids);\n		$i = 0;\n		foreach ($pages[0] as $value) {\n			$pages[0][$i] = tag($value, "span", ako_parse_class($spanclass, false) . ako_parse_id($spanidArr[$i]));\n			$i++;\n		}\n	}\n\n	// heres the trickery to go through the ul''s, and add the class="active" dynamically\n	// it''s trixy... you know you like it.\n	for ($i = 0; $i < count($pages[0]); $i++) {\n		$navbar[] = array("name"      => $pages[0][$i],\n		                  "url"       => $pages[1][$i],\n		                  "title"     => $pages[2][$i],\n		                  "aid"       => $pages[3][$i],\n		                  "liid"      => $pages[4][$i],\n		                  "aclass"    => $pages[5],\n		                  "liclass"   => $pages[6],\n		                  "afchild"   => $pages[7],\n		                  "lifchild"  => $pages[8],\n		                  "alchild"   => $pages[9],\n		                  "lilchild"  => $pages[10],\n		                  "accesskey" => $pages[11][$i]);\n	} // end for()\n\n	// all the magique goes on here. check it further below.\n	switch($curlink) {\n		case 1:\n			$ako_out .= ($classtag == 0)\n				? ako_parse_li_1($navbar, false)\n				: ako_parse_li_2($navbar, false);\n			break;\n		case 0:\n			$ako_out .= ($classtag == 0)\n				? ako_parse_li_3($navbar, false)\n				: ako_parse_li_4($navbar, false);\n			break;\n	}\n\n	// finish off the navbar...\n	$ako_out .= "</ul>".n;\n	$ako_out  = ($divon == 1) ? tag($ako_out, "div", ako_parse_id($divid) . ako_parse_class($divclass, false)) : $ako_out;\n\n	return($ako_out);\n}\n\n\n/* =========================================================================\n    parse_li key:\n    1: link to current selection, add class="active" to anchor tags\n    2: link to current selection, add class="active" to list items\n    3: no link to current selection, add class="active" to anchor tags †\n    4: no link to current selection, add class="active" to list items\n\n    -----------\n    † if you think about it, this makes no sense what so ever.\n========================================================================= */\n\nfunction ako_parse_li_1($navbar, $fullUrl) {\n	// link to current selection, add class="active" to anchor tags\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lilchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false);\n		}\n\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n				? $navbar[$i]["url"]\n				: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n			$aAtts .= ako_parse_title($navbar[$i]["title"]);\n			$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n\n			// first / last child support\n			if ($i == 0) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["afchild"]);\n			} else if ($i == (count($navbar) - 1)) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["alchild"]);\n			} else {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n			}\n\n			$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n			$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n			$p_out .= tag($liLink, "li", $liAtts).n;\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_2($navbar, $fullUrl) {\n	// link to current selection, add class="active" to list items\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n				? $navbar[$i]["url"]\n				: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n			$aAtts .= ako_parse_title($navbar[$i]["title"]);\n			$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n			if ($i == 0) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n			} else if ($i == (count($navbar) - 1)) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n			} else {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n			}\n			$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n			$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n			$p_out .= tag($liLink, "li", $liAtts).n;\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_3($navbar, $fullUrl) {\n	// no link to current selection, add class="active" to anchor tags\n	// this doesn''t make sense...\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lilchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false);\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			if (ako_classActiveize($section, $navbar[$i]["url"])) {\n				$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n			} else {\n				$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n					? $navbar[$i]["url"]\n					: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n				$aAtts .= ako_parse_title($navbar[$i]["title"]);\n				$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n				if ($i == 0) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n				} else if ($i == (count($navbar) - 1)) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n				} else {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n				}\n				$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n				$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n				$p_out .= tag($liLink, "li", $liAtts).n;\n			}\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_4($navbar, $fullUrl) {\n	// no link to current selection, add class="active" to list items\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			if (ako_classActiveize($section, $navbar[$i]["url"])) {\n				// we''re in current section, no link to it\n				$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n			} else {\n				// not in current section, link to those.\n				$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n					? $navbar[$i]["url"]\n					: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n				$aAtts .= ako_parse_title($navbar[$i]["title"]);\n				$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";if ($i == 0) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n				} else if ($i == (count($navbar) - 1)) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n				} else {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n				}\n				$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n				$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n				$p_out .= tag($liLink, "li", $liAtts).n;\n			}\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_classActiveize($sec, $niUrl) { // section, navbar[i][url]\n	return ((strpos($sec, substr($niUrl, 1)) !== false) || (!$sec && (!substr($niUrl, 1))));\n//	return ( (strpos($sec, substr($niUrl, 1)) !== false) || (!$sec && (!substr($niUrl, 1))) || (strpos($sec."/", substr($niUrl, 1)) !== false) );\n}\n\nfunction ako_parse_title($title) {\n	return((empty($title))\n		?	""\n		:	" title=\\"$title\\"");\n}\n\nfunction ako_parse_id($id) {\n	return((empty($id))\n		?	""\n		:	" id=\\"$id\\"");\n}\n\nfunction ako_parse_class($class, $active, $child = '''') { // a => bool, true=its active\n	global $acticlass;\n	if ($active) {\n		$p_out = (empty($class))\n			?	(empty($child) ? " class=\\"$acticlass\\"" : " class=\\"$acticlass $child\\"")\n			:	(empty($child) ? " class=\\"$class $acticlass\\"" : " class=\\"$class $acticlass $child\\"");\n	} else {\n		$p_out = (empty($class))\n			?	(empty($child) ? "" : " class=\\"$child\\"")\n			:	(empty($child) ? " class=\\"$class\\"" : " class=\\"$class $child\\"");\n	}\n	return($p_out);\n}\n\n// if no pages / urls are specified.\nfunction ako_nav_help($error) {\n	$err = strong("Warning:");\n	$link = tag("help file", "a", '' href="http://www.ako-k.com/archive/108" title="Help file for ako_nav"'');\n	return(graf($err." No page ".$error."&#8217;s specified in attributes for ako_nav.  Please see the $link for more information.".n));\n}\n// Congratulations!\n// You''ve made it to the end of my code...', '// ------------------------------------------------------------------\n// ako_nav plugin\n// Dynamic Navigation Bars\n// by Adrian Kostrubiak\n// http://www.ako-k.com/\n// http://www.wereallafraid.com/\n// ------------------------------------------------------------------\n// ------------------------------------------------------------------\n//\n// Version History:\n// Version 0.1 - 02-13-2005 - Initial release\n// Version 0.2 - 02-13-2005 - Added ability to turn off header text.\n// Version 0.3 - 04-12-2005 - Actually added ability to turn off header text.\n//                            (Something was wrong last time, and it took me\n//                            a while to pick up on it).\n// Version 0.4 - 05-21-2005 - Removed dependance on URLs.  Now we go by the\n//                            section variable in $pretext ($pretext[''s'']).\n// Version 0.5 - 06-24-2005 - Fixed an issue in which the class="active" was\n//                            not being added when the site was being run out of a\n//                            subdirectory.\n// Version 0.6 - 06-24-2005 - Added the curlink attribute.  Allows the ability to\n//                            have the current section not be linked.\n// Version 0.7 - 07-06-2005 - Added ability to have extra names that, if there are\n//                            not corresponding links, will not link.  They will\n//                            just reside in the list as plain text.\n// Version 0.8 - 09-06-2005 - Added ability to set class="active" to li tag\n//                            instead of a tag\n// Version 0.9 - 10-09-2005 - Dubbed "super-mega-cool instead-of-comp-sci-paper\n//                            release."  Added the following attributes: aids,\n//                            aclass, liids, liclass, divon, spanwrap, spanclass.\n// Version 0.9.1 -10-10-2005- Dubbed "ok so the last one wasn''t so super-mega-cool\n//                            but this one is" release.  Fixed an issue where\n//                            class="active" wasn''t being added when aclass was not\n//                            specified.\n//\n// Planned:\n// Version 1.0 - 10-16-2006 - Addition of accesskeys and spanids attributes.  Support for\n//                            https://, ftp:// in url atts, https:// as the txp PROTOCAL.\n//                            acticlass, delim, <a> and <li> first / last child support\n\nfunction ako_nav($atts) {\n	// get some attributes\n	global $acticlass;\n\n	extract(lAtts(array(\n					// main important attributes\n					''name''  => '''',\n					''url''   => '''',\n					''title'' => '''',\n\n					// ul attributes\n					''ulid''    => '''',\n					''ulclass'' => '''',\n\n					// li attributes\n					''liid''    => '''',\n					''liclass'' => '''',\n\n					// a attributes\n					''aids''   => '''',\n					''aclass'' => '''',\n\n					// span attributes\n					''spanwrap''  => ''0'',\n					''spanclass'' => '''',\n					''spanids''   => '''',\n\n					// div attributes\n					''divid''    => ''nav'',\n					''divclass'' => '''',\n					''divon''    => ''1'',\n\n					// heading attributes\n					''headid''    => '''',\n					''headclass'' => '''',\n					''headon''    => ''1'',\n					''headtxt''   => ''Navigation'',\n					''headlvl''   => ''2'',\n\n					// first / last child support\n					''afchild''    => '''',\n					''lifchild''   => '''',\n					//''fchildact'' => '''',\n					''alchild''    => '''',\n					''lilchild''   => '''',\n					//''lchildact'' => '''',\n\n					// random\n					''curlink''    => ''1'',\n					''classtag''   => ''0'',\n					''acticlass''  => ''active'',\n					''delim''      => '','',\n					''accesskeys'' => '''',\n					),$atts));\n	// end attributes\n\n	// make sure we''re doing _something_ here..\n	if (!$name && !$url) return(ako_nav_help("name&#8217;s and URL"));\n	if (!$name) return(ako_nav_help("name"));\n	if (!$url)  return(ako_nav_help("URL"));\n\n	$ako_out = "";\n\n	// parse the uri''s, title''s, name''s of pages, <a> class, <li> class, first & last child support..\n	$pages[] = explode($delim, $name);\n	$pages[] = explode($delim, $url);\n	if (!empty($title)) { $pages[] = explode($delim, $title); } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	if (!empty($aids))  { $pages[] = explode($delim, $aids);  } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	if (!empty($liid))  { $pages[] = explode($delim, $liid);  } else { $pages[] = array_fill(0, count($pages[0]), ""); }\n	$pages[] = $aclass;     // index: 5\n	$pages[] = $liclass;    // index: 6\n	$pages[] = $afchild;    // index: 7\n	$pages[] = $lifchild;   // index: 8\n	$pages[] = $alchild;    // index: 9\n	$pages[] = $lilchild;   // index: 10\n\n	// accesskeys...\n	if (!empty($accesskeys)) {\n		$pages[] = explode($delim, $accesskeys);\n	} else {\n		for ($i = 0; $i < count($pages[0]); $i++) {\n			$pages[11][$i] = ($i+1);\n		}\n	}\n\n	// start putting together a navbar...\n	$ako_out = ($headon == 1) ? n.hed($headtxt, $headlvl, ako_parse_id($headid) . ako_parse_class($headclass, false)) : '''';\n	$ako_out .= n."<ul". ako_parse_id($ulid) . ako_parse_class($ulclass, false) .">".n;\n\n    // check spanwrap..\n	if ($spanwrap == 1) {\n		$spanidArr = explode($delim, $spanids);\n		$i = 0;\n		foreach ($pages[0] as $value) {\n			$pages[0][$i] = tag($value, "span", ako_parse_class($spanclass, false) . ako_parse_id($spanidArr[$i]));\n			$i++;\n		}\n	}\n\n	// heres the trickery to go through the ul''s, and add the class="active" dynamically\n	// it''s trixy... you know you like it.\n	for ($i = 0; $i < count($pages[0]); $i++) {\n		$navbar[] = array("name"      => $pages[0][$i],\n		                  "url"       => $pages[1][$i],\n		                  "title"     => $pages[2][$i],\n		                  "aid"       => $pages[3][$i],\n		                  "liid"      => $pages[4][$i],\n		                  "aclass"    => $pages[5],\n		                  "liclass"   => $pages[6],\n		                  "afchild"   => $pages[7],\n		                  "lifchild"  => $pages[8],\n		                  "alchild"   => $pages[9],\n		                  "lilchild"  => $pages[10],\n		                  "accesskey" => $pages[11][$i]);\n	} // end for()\n\n	// all the magique goes on here. check it further below.\n	switch($curlink) {\n		case 1:\n			$ako_out .= ($classtag == 0)\n				? ako_parse_li_1($navbar, false)\n				: ako_parse_li_2($navbar, false);\n			break;\n		case 0:\n			$ako_out .= ($classtag == 0)\n				? ako_parse_li_3($navbar, false)\n				: ako_parse_li_4($navbar, false);\n			break;\n	}\n\n	// finish off the navbar...\n	$ako_out .= "</ul>".n;\n	$ako_out  = ($divon == 1) ? tag($ako_out, "div", ako_parse_id($divid) . ako_parse_class($divclass, false)) : $ako_out;\n\n	return($ako_out);\n}\n\n\n/* =========================================================================\n    parse_li key:\n    1: link to current selection, add class="active" to anchor tags\n    2: link to current selection, add class="active" to list items\n    3: no link to current selection, add class="active" to anchor tags †\n    4: no link to current selection, add class="active" to list items\n\n    -----------\n    † if you think about it, this makes no sense what so ever.\n========================================================================= */\n\nfunction ako_parse_li_1($navbar, $fullUrl) {\n	// link to current selection, add class="active" to anchor tags\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lilchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false);\n		}\n\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n				? $navbar[$i]["url"]\n				: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n			$aAtts .= ako_parse_title($navbar[$i]["title"]);\n			$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n\n			// first / last child support\n			if ($i == 0) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["afchild"]);\n			} else if ($i == (count($navbar) - 1)) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["alchild"]);\n			} else {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n			}\n\n			$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n			$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n			$p_out .= tag($liLink, "li", $liAtts).n;\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_2($navbar, $fullUrl) {\n	// link to current selection, add class="active" to list items\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n				? $navbar[$i]["url"]\n				: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n			$aAtts .= ako_parse_title($navbar[$i]["title"]);\n			$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n			if ($i == 0) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n			} else if ($i == (count($navbar) - 1)) {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n			} else {\n				$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n			}\n			$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n			$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n			$p_out .= tag($liLink, "li", $liAtts).n;\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_3($navbar, $fullUrl) {\n	// no link to current selection, add class="active" to anchor tags\n	// this doesn''t make sense...\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false, $navbar[$i]["lilchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], false);\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			if (ako_classActiveize($section, $navbar[$i]["url"])) {\n				$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n			} else {\n				$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n					? $navbar[$i]["url"]\n					: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n				$aAtts .= ako_parse_title($navbar[$i]["title"]);\n				$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";\n				if ($i == 0) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n				} else if ($i == (count($navbar) - 1)) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n				} else {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n				}\n				$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n				$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n				$p_out .= tag($liLink, "li", $liAtts).n;\n			}\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_parse_li_4($navbar, $fullUrl) {\n	// no link to current selection, add class="active" to list items\n	global $pretext, $siteurl;\n	$section = $pretext[''s''];\n\n	$p = (defined(''PROTOCOL'')) ? PROTOCOL : ''http://'';\n	$ps = array("http://", "https://", "ftp://", "ftps://", "mailto:");\n\n	$section = ($section == "default") ? "" : $section;\n	$p_out = "";\n	for ($i = 0; $i < count($navbar); $i++) {\n		if ($i == 0) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else if ($i == (count($navbar) - 1)) {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]), $navbar[$i]["lifchild"]);\n		} else {\n			$liAtts = ako_parse_id($navbar[$i]["liid"]) . ako_parse_class($navbar[$i]["liclass"], ako_classActiveize($section, $navbar[$i]["url"]));\n		}\n		if (!empty($navbar[$i]["url"])) {\n			// there is a link\n			if (ako_classActiveize($section, $navbar[$i]["url"])) {\n				// we''re in current section, no link to it\n				$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n			} else {\n				// not in current section, link to those.\n				$aAtts  = " href=\\"". (($fullUrl || (strpos($navbar[$i]["url"], $ps) !== false))\n					? $navbar[$i]["url"]\n					: $p.$siteurl.$navbar[$i]["url"]) ."\\"";\n				$aAtts .= ako_parse_title($navbar[$i]["title"]);\n				$aAtts .= " accesskey=\\"". $navbar[$i]["accesskey"] ."\\"";if ($i == 0) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["afchild"]);\n				} else if ($i == (count($navbar) - 1)) {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false, $navbar[$i]["alchild"]);\n				} else {\n					$aAtts .= ako_parse_class($navbar[$i]["aclass"], false);\n				}\n				$aAtts .= ako_parse_id($navbar[$i]["aid"]);\n\n				$liLink = tag($navbar[$i]["name"], "a", $aAtts);\n				$p_out .= tag($liLink, "li", $liAtts).n;\n			}\n		} else {\n			// no link, just text\n			$p_out .= tag($navbar[$i]["name"], "li", $liAtts).n;\n		}\n	}\n	return($p_out);\n}\n\n\nfunction ako_classActiveize($sec, $niUrl) { // section, navbar[i][url]\n	return ((strpos($sec, substr($niUrl, 1)) !== false) || (!$sec && (!substr($niUrl, 1))));\n//	return ( (strpos($sec, substr($niUrl, 1)) !== false) || (!$sec && (!substr($niUrl, 1))) || (strpos($sec."/", substr($niUrl, 1)) !== false) );\n}\n\nfunction ako_parse_title($title) {\n	return((empty($title))\n		?	""\n		:	" title=\\"$title\\"");\n}\n\nfunction ako_parse_id($id) {\n	return((empty($id))\n		?	""\n		:	" id=\\"$id\\"");\n}\n\nfunction ako_parse_class($class, $active, $child = '''') { // a => bool, true=its active\n	global $acticlass;\n	if ($active) {\n		$p_out = (empty($class))\n			?	(empty($child) ? " class=\\"$acticlass\\"" : " class=\\"$acticlass $child\\"")\n			:	(empty($child) ? " class=\\"$class $acticlass\\"" : " class=\\"$class $acticlass $child\\"");\n	} else {\n		$p_out = (empty($class))\n			?	(empty($child) ? "" : " class=\\"$child\\"")\n			:	(empty($child) ? " class=\\"$class\\"" : " class=\\"$class $child\\"");\n	}\n	return($p_out);\n}\n\n// if no pages / urls are specified.\nfunction ako_nav_help($error) {\n	$err = strong("Warning:");\n	$link = tag("help file", "a", '' href="http://www.ako-k.com/archive/108" title="Help file for ako_nav"'');\n	return(graf($err." No page ".$error."&#8217;s specified in attributes for ako_nav.  Please see the $link for more information.".n));\n}\n// Congratulations!\n// You''ve made it to the end of my code...', '9c874e3b266718d6a0b6861a18d31807', 0, 5, 0),
('chh_if_data', 1, 'Coke Harrington', 'http://www.cokesque.com/', '0.10', 'Show a block of text only if enclosed Txp tags produce some output.', '	<h2>chh_if_data</h2>\n\n	<p>This tag is used to determine whether some contained Txp tags output any data, allowing the conditional output of extra markup, like a header or a "no articles found" message.</p>\n\n<pre>\n&lt;txp:chh_if_data&gt;\n    &lt;h3&gt;Here''s an article list&lt;/h3&gt;\n    &lt;txp:article_custom category="Nogo" /&gt;\n&lt;txp:else /&gt;\n    &lt;p&gt;article_custom had nothing to say.&lt;/p&gt;\n&lt;/txp:chh_if_data&gt;</pre>\n<pre>\n&lt;txp:chh_if_data&gt;\n    Static text will NEVER be shown.\n    Drop a Txp tag in here!<br />\n&lt;/txp:chh_if_data&gt;<br />\n</pre>\n\n	<p>If the enclosed chunk of text contains several Txp tags, <code>chh_if_data</code> will evaluate to true when <em>any</em> of the tags returns data.</p>\n\n	<p>"Data," in this case, mean any output at all, including whitespace.</p>\n\n\n ', '\nfunction chh_if_data ($atts, $thing='''') {\n    $atts = lAtts(array(''debug'' => 0), $atts);\n\n    $f = ''/<txp:(\\S+)\\b(.*)(?:(?<!br )(\\/))?''.chr(62).''(?(3)|(.+)<\\/txp:\\1>)/sU'';\n    $iftext = EvalElse($thing, true);\n    $thresh = 1 + strlen(preg_replace($f, '''', $iftext));\n\n    $parsed = parse($iftext);\n    $parsed_len = strlen($parsed);\n\n    $empty = ''Data'';\n    if (strlen($parsed) < $thresh) { #or !preg_match(''/\\S/'', $parsed)) {\n        $parsed = parse(EvalElse($thing, false));\n        $empty = ''No Data'';\n    }\n    return $atts[''debug'']\n           ? "<!-- $empty -- Threshhold: $thresh Length: $parsed_len -->" . $parsed\n           : $parsed;\n}\n\n// Deprecated due to poor naming\nfunction chh_unless_empty ($atts, $thing='''') {\n    return chh_if_data($atts, $thing);\n}\nfunction chh_if_not_empty ($atts, $thing='''') {\n    return chh_if_data($atts, $thing);\n}\n', '\nfunction chh_if_data ($atts, $thing='''') {\n    $atts = lAtts(array(''debug'' => 0), $atts);\n\n    $f = ''/<txp:(\\S+)\\b(.*)(?:(?<!br )(\\/))?''.chr(62).''(?(3)|(.+)<\\/txp:\\1>)/sU'';\n    $iftext = EvalElse($thing, true);\n    $thresh = 1 + strlen(preg_replace($f, '''', $iftext));\n\n    $parsed = parse($iftext);\n    $parsed_len = strlen($parsed);\n\n    $empty = ''Data'';\n    if (strlen($parsed) < $thresh) { #or !preg_match(''/\\S/'', $parsed)) {\n        $parsed = parse(EvalElse($thing, false));\n        $empty = ''No Data'';\n    }\n    return $atts[''debug'']\n           ? "<!-- $empty -- Threshhold: $thresh Length: $parsed_len -->" . $parsed\n           : $parsed;\n}\n\n// Deprecated due to poor naming\nfunction chh_unless_empty ($atts, $thing='''') {\n    return chh_if_data($atts, $thing);\n}\nfunction chh_if_not_empty ($atts, $thing='''') {\n    return chh_if_data($atts, $thing);\n}\n', '967a43e8eecce18e74b790ee6204c956', 0, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('zem_contact_reborn', 1, 'TXP Community', 'http://forum.textpattern.com/viewtopic.php?id=23728', '4.0.3.20', 'Form mailer for Textpattern', ' <style>\n   li code {font-weight: bold;}\n   pre {padding: 0.5em 1em; background: #eee; border: 1px dashed #ccc;}\n   h1, h2, h3, h3 code {font-family: sans-serif; font-weight: bold;}\n   h1, h2, h3 {margin-left: -1em;}\n   h2, h3 {margin-top: 2em;}\n   h1 {font-size: 3em;}\n   h2 {font-size: 2em;}\n   h3 {font-size: 1.5em;}\n   li a code {font-weight: normal;}\n   .required, .warning {color:red;}\n </style>\n\n	<h1 id="top">Zem Contact Reborn</h1>\n\n	<p>Please reports bugs and problems with this plugin in <a href="http://forum.textpattern.com/viewtopic.php?id=23728">this forum thread</a>.</p>\n\n	<p class="required">This plugin requires a separate language plugin called <code>zem_contact_lang</code> to be installed and activated to work properly.</p>\n\n	<h2 id="contents">Contents</h2>\n\n	<ul>\n		<li><a href="#features">Features</a></li>\n		<li><a href="#start">Getting started</a>\n	<ul>\n		<li><a href="#contactform">Contact form</a></li>\n		<li><a href="#sendarticle">Send article</a></li>\n	</ul></li>\n		<li><a href="#tags">Tags</a>\n	<ul>\n		<li><a href="#zc"> <code>&#60;txp:zem_contact /&#62;</code> </a></li>\n		<li><a href="#zc_text"> <code>&#60;txp:zem_contact_text /&#62;</code> </a></li>\n		<li><a href="#zc_email"> <code>&#60;txp:zem_contact_email /&#62;</code> </a></li>\n		<li><a href="#zc_textarea"> <code>&#60;txp:zem_contact_textarea /&#62;</code> </a></li>\n		<li><a href="#zc_submit"> <code>&#60;txp:zem_contact_submit /&#62;</code> </a></li>\n		<li><a href="#zc_select"> <code>&#60;txp:zem_contact_select /&#62;</code> </a></li>\n		<li><a href="#zc_checkbox"> <code>&#60;txp:zem_contact_checkbox /&#62;</code> </a></li>\n		<li><a href="#zc_radio"> <code>&#60;txp:zem_contact_radio /&#62;</code> </a></li>\n		<li><a href="#zc_secret"> <code>&#60;txp:zem_contact_secret /&#62;</code> </a></li>\n		<li><a href="#zc_server_info"> <code>&#60;txp:zem_contact_serverinfo /&#62;</code> </a></li>\n		<li><a href="#zc_send_article"> <code>&#60;txp:zem_contact_send_article /&#62;</code> </a></li>\n	</ul></li>\n		<li><a href="#advanced">Advanced examples</a>\n	<ul>\n		<li><a href="#show_error">Separate input and error forms</a></li>\n		<li><a href="#subject_form">User selectable subject field</a></li>\n		<li><a href="#to_form">User selectable recipient, without showing email addresses</a></li>\n	</ul></li>\n		<li><a href="#styling">Styling</a></li>\n		<li><a href="#history">History</a></li>\n		<li><a href="#credits">Credits</a></li>\n		<li><a href="#api">Plugin <span class="caps"><span class="caps">API</span></span> and callback events</a></li>\n	</ul>\n\n	<h2 id="features">Features</h2>\n\n	<ul>\n		<li>Arbitrary text fields can be specified, with min/max/required settings for validation.</li>\n		<li>Email address validation, including a check for a valid MX record (Unix only).</li>\n		<li>Safe escaping of input data.</li>\n		<li>UTF-8 safe.</li>\n		<li>Accessible form layout, including <code>&#60;label&#62;</code>, <code>&#60;legend&#62;</code> and <code>&#60;fieldset&#62;</code> tags.</li>\n		<li>Various classes and ids to allow easy styling of all parts of the form.</li>\n		<li>A separate language plug-in to enable easy localisation.</li>\n		<li>Spam prevention <span class="caps"><span class="caps">API</span></span> (used by Tranquillo&#8217;s <code>pap_contact_cleaner</code> plugin).</li>\n	</ul>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="start">Getting started</h2>\n\n	<h3 id="contactform">Contact form</h3>\n\n	<p>The simplest form is shown below, which produces a default form with Name, Email and Message fields. Email will be delivered to recipient@example.com, with the user&#8217;s supplied email as the &#8220;From:&#8221; address.</p>\n\n<pre><code>&#60;txp:zem_contact to=&#34;recipient@example.com&#34; /&#62;\n</code></pre>\n\n	<p>To specify fields explicitly, use something like this:</p>\n\n<pre><code>&#60;txp:zem_contact to=&#34;recipient@example.com&#34;&#62;\n  &#60;txp:zem_contact_email /&#62;\n  &#60;txp:zem_contact_text label=&#34;Phone&#34; min=7 max=15/&#62;\n  &#60;txp:zem_contact_textarea label=&#34;Your question&#34; /&#62;\n  &#60;txp:zem_contact_submit label=&#34;Send&#34; /&#62;\n&#60;/txp:zem_contact&#62;\n</code></pre>\n\n	<p>Alternatively, place the field specifications in a Textpattern form, and call it like this:</p>\n\n<pre><code>&#60;txp:zem_contact to=&#34;recipient@example.com&#34; form=&#34;mycontactform&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="sendarticle">Send article</h3>\n\n	<p>Within the context of an individual article, this plugin can be used to send the article (or excerpt, if it exists) to an email address specified by the visitor. This requires at least two tags:\n	<ul>\n		<li><code>zem_contact</code>, to create form that is initially hidden by setting the <code>send_article</code> attribute.</li>\n		<li><code>zem_contact_send_article</code>, to create a &#8216;send article&#8217; link which reveals the aforementioned form when clicked.</li>\n	</ul></p>\n\n<pre><code>&#60;txp:zem_contact send_article=&#34;1&#34; /&#62;\n&#60;txp:zem_contact_send_article /&#62;\n</code></pre>\n\n	<p>By default the form contains fields for your name and email address, the recipient&#8217;s email address and a personal message, but similar to contact forms you can create your own form layout. Some things you need to know:\n	<ul>\n		<li>Set the <code>send_article</code> attribute to <code>1</code> in the <code>zem_contact</code> tag.</li>\n		<li>Use a <code>zem_contact_email</code> tag with the <code>send_article</code> attribute set to <code>1</code>. This field will be used as the recipient email address.</li>\n	</ul></p>\n\n<pre><code>&#60;txp:zem_contact to=&#34;you@example.com&#34; send_article=&#34;1&#34;&#62;\n  &#60;txp:zem_contact_email label=&#34;Recipient Email&#34; send_article=&#34;1&#34; /&#62;\n  &#60;txp:zem_contact_email label=&#34;Your Email&#34; /&#62;\n  &#60;txp:zem_contact_submit label=&#34;Send Article&#34; /&#62;\n&#60;/txp:zem_contact&#62;\n</code>\n<code>&#60;txp:zem_contact_send_article /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="tags">Tags</h2>\n\n	<p><a href="#zc"><code>&#60;txp:zem_contact /&#62;</code></a> produces a flexible, customisable email contact form. It is intended for use as an enquiry form for commercial and private sites, and includes several features to help reduce common problems with such forms (invalid email addresses, missing information).</p>\n\n	<p><a href="#zc_send_article"><code>&#60;txp:zem_contact_send_article /&#62;</code></a> can be used to create a &#8220;send article&#8221; link within an article form, connecting it to the contact form.</p>\n\n	<p>All other tags provided by this plugin can only be used inside a <code>&#60;txp:zem_contact&#62;</code> &#8230; <code>&#60;/txp:zem_contact&#62;</code> container tag or in a Textpattern form used as the <code>form</code> attribute in the <code>&#60;txp:zem_contact /&#62;</code> tag.</p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc"><code>&#60;txp:zem_contact /&#62;</code></h3>\n\n	<p>May be used as a self-closing or container tag. Place this where you want the input form to go. Status and error messages, if any, will be displayed before the form.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>to=&#34;email address&#34;</code> <span class="required">required</span><br />\nRecipient email address.</li>\n		<li><code>to_form=&#34;form name&#34;</code><br />\nUse specified form (overrides <strong>to</strong> attribute).</li>\n	</ul>\n\n	<ul>\n		<li><code>from=&#34;email address&#34;</code><br />\nEmail address used in the &#8220;From:&#8221; field when sending email. Defaults to the sender&#8217;s email address. If specified, the sender&#8217;s email address will be placed in the &#8220;Reply-To:&#8221; field instead.</li>\n		<li><code>from_form=&#34;form name&#34;</code><br />\nUse specified form (overrides <strong>from</strong> attribute).</li>\n	</ul>\n\n	<ul>\n		<li><code>subject=&#34;subject text&#34;</code><br />\nSubject used when sending an email. Default is the site name.</li>\n		<li><code>subject_form=&#34;form name&#34;</code><br />\nUse specified form (overrides <strong>subject</strong> attribute).</li>\n	</ul>\n\n	<ul>\n		<li><code>thanks=&#34;text&#34;</code><br />\nMessage shown after successfully submitting a message. Default is <strong>Thank you, your message has been sent</strong>.</li>\n		<li><code>thanks_form=&#34;form name&#34;</code><br />\nUse specified form (overrides <strong>thanks</strong> attribute).</li>\n		<li><code>redirect=&#34;URL&#34;</code><br />\nRedirect to specified <span class="caps"><span class="caps">URL</span></span> (overrides <strong>thanks</strong> and <strong>thanks_form</strong> attributes). <span class="caps"><span class="caps">URL</span></span> must be relative to the Textpattern Site <span class="caps"><span class="caps">URL</span></span>. Example: <em>redirect=&#8220;monkey&#8221;</em> would redirect to http://example.com/monkey.</li>\n	</ul>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nLabel for the contact form. If set to an empty string, display of the fieldset and legend tags will be suppressed. Default is <strong>Contact</strong>.</li>\n		<li><code>send_article=&#34;boolean&#34;</code><br />\nWhether to use this form to <a href="#article">send an article</a>. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>0</strong> (no).</li>\n		<li><code>copysender=&#34;boolean&#34;</code><br />\nWhether to send a copy of the email to the sender&#8217;s address. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>0</strong> (no).</li>\n	</ul>\n\n	<ul>\n		<li><code>form=&#34;form name&#34;</code><br />\nUse specified form, containing the layout of the contact form fields.</li>\n		<li><code>show_input=&#34;boolean&#34;</code><br />\n Whether to display the form input fields. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n		<li><code>show_error=&#34;boolean&#34;</code><br />\n Whether to display error and status messages. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>See <a href="#contactform">Getting started</a> and <a href="#advanced">Advanced examples</a>.</p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_text"><code>&#60;txp:zem_contact_text /&#62;</code></h3>\n\n	<p>Creates a text input field and corresponding <code>&#60;label&#62;</code> tag. The input value will be included in the email, preceded by the label.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nText label displayed to the user. Default is <strong>Text</strong>.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and input field. Default is <code>&#60;br /&#62;</code>. Use <code>break=&#34;&#34;</code> to put the label and input field on the same line.</li>\n		<li><code>default=&#34;value&#34;</code><br />\nDefault value when no input is provided.</li>\n		<li><code>min=&#34;integer&#34;</code><br />\nMinimum input length in characters. Default is <strong>0</strong>.</li>\n		<li><code>max=&#34;integer&#34;</code><br />\nMaximum input length in characters. Default is <strong>100</strong>.</li>\n		<li><code>size=&#34;integer&#34;</code><br />\nSize of the input field as displayed to the user.</li>\n		<li><code>required=&#34;boolean&#34;</code><br />\nWhether this text field must be filled out. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n	</ul>\n\n	<h4>Example</h4>\n\n<pre><code>&#60;txp:zem_contact_text label=&#34;Your name&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_email"><code>&#60;txp:zem_contact_email /&#62;</code></h3>\n\n	<p>Input field for user&#8217;s email address.</p>\n\n	<p>The entered email address will automatically be validated to make sure it is of the form &#8220;abc@xxx.yyy[.zzz]&#8221;. On non-Windows servers, a test will be done to verify that an A or MX record exists for the domain. Neither test prevents spam, but it does help detecting accidental typing errors.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nText label displayed to the user. Default is <strong>Email</strong>.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and input field. Default is <code>&#60;br /&#62;</code>. Use <code>break=&#34;&#34;</code> to put the label and input field on the same line.</li>\n		<li><code>default=&#34;value&#34;</code><br />\nDefault value when no input is provided.</li>\n		<li><code>min=&#34;integer&#34;</code><br />\nMinimum input length in characters. Default is <strong>0</strong>.</li>\n		<li><code>max=&#34;integer&#34;</code><br />\nMaximum input length in characters. Default is <strong>100</strong>.</li>\n		<li><code>size=&#34;integer&#34;</code><br />\nSize of the input field as displayed to the user.</li>\n		<li><code>required=&#34;boolean&#34;</code><br />\nWhether this text field must be filled out. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n		<li><code>send_article=&#34;boolean&#34;</code><br />\nWhether this field is used as the recipient email address when using the send_article function. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>0</strong> (no).</li>\n	</ul>\n\n	<h4>Example</h4>\n\n<pre><code>&#60;txp:zem_contact_email label=&#34;Your email address&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_textarea"><code>&#60;txp:zem_contact_textarea /&#62;</code></h3>\n\n	<p>Creates a textarea.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nText label displayed to the user. Default is <strong>Message</strong>.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and input field. Default is <code>&#60;br /&#62;</code>. Use <code>break=&#34;&#34;</code> to put the label and input field on the same line.</li>\n		<li><code>default=&#34;value&#34;</code><br />\nDefault value when no input is provided.</li>\n		<li><code>cols=&#34;integer&#34;</code><br />\nColumn width. Default is <strong>58</strong>.</li>\n		<li><code>rows=&#34;integer&#34;</code><br />\nRow height. Default is <strong>8</strong>.</li>\n		<li><code>min=&#34;integer&#34;</code><br />\nMinimum input length in characters. Default is <strong>0</strong>.</li>\n		<li><code>max=&#34;integer&#34;</code><br />\nMaximum input length in characters. Default is <strong>10000</strong>.</li>\n		<li><code>required=&#34;boolean&#34;</code><br />\nWhether this text field must be filled out. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n	</ul>\n\n	<h4>Example</h4>\n\n	<p>Textarea that is 40 chars wide, 10 lines high, with a customized label:</p>\n\n<pre><code>&#60;txp:zem_contact_textarea cols=&#34;40&#34; rows=&#34;10&#34; label=&#34;Your question&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_submit"><code>&#60;txp:zem_contact_submit /&#62;</code></h3>\n\n	<p>Creates a submit button.<br />\nWhen used as a container tag, a &#8220;button&#8221; element will be used instead of an &#8220;input&#8221; element.</p>\n\n	<h4>Attributes:</h4>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nText shown on the submit button. Default is &#8220;Send&#8221;.</li>\n		<li><code>button=&#34;boolean&#34;</code><br />\n<em>Deprecated. Use a container tag if you want a button element.</em></li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>Standard submit button:</p>\n\n<pre><code>&#60;txp:zem_contact_submit /&#62;\n</code></pre>\n\n	<p>Submit button with your own text:</p>\n\n<pre><code>&#60;txp:zem_contact_submit label=&#34;Send&#34; /&#62;\n</code></pre>\n\n	<p>Usage as a container tag, which allows you to use Textpattern tags and <span class="caps">HTML</span> markup in the submit button:</p>\n\n<pre><code>&#60;txp:zem_contact_submit&#62;&#60;strong&#62;Send&#60;/strong&#62; question&#60;/txp:zem_contact_submit&#62;\n</code></pre>\n\n<pre><code>&#60;txp:zem_contact_submit&#62;&#60;img src=&#34;path/to/img.png&#34; alt=&#34;submit&#34;&#62;&#60;/txp:zem_contact_submit&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_select"><code>&#60;txp:zem_contact_select /&#62;</code></h3>\n\n	<p>Creates a drop-down selection list.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>list=&#34;comma-separated values&#34;</code> <span class="required">required</span><br />\nList of items to show in the select box.</li>\n		<li><code>selected=&#34;value&#34;</code><br />\nList item that is selected by default.</li>\n		<li><code>label=&#34;text&#34;</code><br />\nText label displayed to the user. Default is <strong>Option</strong>.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and input field. Default is <code>&#60;br /&#62;</code>. Use <code>break=&#34;&#34;</code> to put the label and input field on the same line.</li>\n		<li><code>delimiter=&#34;character&#34;</code><br />\nSeparator character used in the <strong>list</strong> attribute. Default is <strong>,</strong> (comma).</li>\n		<li><code>required=&#34;boolean&#34;</code><br />\nWhether a non-empty option must be selected. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>Select list labeled &#8216;Department&#8217;, containing three options and a blank option (due to the comma before &#8216;Marketing&#8217;) shown by default, forcing the user to make a selection.</p>\n\n<pre><code>&#60;txp:zem_contact_select label=&#34;Department&#34; list=&#34;,Marketing,Sales,Support&#34; /&#62;\n</code></pre>\n\n	<p>Select list containing three options with &#8216;Marketing&#8217; selected by default.</p>\n\n<pre><code>&#60;txp:zem_contact_select list=&#34;Marketing,Sales,Support&#34; selected=&#34;Marketing&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_checkbox"><code>&#60;txp:zem_contact_checkbox /&#62;</code></h3>\n\n	<p>Creates a check box.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>label=&#34;text&#34;</code><br />\nText label displayed to the user. Default is <strong>Checkbox</strong>.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and input field. Default is <code>&#60;br /&#62;</code>. Use <code>break=&#34;&#34;</code> to put the label and input field on the same line.</li>\n		<li><code>checked=&#34;boolean&#34;</code><br />\nWhether this box is checked when first displayed. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is &#8220;0&#8221; (no).</li>\n		<li><code>required=&#34;boolean&#34;</code><br />\nWhether this checkbox must be filled out. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>1</strong> (yes).</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>Shrink-wrap agreement which must be checked by the user before the email will be sent.</p>\n\n<pre><code>&#60;txp:zem_contact_checkbox label=&#34;I accept the terms and conditions&#34; /&#62;\n</code></pre>\n\n	<p>Optional checkboxes:</p>\n\n<pre><code>Select which operating systems are you familiar with:&#60;br /&#62;\n&#60;txp:zem_contact_checkbox label=&#34;Windows&#34; required=&#34;0&#34; /&#62;&#60;br /&#62;\n&#60;txp:zem_contact_checkbox label=&#34;Unix/Linux/BSD&#34; required=&#34;0&#34; /&#62;&#60;br /&#62;\n&#60;txp:zem_contact_checkbox label=&#34;MacOS&#34; required=&#34;0&#34; /&#62;&#60;br /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_radio"><code>&#60;txp:zem_contact_radio /&#62;</code></h3>\n\n	<p>Creates a radio button.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>group=&#34;text&#34;</code> <span class="required">required</span><br />\nText used in the email to describe this group of radio buttons. This attribute value is remembered for subsequent radio buttons, so you only have to set it on the first radio button of a group. Default is <strong>Radio</strong>.</li>\n		<li><code>label=&#34;text&#34;</code> <span class="required">required</span><br />\nText label displayed to the user as radio button option.</li>\n		<li><code>name=&#34;value&#34;</code><br />\nField name, as used in the <span class="caps"><span class="caps">HTML</span></span> input tag. This attribute value is remembered for subsequent radio buttons, so you only have to set it on the first radio button of a group. If it hasn&#8217;t been set at all, it will be derived from the <code>group</code> attribute.</li>\n		<li><code>break=&#34;tag&#34;</code><br />\nBreak tag between the label and field. Default is a space.</li>\n		<li><code>checked=&#34;boolean&#34;</code><br />\nWhether this radio option is checked when the form is first displayed. Available values: <strong>1</strong> (yes), <strong>0</strong> (no). Default is <strong>0</strong> (no).</li>\n	</ul>\n\n	<h4>Example</h4>\n\n	<p>Group mutually exclusive radio buttons by setting the <code>group</code> attribute on the first radio button in a group. Only the chosen radio button from each group will be used in the email message. The message will be output in the form <strong>group: label</strong> for each of the chosen radio buttons.</p>\n\n<pre><code>&#60;txp:zem_contact_radio label=&#34;Medium&#34; group=&#34;I like my steak&#34; /&#62;\n&#60;txp:zem_contact_radio label=&#34;Rare&#34; /&#62;\n&#60;txp:zem_contact_radio label=&#34;Well done&#34; /&#62;\n</code>\n<code>&#60;txp:zem_contact_radio label=&#34;Wine&#34; group=&#34;With a glass of&#34; /&#62;\n&#60;txp:zem_contact_radio label=&#34;Beer&#34; /&#62;\n&#60;txp:zem_contact_radio label=&#34;Water&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_secret"><code>&#60;txp:zem_contact_secret /&#62;</code></h3>\n\n	<p>This tag has no effect on the form or <span class="caps">HTML</span> output, but will include additional information in the email. It can be used as a self-closing tag or as a container tag.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>name=&#34;text&#34;</code><br />\nUsed internally. Set this only if you have multiple &#8216;secret&#8217; form elements with identical labels.</li>\n		<li><code>label=&#34;text&#34;</code><br />\nUsed to identify the field in the email. Default is <strong>Secret</strong>.</li>\n		<li><code>value=&#34;value&#34;</code><br />\nSome text you want to add to the email.</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>Usage as a self-closing tag</p>\n\n<pre><code>&#60;txp:zem_contact_secret value=&#34;The answer is 42&#34; /&#62;\n</code></pre>\n\n	<p>Usage as a container tag</p>\n\n<pre><code>&#60;txp:zem_contact_secret label=&#34;Dear user&#34;&#62;\n  Please provide a useful example for this tag!\n&#60;/txp:zem_contact_secret&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_serverinfo"><code>&#60;txp:zem_contact_serverinfo /&#62;</code></h3>\n\n	<p>This tag has no effect on the form or <span class="caps">HTML</span> output, but will include additional information in the email based on the <span class="caps">PHP</span> $_SERVER variable.</p>\n\n	<h4>Attributes</h4>\n\n	<ul>\n		<li><code>name=&#34;value&#34;</code> <span class="required">required</span><br />\nName of the server variable. See the <a href="http://php.net/manual/reserved.variables.php#reserved.variables.server"><span class="caps">PHP</span> manual</a> for a full list.</li>\n		<li><code>label=&#34;text&#34;</code><br />\nUsed to identify the field in the email. Defaults to the value of the <strong>name</strong> attribute.</li>\n	</ul>\n\n	<h4>Examples</h4>\n\n	<p>Add the IP address of the visitor to the email</p>\n\n<pre><code>&#60;txp:zem_contact_serverinfo name=&#34;REMOTE_ADDR&#34; label=&#34;IP number&#34; /&#62;\n</code></pre>\n\n	<p>Add the name of the visitor&#8217;s browser to the email</p>\n\n<pre><code>&#60;txp:zem_contact_serverinfo name=&#34;HTTP_USER_AGENT&#34; label=&#34;Browser&#34; /&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="zc_send_article"><code>&#60;txp:zem_contact_send_article /&#62;</code></h3>\n\n	<p>Use this tag in your individual article form, where you want the &#8220;send article&#8221; link to be displayed.</p>\n\n	<h4>Attributes:</h4>\n\n	<ul>\n		<li><code>linktext=&#34;text&#34;</code><br />\nText displayed for the link. Default is <strong>send article</strong></li>\n	</ul>\n\n	<h4>Examples:</h4>\n\n	<p>See <a href="#sendarticle">Getting started</a></p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="advanced">Advanced examples</h2>\n\n	<h3 id="show_error">Separate input and error forms</h3>\n\n	<p>Using <code>show_input</code> and <code>show_error</code> to display the form and error messages on different parts of a page. A form is used to make sure the contents of both forms are identical, otherwise they would be seen as two independent forms. The first form only shows errors (no input), the second form only shows the input fields (no errors).</p>\n\n<pre><code>&#60;div id=&#34;error&#34;&#62;\n  &#60;txp:zem_contact form=&#34;contact_form&#34; show_input=&#34;0&#34; /&#62;\n&#60;/div&#62;\n</code>\n<code>&#60;div id=&#34;inputform&#34;&#62;\n  &#60;txp:zem_contact form=&#34;contact_form&#34; show_error=&#34;0&#34; /&#62;\n&#60;/div&#62;\n</code></pre>\n\n	<p>Apart from the <code>show_error</code> and <code>show_input</code> attributes, all other attributes must be 100% identical in both forms, otherwise they would be seen as two unrelated forms.</p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="subject_form">User selectable subject field</h3>\n\n	<p>Specify the <code>subject_form</code> attribute and create a form which includes a <code>zem_contact_select</code> tag:</p>\n\n<pre><code>&#60;txp:zem_contact to=&#34;you@example.com&#34; subject_form=&#34;my_subject_form&#34; /&#62;\n  &#60;txp:zem_contact_text label=&#34;Name&#34; /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_email /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_select label=&#34;Choose Subject&#34; list=&#34;,Question,Feedback&#34; /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_textarea label=&#34;Message&#34; /&#62;&#60;br /&#62;\n&#60;/txp:zem_contact&#62;\n</code></pre>\n\n	<p>Create a Textpattern form called &#8220;my_subject_form&#8221;, containing:</p>\n\n<pre><code>&#60;txp:php&#62;\n  global $zem_contact_form;\n  echo $zem_contact_form[&#39;Choose Subject&#39;];\n&#60;/txp:php&#62;\n</code></pre>\n\n	<p>The <code>label</code> used in the <code>zem_contact_select</code> tag must be identical to the corresponding variable in the <code>subject_form</code>. Here we used <code>Choose subject</code>.</p>\n\n	<p>If you&#8217;d prefer to add a common prefix for all subjects, use a <code>subject_form</code> containing:</p>\n\n<pre><code>&#60;txp:php&#62;\n  global $zem_contact_form;\n  echo &#39;My common prefix - &#39; . $zem_contact_form[&#39;Choose Subject&#39;];\n&#60;/txp:php&#62;\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h3 id="to_form">User selectable recipient, without showing email address</h3>\n\n	<p>Specify the <code>to_form</code> attribute and create a form which includes a <code>zem_contact_select</code> tag:</p>\n\n<pre><code>&#60;txp:zem_contact to_form=&#34;my_zem_contact_to_form&#34;&#62;\n  &#60;txp:zem_contact_text label=&#34;Name&#34; /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_email /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_select label=&#34;Department&#34; list=&#34;,Support,Sales&#34; /&#62;&#60;br /&#62;\n  &#60;txp:zem_contact_textarea label=&#34;Message&#34; /&#62;&#60;br /&#62;\n&#60;/txp:zem_contact&#62;\n</code></pre>\n\n	<p>Create a Textpattern form called &#8220;my_zem_contact_to_form&#8221;, containing:</p>\n\n<pre><code>&#60;txp:php&#62;\n  global $zem_contact_form;\n  switch($zem_contact_form[&#39;Department&#39;])\n  {\n    case &#39;Support&#39;:\n      echo &#39;crew@example.com&#39;;\n      break;\n    case &#39;Sales&#39;:\n      echo &#39;showmethemoney@example.com&#39;;\n      break;\n    default:\n      echo &#39;someone@example.com&#39;;\n  }\n&#60;/txp:php&#62;\n</code></pre>\n\n	<p>The <code>label</code> used in the <code>zem_contact_select</code> tag must be identical to the corresponsing variable in the <code>to_form</code>. Here we used <code>Department</code>.</p>\n\n	<p>A &#8216;default&#8217; email address in the <code>to_form</code> is specified to ensure that a valid email address is used in cases where you add or change a select/radio option and forget to update the <code>to_form</code>.</p>\n\n	<p class="warning">Never use tags like <code>zem_contact_text</code>, <code>zem_contact_email</code> or <code>zem_contact_textarea</code> for setting the recipient address, otherwise your form can be abused to send spam to any email address!</p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="styling">Styling</h2>\n\n	<p>The form itself has a class <strong>zemContactForm</strong> set on the <code>FORM</code> <span class="caps">HTML</span> tag.</p>\n\n	<p>The list of error messages (if any) has a class <strong>zemError</strong> set on the <code>UL</code> <span class="caps">HTML</span> tag that encloses the list of errors.</p>\n\n	<p>All form elements and corresponding labels have the following classes (or ids set):\n	<ol>\n		<li>One of <strong>zemText</strong>, <strong>zemTextarea</strong>, <strong>zemSelect</strong>, <strong>zemRadio</strong>, <strong>zemCheckbox</strong>, <strong>zemSubmit</strong>. It should be obvious which class is used for which form element (and corresponding label).</li>\n		<li><strong>zemRequired</strong> or <strong>errorElement</strong> or <strong>zemRequirederrorElement</strong>, depending on whether the form element is required, an error was found in whatever the visitor entered&#8230; or both.</li>\n		<li>An individual &#8220;id&#8221; or &#8220;class&#8221; set to the value of the <code>name</code> attribute of the corresponding tag. When styling forms based on this class, you should explicitly set the <code>name</code> attribute because automatically generated names may change in newer zem_contact_reborn versions.</li>\n	</ol></p>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="history">History</h2>\n\n	<p>Only the changes that may affect people who upgrade are detailed below.<br />\nTo save space, links to forum topics that detail all the changes in each version have been added.</p>\n\n	<ul>\n		<li>14 feb 2007: <strong>version 4.0.3.19</strong> <a href="http://forum.textpattern.com/viewtopic.php?id=21144">changelog</a>\n	<ul>\n		<li><a href="#sendarticle">send_article</a> functionality revised, requiring changes when upgrading from earlier versions</li>\n		<li>New language strings: &#8216;send_article&#8217; and &#8216;recipient&#8217; (replaces &#8216;receiver&#8217;)</li>\n		<li>Sets of radio buttons require the new <a href="#zc_radio">group</a> attribute</li>\n		<li>Yes/No values deprecated in favor for the <span class="caps"><span class="caps">TXP</span></span> standard 1/0 values (yes/no still work in this version)</li>\n	</ul></li>\n		<li>20 nov 2006: <strong>version 4.0.3.18</strong> <a href="http://forum.textpattern.com/viewtopic.php?id=19823">changelog</a>\n	<ul>\n		<li>IDs &#8216;zemContactForm&#8217; and &#8216;zemSubmit&#8217; have changed to classes to allow multiple forms per page</li>\n		<li>New language strings: &#8216;form_used&#8217;, &#8216;invalid_utf8&#8217;, &#8216;max_warning&#8217;, &#8216;name&#8217;, &#8216;refresh&#8217;, &#8216;secret&#8217;</li>\n	</ul></li>\n		<li>12 mar 2006: <strong>version 4.0.3.17</strong> <a href="http://forum.textpattern.com/viewtopic.php?id=13416">changelog</a></li>\n		<li>11 feb 2006: <strong>version .16</strong></li>\n		<li>06 feb 2006: <strong>version .15</strong></li>\n		<li>03 feb 2006: <strong>version .14</strong>\n	<ul>\n		<li>Requires separate zem_contact_lang plugin</li>\n	</ul></li>\n		<li>29 jan 2006: <strong>version .12</strong></li>\n		<li>27 jan 2006: <strong>version .11</strong></li>\n		<li>23 jan 2006: <strong>version .09 and .10</strong></li>\n		<li>23 jan 2006: <strong>version .08</strong></li>\n		<li>17 jan 2006: <strong>version .07</strong></li>\n		<li>16 jan 2006: <strong>version .05 and .06</strong></li>\n		<li>15 jan 2006: <strong>version .04</strong></li>\n		<li>10 jan 2006: <strong>version .03</strong></li>\n		<li>19 dec 2005: <strong>version .02</strong></li>\n	</ul>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="credits">Credits</h2>\n\n	<ul>\n		<li><strong>zem</strong> wrote the zem_contact 0.6 plugin on which this plugin was initially based.</li>\n		<li><strong>Mary</strong> completely revised the plugin code.</li>\n		<li><strong>Stuart</strong> Turned it into a plugin, added a revised help text and additional code. Maintained all plugin versions till 4.0.3.17.</li>\n		<li><strong>wet</strong> added the zem_contact_radio tag.</li>\n		<li><strong>Tranquillo</strong> added the anti-spam <span class="caps"><span class="caps">API</span></span> and zem_contact_send_article functionality.</li>\n		<li><strong>aslsw66</strong>, <strong>jdykast</strong> and others (?) provided additional code</li>\n		<li><strong>Ruud</strong> cleaned up and audited the code to weed out bugs and completely revised the help text. Maintainer of versions 4.0.3.18 and up.</li>\n		<li>Supported and tested to destruction by the Textpattern community.</li>\n	</ul>\n\n	<p><a href="#top">Back to top</a></p>\n\n	<h2 id="api">Zem Contact Reborn&#8217;s <span class="caps">API</span></h2>\n\n	<p>The plugin <span class="caps">API</span> of zem contact, developed by Tranquillo, is similar to the comments <span class="caps">API</span> of Textpattern, which is explained in the Textbook <a href="http://textpattern.net/wiki/index.php?title=Plugin_Development_Topics">Plugin Development Topics</a> and <a href="http://textpattern.net/wiki/index.php?title=Combat_Comment_Spam">Combat Comment Spam</a>.</p>\n\n	<p>Two callback events exist in zem_contact_reborn:\n	<ul>\n		<li><code>zemcontact.submit</code> is called after the form is submitted and the values are checked if empty or valid email addresses, but before the mail is sent.</li>\n		<li><code>zemcontact.form</code> let&#8217;s you insert content in the contact form as displayed to the visitor.</li>\n	</ul></p>\n\n	<p>For reference here are the commands that will be interesting to plugin developers:</p>\n\n<pre><code>// This will call your function before the form is submitted\n// So you can analyse the submitted data\nregister_callback(&#39;abc_myfunction&#39;,&#39;zemcontact.submit&#39;);\n</code>\n<code>// This will call your function and add the output (use return $mystuff)\n// to the contact-form.\nregister_callback(&#39;abc_myotherfunction2&#39;,&#39;zemcontact.form&#39;);\n</code>\n<code>// To get hold of the form-variables you can use\nglobal zem_contact_form;\n</code>\n<code>// With the following two lines you can tell zem_contact if your\n// plugin found spam\n$evaluator =&#38; get_zemcontact_evaluator();\n</code>\n<code>// The passed value must be non-zero to mark the content as spam.\n// Value must be a number between 0 and 1.\n$evaluator -&#62; add_zemcontact_status(1);\n</code></pre>\n\n	<p>Multiple plugins can be active at the same time and each of them can mark the submitted content as spam and prevent the form from being submitted.</p>\n\n	<p><strong>An example of a plug-in connecting to Zem Contact Reborn&#8217;s API:</strong></p>\n\n<pre><code>register_callback(&#39;pap_zemcontact_form&#39;,&#39;zemcontact.form&#39;);\nregister_callback(&#39;pap_zemcontact_submit&#39;,&#39;zemcontact.submit&#39;);\n</code>\n<code>function pap_zemcontact_form() {\n  $field = &#39;&#60;div style=&#34;display:none&#34;&#62;&#39;.\n    finput(&#39;text&#39;,&#39;phone&#39;,ps(&#39;phone&#39;),&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;phone&#39;).&#39;&#60;br /&#62;&#39;.\n    finput(&#39;text&#39;,&#39;mail&#39;,ps(&#39;mail&#39;),&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;&#39;,&#39;mail&#39;).&#39;&#60;/div&#62;&#39;;\n  return $field;&#60;/code&#62;\n}\n</code>\n<code>function pap_zemcontact_submit() {\n  $checking_mail_field = trim(ps(&#39;mail&#39;));\n  $checking_phone_field = trim(ps(&#39;phone&#39;));\n</code>\n<code>  $evaluation =&#38; get_zemcontact_evaluator();\n</code>\n<code>  // If the hidden fields are filled out, the contact form won&#39;t be submitted!\n  if ($checking_mail_field != &#39;&#39; or $checking_phone_field != &#39;&#39;) {\n    $evaluation -&#62; add_zemcontact_status(1);\n  }\n  return;\n}\n</code></pre>\n\n	<p><a href="#top">Back to top</a></p>', '\nfunction zem_contact($atts, $thing = '''')\n{\n	global $sitename, $prefs, $production_status, $zem_contact_from,\n		$zem_contact_recipient, $zem_contact_error, $zem_contact_submit,\n		$zem_contact_form, $zem_contact_labels, $zem_contact_values;\n\n	extract(zem_contact_lAtts(array(\n		''copysender''	=> 0,\n		''form''		=> '''',\n		''from''		=> '''',\n		''from_form''	=> '''',\n		''label''		=> zem_contact_gTxt(''contact''),\n		''redirect''	=> '''',\n		''show_error''	=> 1,\n		''show_input''	=> 1,\n		''send_article''	=> 0,\n		''subject''	=> zem_contact_gTxt(''email_subject'', html_entity_decode($sitename,ENT_QUOTES)),\n		''subject_form''	=> '''',\n		''to''		=> '''',\n		''to_form''	=> '''',\n		''thanks''	=> graf(zem_contact_gTxt(''email_thanks'')),\n		''thanks_form''	=> ''''\n	), $atts));\n\n	unset($atts[''show_error''], $atts[''show_input'']);\n	$zem_contact_form_id = md5(serialize($atts).preg_replace(''/[\\t\\s\\r\\n]/'','''',$thing));\n	$zem_contact_submit = (ps(''zem_contact_form_id'') == $zem_contact_form_id);\n\n	if (!is_callable(''mail''))\n	{\n		return ($production_status == ''live'') ?\n			zem_contact_gTxt(''mail_sorry'') :\n			gTxt(''warn_mail_unavailable'');\n	}\n\n	static $headers_sent = false;\n	if (!$headers_sent) {\n		header(''Last-Modified: ''.gmdate(''D, d M Y H:i:s'',time()-3600*24*7).'' GMT'');\n		header(''Expires: ''.gmdate(''D, d M Y H:i:s'',time()+600).'' GMT'');\n		header(''Cache-Control: no-cache, must-revalidate'');\n		$headers_sent = true;\n	}\n\n	$nonce   = mysql_real_escape_string(ps(''zem_contact_nonce''));\n	$renonce = false;\n\n	if ($zem_contact_submit)\n	{\n		safe_delete(''txp_discuss_nonce'', ''issue_time < date_sub(now(), interval 10 minute)'');\n		if ($rs = safe_row(''used'', ''txp_discuss_nonce'', "nonce = ''$nonce''"))\n		{\n			if ($rs[''used''])\n			{\n				unset($zem_contact_error);\n				$zem_contact_error[] = zem_contact_gTxt(''form_used'');\n				$renonce = true;\n				$_POST = array();\n				$_POST[''zem_contact_submit''] = TRUE;\n				$_POST[''zem_contact_form_id''] = $zem_contact_form_id;\n				$_POST[''zem_contact_nonce''] = $nonce;\n			}\n		}\n		else\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''form_expired'');\n			$renonce = true;\n		}\n	}\n\n	if ($zem_contact_submit and $nonce and !$renonce)\n	{\n		$zem_contact_nonce = $nonce;\n	}\n\n	elseif (!$show_error or $show_input)\n	{\n		$zem_contact_nonce = md5(uniqid(rand(), true));\n		safe_insert(''txp_discuss_nonce'', "issue_time = now(), nonce = ''$zem_contact_nonce''");\n	}\n\n	$form = ($form) ? fetch_form($form) : $thing;\n\n	if (empty($form))\n	{\n		$form = ''\n<txp:zem_contact_text label="''.zem_contact_gTxt(''name'').''" /><br />\n<txp:zem_contact_email /><br />''.\n($send_article ? ''<txp:zem_contact_email send_article="1" label="''.zem_contact_gTxt(''recipient'').''" /><br />'' : '''').\n''<txp:zem_contact_textarea /><br />\n<txp:zem_contact_submit />\n'';\n	}\n\n	$form = parse($form);\n\n	if ($to_form)\n	{\n		$to = parse(fetch_form($to_form));\n	}\n\n	if (!$to and !$send_article)\n	{\n		return zem_contact_gTxt(''to_missing'');\n	}\n\n	$out = '''';\n\n	if (!$zem_contact_submit) {\n	  # don''t show errors or send mail\n	}\n\n	elseif (!empty($zem_contact_error))\n	{\n		if ($show_error or !$show_input)\n		{\n			$out .= n.''<ul class="zemError">'';\n\n			foreach (array_unique($zem_contact_error) as $error)\n			{\n				$out .= n.t.''<li>''.$error.''</li>'';\n			}\n\n			$out .= n.''</ul>'';\n\n			if (!$show_input) return $out;\n		}\n	}\n\n	elseif ($show_input and is_array($zem_contact_form))\n	{\n		/// load and check spam plugins/\n		callback_event(''zemcontact.submit'');\n		$evaluation =& get_zemcontact_evaluator();\n		$clean = $evaluation->get_zemcontact_status();\n		if ($clean != 0) {\n			return zem_contact_gTxt(''spam'');\n		}\n\n		if ($from_form)\n		{\n			$from = parse(fetch_form($from_form));\n		}\n\n		if ($subject_form)\n		{\n			$subject = parse(fetch_form($subject_form));\n		}\n\n		$sep = !is_windows() ? "\\n" : "\\r\\n";\n\n		$msg = array();\n\n		foreach ($zem_contact_labels as $name => $label)\n		{\n			if (!trim($zem_contact_values[$name])) continue;\n			$msg[] = $label.'': ''.$zem_contact_values[$name];\n		}\n\n		if ($send_article)\n		{\n			global $thisarticle;\n			$subject = str_replace(''&#38;'', ''&'', $thisarticle[''title'']);\n			$msg[] = permlinkurl($thisarticle);\n			$msg[] = $subject;\n			$s_ar = array(''&#8216;'', ''&#8217;'', ''&#8220;'', ''&#8221;'', ''&#8217;'', ''&#8242;'', ''&#8243;'', ''&#8230;'', ''&#8211;'', ''&#8212;'', ''&#215;'', ''&#8482;'', ''&#174;'', ''&#169;'', ''&lt;'', ''&gt;'', ''&quot;'', ''&amp;'', ''&#38;'', "\\t", ''<p'');\n			if ($prefs[''override_emailcharset''] and is_callable(''utf8_decode'')) {\n				$r_ar = array("''", "''", ''"'', ''"'', "''", "''", ''"'', ''...'', ''-'', ''--'', ''x'', ''[tm]'', ''(r)'', ''(c)'', ''<'', ''>'', ''"'', ''&'', ''&'', '' '', "\\n<p");\n			}\n			else\n			{\n				$r_ar = array(''‘'', ''’'', ''“'', ''”'', ''’'', ''′'', ''″'', ''…'', ''–'', ''—'', ''×'', ''™'', ''®'', ''©'', ''<'', ''>'', ''"'', ''&'', ''&'', '' '', "\\n<p");\n			}\n			$msg[] = trim(strip_tags(str_replace($s_ar,$r_ar,(trim(strip_tags($thisarticle[''excerpt''])) ? $thisarticle[''excerpt''] : $thisarticle[''body'']))));\n			if (empty($zem_contact_recipient))\n			{\n				return zem_contact_gTxt(''field_missing'', zem_contact_gTxt(''recipient''));\n			}\n			else\n			{\n				$to = $zem_contact_recipient;\n			}\n		}\n\n		$msg = join("\\n\\n", $msg);\n		$msg = str_replace("\\r\\n", "\\n", $msg);\n		$msg = str_replace("\\r", "\\n", $msg);\n		$msg = str_replace("\\n", $sep, $msg);\n\n		if ($from)\n		{\n			$reply = $zem_contact_from;\n		}\n\n		else\n		{\n			$from = $zem_contact_from;\n			$reply = '''';\n		}\n\n		$from    = zem_contact_strip($from);\n		$to      = zem_contact_strip($to);\n		$subject = zem_contact_strip($subject);\n		$reply   = zem_contact_strip($reply);\n		$msg     = zem_contact_strip($msg, FALSE);\n\n		if ($prefs[''override_emailcharset''] and is_callable(''utf8_decode''))\n		{\n			$charset = ''ISO-8859-1'';\n			$subject = utf8_decode($subject);\n			$msg     = utf8_decode($msg);\n		}\n\n		else\n		{\n			$charset = ''UTF-8'';\n		}\n\n		$subject = zem_contact_mailheader($subject, ''text'');\n\n		$headers = ''From: ''.$from.\n			($reply ? ($sep.''Reply-To: ''.$reply) : '''').\n			$sep.''X-Mailer: Textpattern (zem_contact_reborn)''.\n			$sep.''X-Originating-IP: ''.zem_contact_strip((!empty($_SERVER[''HTTP_X_FORWARDED_FOR'']) ? $_SERVER[''HTTP_X_FORWARDED_FOR''].'' via '' : '''').$_SERVER[''REMOTE_ADDR'']).\n			$sep.''Content-Transfer-Encoding: 8bit''.\n			$sep.''Content-Type: text/plain; charset="''.$charset.''"'';\n\n		safe_update(''txp_discuss_nonce'', "used = ''1'', issue_time = now()", "nonce = ''$nonce''");\n\n		if (mail($to, $subject, $msg, $headers))\n		{\n			$_POST = array();\n\n			if ($copysender and $zem_contact_from)\n			{\n				mail(zem_contact_strip($zem_contact_from), $subject, $msg, $headers);\n			}\n\n			if ($redirect)\n			{\n				while (@ob_end_clean());\n				$uri = hu.ltrim($redirect,''/'');\n				if (empty($_SERVER[''FCGI_ROLE'']) and empty($_ENV[''FCGI_ROLE'']))\n				{\n					txp_status_header(''303 See Other'');\n					header(''Location: ''.$uri);\n					header(''Connection: close'');\n					header(''Content-Length: 0'');\n				}\n				else\n				{\n					$uri = htmlspecialchars($uri);\n					$refresh = zem_contact_gTxt(''refresh'');\n					echo <<<END\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n	<title>$sitename</title>\n	<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\n	<meta http-equiv="refresh" content="0;url=$uri" />\n</head>\n<body>\n<a href="$uri">$refresh</a>\n</body>\n</html>\nEND;\n				}\n				exit;\n			}\n\n			else\n			{\n				return ''<div class="zemThanks" id="zcr''.$zem_contact_form_id.''">'' .\n					($thanks_form ? fetch_form($thanks_form) : $thanks) .\n					''</div>'';\n			}\n		}\n\n		else\n		{\n			$out .= graf(zem_contact_gTxt(''mail_sorry''));\n		}\n	}\n\n	if ($show_input and !$send_article or gps(''zem_contact_send_article''))\n	{\n		return ''<form method="post"''.((!$show_error and $zem_contact_error) ? '''' : '' id="zcr''.$zem_contact_form_id.''"'').'' class="zemContactForm" action="''.htmlspecialchars(serverSet(''REQUEST_URI'')).''#zcr''.$zem_contact_form_id.''">''.\n			( $label ? n.''<fieldset>'' : n.''<div>'' ).\n			( $label ? n.''<legend>''.htmlspecialchars($label).''</legend>'' : '''' ).\n			$out.\n			n.''<input type="hidden" name="zem_contact_nonce" value="''.$zem_contact_nonce.''" />''.\n			n.''<input type="hidden" name="zem_contact_form_id" value="''.$zem_contact_form_id.''" />''.\n			$form.\n			callback_event(''zemcontact.form'').\n			( $label ? (n.''</fieldset>'') : (n.''</div>'') ).\n			n.''</form>'';\n	}\n\n	return '''';\n}\n\nfunction zem_contact_strip($str, $header = TRUE) {\n	if ($header) $str = strip_rn($str);\n	return preg_replace(''/[\\x00]/'', '' '', $str);\n}\n\nfunction zem_contact_text($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> br,\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''text''),\n		''max''		=> 100,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''size''		=> ''''\n	), $atts));\n\n	$min = intval($min);\n	$max = intval($max);\n	$size = intval($size);\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = trim(ps($name));\n		$utf8len = preg_match_all("/./su", $value, $utf8ar);\n		$hlabel = htmlspecialchars($label);\n\n		if (strlen($value))\n		{\n			if (!$utf8len)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_utf8'', $hlabel);\n				$isError = "errorElement";\n			}\n\n			elseif ($min and $utf8len < $min)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''min_warning'', $hlabel, $min);\n				$isError = "errorElement";\n			}\n\n			elseif ($max and $utf8len > $max)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''max_warning'', $hlabel, $max);\n				$isError = "errorElement";\n				#$value = join('''',array_slice($ar[0],0,$max));\n			}\n\n			else\n			{\n				zem_contact_store($name, $label, $value);\n			}\n		}\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', $hlabel);\n			$isError = "errorElement";\n		}\n	}\n\n	else\n	{\n		$value = $default;\n	}\n\n	$size = ($size) ? '' size="''.$size.''"'' : '''';\n	$maxlength = ($max) ? '' maxlength="''.$max.''"'' : '''';\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n        return ''<label for="''.$name.''" class="zemText ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		''<input type="text" id="''.$name.''" class="zemText ''.$zemRequired.$isError.''" name="''.$name.''" value="''.htmlspecialchars($value).''"''.$size.$maxlength.'' />'';\n}\n\nfunction zem_contact_textarea($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> br,\n		''cols''		=> 58,\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''message''),\n		''max''		=> 10000,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''rows''		=> 8\n	), $atts));\n\n	$min = intval($min);\n	$max = intval($max);\n	$cols = intval($cols);\n	$rows = intval($rows);\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = preg_replace(''/^\\s*[\\r\\n]/'', '''', rtrim(ps($name)));\n		$utf8len = preg_match_all("/./su", ltrim($value), $utf8ar);\n		$hlabel = htmlspecialchars($label);\n\n		if (strlen(ltrim($value)))\n		{\n			if (!$utf8len)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_utf8'', $hlabel);\n				$isError = "errorElement";\n			}\n\n			elseif ($min and $utf8len < $min)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''min_warning'', $hlabel, $min);\n				$isError = "errorElement";\n			}\n\n			elseif ($max and $utf8len > $max)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''max_warning'', $hlabel, $max);\n				$isError = "errorElement";\n				#$value = join('''',array_slice($utf8ar[0],0,$max));\n			}\n\n			else\n			{\n				zem_contact_store($name, $label, $value);\n			}\n		}\n\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', $hlabel);\n			$isError = "errorElement";\n		}\n	}\n\n	else\n	{\n		$value = $default;\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<label for="''.$name.''" class="zemTextarea ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		''<textarea id="''.$name.''" class="zemTextarea ''.$zemRequired.$isError.''" name="''.$name.''" cols="''.$cols.''" rows="''.$rows.''">''.htmlspecialchars($value).''</textarea>'';\n}\n\nfunction zem_contact_email($atts)\n{\n	global $zem_contact_error, $zem_contact_submit, $zem_contact_from, $zem_contact_recipient;\n\n	extract(zem_contact_lAtts(array(\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''email''),\n		''max''		=> 100,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''break''		=> br,\n		''size''		=> '''',\n		''send_article''	=> 0\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	$email = $zem_contact_submit ? trim(ps($name)) : $default;\n\n	if ($zem_contact_submit and strlen($email))\n	{\n		if (!is_valid_email($email))\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''invalid_email'', htmlspecialchars($email));\n			$isError = "errorElement";\n		}\n\n		else\n		{\n			preg_match("/@(.+)$/", $email, $match);\n			$domain = $match[1];\n\n			if (is_callable(''checkdnsrr'') and checkdnsrr(''textpattern.com.'',''A'') and !checkdnsrr($domain.''.'',''MX'') and !checkdnsrr($domain.''.'',''A''))\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_host'', htmlspecialchars($domain));\n				$isError = "errorElement";\n			}\n\n			else\n			{\n				if ($send_article) {\n					$zem_contact_recipient = $email;\n				}\n				else {\n					$zem_contact_from = $email;\n				}\n			}\n		}\n	}\n\n	return zem_contact_text(array(\n		''default''	=> $email,\n		''isError''	=> $isError,\n		''label''		=> $label,\n		''max''		=> $max,\n		''min''		=> $min,\n		''name''		=> $name,\n		''required''	=> $required,\n		''break''		=> $break,\n		''size''		=> $size\n	));\n}\n\nfunction zem_contact_select($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''name''		=> '''',\n		''break''		=> '' '',\n		''delimiter''	=> '','',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''option''),\n		''list''		=> zem_contact_gTxt(''general_inquiry''),\n		''required''	=> 1,\n		''selected''	=> ''''\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	$list = array_map(''trim'', split($delimiter, preg_replace(''/[\\r\\n\\t\\s]+/'', '' '',$list)));\n\n	if ($zem_contact_submit)\n	{\n		$value = trim(ps($name));\n\n		if (strlen($value))\n		{\n			if (in_array($value, $list))\n			{\n				zem_contact_store($name, $label, $value);\n			}\n\n			else\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_value'', htmlspecialchars($label), htmlspecialchars($value));\n				$isError = "errorElement";\n			}\n		}\n\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', htmlspecialchars($label));\n			$isError = "errorElement";\n		}\n	}\n	else\n	{\n		$value = $selected;\n	}\n\n	$out = '''';\n\n	foreach ($list as $item)\n	{\n		$out .= n.t.''<option''.($item == $value ? '' selected="selected">'' : ''>'').(strlen($item) ? htmlspecialchars($item) : '' '').''</option>'';\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<label for="''.$name.''" class="zemSelect ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		n.''<select id="''.$name.''" name="''.$name.''" class="zemSelect ''.$zemRequired.$isError.''">''.\n			$out.\n		n.''</select>'';\n}\n\nfunction zem_contact_checkbox($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> '' '',\n		''checked''	=> 0,\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''checkbox''),\n		''name''		=> '''',\n		''required''	=> 1\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = (bool) ps($name);\n\n		if ($required and !$value)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', htmlspecialchars($label));\n			$isError = "errorElement";\n		}\n\n		else\n		{\n			zem_contact_store($name, $label, $value ? gTxt(''yes'') : gTxt(''no''));\n		}\n	}\n\n	else {\n		$value = $checked;\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<input type="checkbox" id="''.$name.''" class="zemCheckbox ''.$zemRequired.$isError.''" name="''.$name.''"''.\n		($value ? '' checked="checked"'' : '''').'' />''.$break.\n		''<label for="''.$name.''" class="zemCheckbox ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>'';\n}\n\nfunction zem_contact_serverinfo($atts)\n{\n	global $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''label''		=> '''',\n		''name''		=> ''''\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if (strlen($name) and $zem_contact_submit)\n	{\n		if (!$label) $label = $name;\n		zem_contact_store($name, $label, serverSet($name));\n	}\n}\n\nfunction zem_contact_secret($atts, $thing = '''')\n{\n	global $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''name''	=> '''',\n		''label''	=> zem_contact_gTxt(''secret''),\n		''value''	=> ''''\n	), $atts));\n\n	$name = zem_contact_label2name($name ? $name : $label);\n\n	if ($zem_contact_submit)\n	{\n		if ($thing) $value = trim(parse($thing));\n		zem_contact_store($name, $label, $value);\n	}\n\n	return '''';\n}\n\nfunction zem_contact_radio($atts)\n{\n	global $zem_contact_error, $zem_contact_submit, $zem_contact_values;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> '' '',\n		''checked''	=> 0,\n		''group''		=> '''',\n		''label''		=> zem_contact_gTxt(''option''),\n		''name''		=> ''''\n	), $atts));\n\n	static $cur_name = '''';\n	static $cur_group = '''';\n\n	if (!$name and !$group and !$cur_name and !$cur_group) {\n		$cur_group = zem_contact_gTxt(''radio'');\n		$cur_name = $cur_group;\n	}\n	if ($group and !$name and $group != $cur_group) $name = $group;\n\n	if ($name) $cur_name = $name;\n	else $name = $cur_name;\n\n	if ($group) $cur_group = $group;\n	else $group = $cur_group;\n\n	$id   = ''q''.md5($name.''=>''.$label);\n	$name = zem_contact_label2name($name);\n\n	if ($zem_contact_submit)\n	{\n		$is_checked = (ps($name) == $id);\n\n		if ($is_checked or $checked and !isset($zem_contact_values[$name]))\n		{\n			zem_contact_store($name, $group, $label);\n		}\n	}\n\n	else\n	{\n		$is_checked = $checked;\n	}\n\n	return ''<input value="''.$id.''" type="radio" id="''.$id.''" class="zemRadio ''.$name.''" name="''.$name.''"''.\n		( $is_checked ? '' checked="checked" />'' : '' />'').$break.\n		''<label for="''.$id.''" class="zemRadio ''.$name.''">''.htmlspecialchars($label).''</label>'';\n}\n\nfunction zem_contact_send_article($atts)\n{\n	if (!isset($_REQUEST[''zem_contact_send_article''])) {\n		$linktext = (empty($atts[''linktext''])) ? zem_contact_gTxt(''send_article'') : $atts[''linktext''];\n		$join = (empty($_SERVER[''QUERY_STRING''])) ? ''?'' : ''&'';\n		$href = $_SERVER[''REQUEST_URI''].$join.''zem_contact_send_article=yes'';\n		return ''<a href="''.htmlspecialchars($href).''">''.htmlspecialchars($linktext).''</a>'';\n	}\n	return;\n}\n\nfunction zem_contact_submit($atts, $thing)\n{\n	extract(zem_contact_lAtts(array(\n		''button''	=> 0,\n		''label''		=> zem_contact_gTxt(''send'')\n	), $atts));\n\n	$label = htmlspecialchars($label);\n\n	if ($button or strlen($thing))\n	{\n		return ''<button type="submit" class="zemSubmit" name="zem_contact_submit" value="''.$label.''">''.($thing ? trim(parse($thing)) : $label).''</button>'';\n	}\n	else\n	{\n		return ''<input type="submit" class="zemSubmit" name="zem_contact_submit" value="''.$label.''" />'';\n	}\n}\n\nfunction zem_contact_lAtts($arr, $atts)\n{\n	foreach(array(''button'', ''copysender'', ''checked'', ''required'', ''send_article'', ''show_input'', ''show_error'') as $key)\n	{\n		if (isset($atts[$key]))\n		{\n			$atts[$key] = ($atts[$key] === ''yes'' or intval($atts[$key])) ? 1 : 0;\n		}\n	}\n	if (isset($atts[''break'']) and $atts[''break''] == ''br'') $atts[''break''] = ''<br />'';\n	return lAtts($arr, $atts);\n}\n\nclass zemcontact_evaluation\n{\n	var $status;\n\n	function zemcontact_evaluation() {\n		$this->status = 0;\n	}\n\n	function add_zemcontact_status($check) {\n		$this->status += $check;\n	}\n\n	function get_zemcontact_status() {\n		return $this->status;\n	}\n}\n\nfunction &get_zemcontact_evaluator()\n{\n	static $instance;\n\n	if(!isset($instance)) {\n		$instance = new zemcontact_evaluation();\n	}\n	return $instance;\n}\n\nfunction zem_contact_label2name($label)\n{\n	$label = trim($label);\n	if (strlen($label) == 0) return ''invalid'';\n	if (strlen($label) <= 32 and preg_match(''/^[a-zA-Z][A-Za-z0-9:_-]*$/'', $label)) return $label;\n	else return ''q''.md5($label);\n}\n\nfunction zem_contact_store($name, $label, $value)\n{\n	global $zem_contact_form, $zem_contact_labels, $zem_contact_values;\n	$zem_contact_form[$label] = $value;\n	$zem_contact_labels[$name] = $label;\n	$zem_contact_values[$name] = $value;\n}\n\nfunction zem_contact_mailheader($string, $type)\n{\n	global $prefs;\n	if (!strstr($string,''=?'') and !preg_match(''/[\\x00-\\x1F\\x7F-\\xFF]/'', $string)) {\n		if ("phrase" == $type) {\n			if (preg_match(''/[][()<>@,;:".\\x5C]/'', $string)) {\n				$string = ''"''. strtr($string, array("\\\\" => "\\\\\\\\", ''"'' => ''\\"'')) . ''"'';\n			}\n		}\n		elseif ("text" != $type) {\n			trigger_error(''Unknown encode_mailheader type'', E_USER_WARNING);\n		}\n		return $string;\n	}\n	if ($prefs[''override_emailcharset'']) {\n		$start = ''=?ISO-8859-1?B?'';\n		$pcre  = ''/.{1,42}/s'';\n	}\n	else {\n		$start = ''=?UTF-8?B?'';\n		$pcre  = ''/.{1,45}(?=[\\x00-\\x7F\\xC0-\\xFF]|$)/s'';\n	}\n	$end = ''?='';\n	$sep = is_windows() ? "\\r\\n" : "\\n";\n	preg_match_all($pcre, $string, $matches);\n	return $start . join($end.$sep.'' ''.$start, array_map(''base64_encode'',$matches[0])) . $end;\n}\n', '\nfunction zem_contact($atts, $thing = '''')\n{\n	global $sitename, $prefs, $production_status, $zem_contact_from,\n		$zem_contact_recipient, $zem_contact_error, $zem_contact_submit,\n		$zem_contact_form, $zem_contact_labels, $zem_contact_values;\n\n	extract(zem_contact_lAtts(array(\n		''copysender''	=> 0,\n		''form''		=> '''',\n		''from''		=> '''',\n		''from_form''	=> '''',\n		''label''		=> zem_contact_gTxt(''contact''),\n		''redirect''	=> '''',\n		''show_error''	=> 1,\n		''show_input''	=> 1,\n		''send_article''	=> 0,\n		''subject''	=> zem_contact_gTxt(''email_subject'', html_entity_decode($sitename,ENT_QUOTES)),\n		''subject_form''	=> '''',\n		''to''		=> '''',\n		''to_form''	=> '''',\n		''thanks''	=> graf(zem_contact_gTxt(''email_thanks'')),\n		''thanks_form''	=> ''''\n	), $atts));\n\n	unset($atts[''show_error''], $atts[''show_input'']);\n	$zem_contact_form_id = md5(serialize($atts).preg_replace(''/[\\t\\s\\r\\n]/'','''',$thing));\n	$zem_contact_submit = (ps(''zem_contact_form_id'') == $zem_contact_form_id);\n\n	if (!is_callable(''mail''))\n	{\n		return ($production_status == ''live'') ?\n			zem_contact_gTxt(''mail_sorry'') :\n			gTxt(''warn_mail_unavailable'');\n	}\n\n	static $headers_sent = false;\n	if (!$headers_sent) {\n		header(''Last-Modified: ''.gmdate(''D, d M Y H:i:s'',time()-3600*24*7).'' GMT'');\n		header(''Expires: ''.gmdate(''D, d M Y H:i:s'',time()+600).'' GMT'');\n		header(''Cache-Control: no-cache, must-revalidate'');\n		$headers_sent = true;\n	}\n\n	$nonce   = mysql_real_escape_string(ps(''zem_contact_nonce''));\n	$renonce = false;\n\n	if ($zem_contact_submit)\n	{\n		safe_delete(''txp_discuss_nonce'', ''issue_time < date_sub(now(), interval 10 minute)'');\n		if ($rs = safe_row(''used'', ''txp_discuss_nonce'', "nonce = ''$nonce''"))\n		{\n			if ($rs[''used''])\n			{\n				unset($zem_contact_error);\n				$zem_contact_error[] = zem_contact_gTxt(''form_used'');\n				$renonce = true;\n				$_POST = array();\n				$_POST[''zem_contact_submit''] = TRUE;\n				$_POST[''zem_contact_form_id''] = $zem_contact_form_id;\n				$_POST[''zem_contact_nonce''] = $nonce;\n			}\n		}\n		else\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''form_expired'');\n			$renonce = true;\n		}\n	}\n\n	if ($zem_contact_submit and $nonce and !$renonce)\n	{\n		$zem_contact_nonce = $nonce;\n	}\n\n	elseif (!$show_error or $show_input)\n	{\n		$zem_contact_nonce = md5(uniqid(rand(), true));\n		safe_insert(''txp_discuss_nonce'', "issue_time = now(), nonce = ''$zem_contact_nonce''");\n	}\n\n	$form = ($form) ? fetch_form($form) : $thing;\n\n	if (empty($form))\n	{\n		$form = ''\n<txp:zem_contact_text label="''.zem_contact_gTxt(''name'').''" /><br />\n<txp:zem_contact_email /><br />''.\n($send_article ? ''<txp:zem_contact_email send_article="1" label="''.zem_contact_gTxt(''recipient'').''" /><br />'' : '''').\n''<txp:zem_contact_textarea /><br />\n<txp:zem_contact_submit />\n'';\n	}\n\n	$form = parse($form);\n\n	if ($to_form)\n	{\n		$to = parse(fetch_form($to_form));\n	}\n\n	if (!$to and !$send_article)\n	{\n		return zem_contact_gTxt(''to_missing'');\n	}\n\n	$out = '''';\n\n	if (!$zem_contact_submit) {\n	  # don''t show errors or send mail\n	}\n\n	elseif (!empty($zem_contact_error))\n	{\n		if ($show_error or !$show_input)\n		{\n			$out .= n.''<ul class="zemError">'';\n\n			foreach (array_unique($zem_contact_error) as $error)\n			{\n				$out .= n.t.''<li>''.$error.''</li>'';\n			}\n\n			$out .= n.''</ul>'';\n\n			if (!$show_input) return $out;\n		}\n	}\n\n	elseif ($show_input and is_array($zem_contact_form))\n	{\n		/// load and check spam plugins/\n		callback_event(''zemcontact.submit'');\n		$evaluation =& get_zemcontact_evaluator();\n		$clean = $evaluation->get_zemcontact_status();\n		if ($clean != 0) {\n			return zem_contact_gTxt(''spam'');\n		}\n\n		if ($from_form)\n		{\n			$from = parse(fetch_form($from_form));\n		}\n\n		if ($subject_form)\n		{\n			$subject = parse(fetch_form($subject_form));\n		}\n\n		$sep = !is_windows() ? "\\n" : "\\r\\n";\n\n		$msg = array();\n\n		foreach ($zem_contact_labels as $name => $label)\n		{\n			if (!trim($zem_contact_values[$name])) continue;\n			$msg[] = $label.'': ''.$zem_contact_values[$name];\n		}\n\n		if ($send_article)\n		{\n			global $thisarticle;\n			$subject = str_replace(''&#38;'', ''&'', $thisarticle[''title'']);\n			$msg[] = permlinkurl($thisarticle);\n			$msg[] = $subject;\n			$s_ar = array(''&#8216;'', ''&#8217;'', ''&#8220;'', ''&#8221;'', ''&#8217;'', ''&#8242;'', ''&#8243;'', ''&#8230;'', ''&#8211;'', ''&#8212;'', ''&#215;'', ''&#8482;'', ''&#174;'', ''&#169;'', ''&lt;'', ''&gt;'', ''&quot;'', ''&amp;'', ''&#38;'', "\\t", ''<p'');\n			if ($prefs[''override_emailcharset''] and is_callable(''utf8_decode'')) {\n				$r_ar = array("''", "''", ''"'', ''"'', "''", "''", ''"'', ''...'', ''-'', ''--'', ''x'', ''[tm]'', ''(r)'', ''(c)'', ''<'', ''>'', ''"'', ''&'', ''&'', '' '', "\\n<p");\n			}\n			else\n			{\n				$r_ar = array(''‘'', ''’'', ''“'', ''”'', ''’'', ''′'', ''″'', ''…'', ''–'', ''—'', ''×'', ''™'', ''®'', ''©'', ''<'', ''>'', ''"'', ''&'', ''&'', '' '', "\\n<p");\n			}\n			$msg[] = trim(strip_tags(str_replace($s_ar,$r_ar,(trim(strip_tags($thisarticle[''excerpt''])) ? $thisarticle[''excerpt''] : $thisarticle[''body'']))));\n			if (empty($zem_contact_recipient))\n			{\n				return zem_contact_gTxt(''field_missing'', zem_contact_gTxt(''recipient''));\n			}\n			else\n			{\n				$to = $zem_contact_recipient;\n			}\n		}\n\n		$msg = join("\\n\\n", $msg);\n		$msg = str_replace("\\r\\n", "\\n", $msg);\n		$msg = str_replace("\\r", "\\n", $msg);\n		$msg = str_replace("\\n", $sep, $msg);\n\n		if ($from)\n		{\n			$reply = $zem_contact_from;\n		}\n\n		else\n		{\n			$from = $zem_contact_from;\n			$reply = '''';\n		}\n\n		$from    = zem_contact_strip($from);\n		$to      = zem_contact_strip($to);\n		$subject = zem_contact_strip($subject);\n		$reply   = zem_contact_strip($reply);\n		$msg     = zem_contact_strip($msg, FALSE);\n\n		if ($prefs[''override_emailcharset''] and is_callable(''utf8_decode''))\n		{\n			$charset = ''ISO-8859-1'';\n			$subject = utf8_decode($subject);\n			$msg     = utf8_decode($msg);\n		}\n\n		else\n		{\n			$charset = ''UTF-8'';\n		}\n\n		$subject = zem_contact_mailheader($subject, ''text'');\n\n		$headers = ''From: ''.$from.\n			($reply ? ($sep.''Reply-To: ''.$reply) : '''').\n			$sep.''X-Mailer: Textpattern (zem_contact_reborn)''.\n			$sep.''X-Originating-IP: ''.zem_contact_strip((!empty($_SERVER[''HTTP_X_FORWARDED_FOR'']) ? $_SERVER[''HTTP_X_FORWARDED_FOR''].'' via '' : '''').$_SERVER[''REMOTE_ADDR'']).\n			$sep.''Content-Transfer-Encoding: 8bit''.\n			$sep.''Content-Type: text/plain; charset="''.$charset.''"'';\n\n		safe_update(''txp_discuss_nonce'', "used = ''1'', issue_time = now()", "nonce = ''$nonce''");\n\n		if (mail($to, $subject, $msg, $headers))\n		{\n			$_POST = array();\n\n			if ($copysender and $zem_contact_from)\n			{\n				mail(zem_contact_strip($zem_contact_from), $subject, $msg, $headers);\n			}\n\n			if ($redirect)\n			{\n				while (@ob_end_clean());\n				$uri = hu.ltrim($redirect,''/'');\n				if (empty($_SERVER[''FCGI_ROLE'']) and empty($_ENV[''FCGI_ROLE'']))\n				{\n					txp_status_header(''303 See Other'');\n					header(''Location: ''.$uri);\n					header(''Connection: close'');\n					header(''Content-Length: 0'');\n				}\n				else\n				{\n					$uri = htmlspecialchars($uri);\n					$refresh = zem_contact_gTxt(''refresh'');\n					echo <<<END\n<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">\n<html xmlns="http://www.w3.org/1999/xhtml" xml:lang="en" lang="en">\n<head>\n	<title>$sitename</title>\n	<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />\n	<meta http-equiv="refresh" content="0;url=$uri" />\n</head>\n<body>\n<a href="$uri">$refresh</a>\n</body>\n</html>\nEND;\n				}\n				exit;\n			}\n\n			else\n			{\n				return ''<div class="zemThanks" id="zcr''.$zem_contact_form_id.''">'' .\n					($thanks_form ? fetch_form($thanks_form) : $thanks) .\n					''</div>'';\n			}\n		}\n\n		else\n		{\n			$out .= graf(zem_contact_gTxt(''mail_sorry''));\n		}\n	}\n\n	if ($show_input and !$send_article or gps(''zem_contact_send_article''))\n	{\n		return ''<form method="post"''.((!$show_error and $zem_contact_error) ? '''' : '' id="zcr''.$zem_contact_form_id.''"'').'' class="zemContactForm" action="''.htmlspecialchars(serverSet(''REQUEST_URI'')).''#zcr''.$zem_contact_form_id.''">''.\n			( $label ? n.''<fieldset>'' : n.''<div>'' ).\n			( $label ? n.''<legend>''.htmlspecialchars($label).''</legend>'' : '''' ).\n			$out.\n			n.''<input type="hidden" name="zem_contact_nonce" value="''.$zem_contact_nonce.''" />''.\n			n.''<input type="hidden" name="zem_contact_form_id" value="''.$zem_contact_form_id.''" />''.\n			$form.\n			callback_event(''zemcontact.form'').\n			( $label ? (n.''</fieldset>'') : (n.''</div>'') ).\n			n.''</form>'';\n	}\n\n	return '''';\n}\n\nfunction zem_contact_strip($str, $header = TRUE) {\n	if ($header) $str = strip_rn($str);\n	return preg_replace(''/[\\x00]/'', '' '', $str);\n}\n\nfunction zem_contact_text($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> br,\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''text''),\n		''max''		=> 100,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''size''		=> ''''\n	), $atts));\n\n	$min = intval($min);\n	$max = intval($max);\n	$size = intval($size);\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = trim(ps($name));\n		$utf8len = preg_match_all("/./su", $value, $utf8ar);\n		$hlabel = htmlspecialchars($label);\n\n		if (strlen($value))\n		{\n			if (!$utf8len)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_utf8'', $hlabel);\n				$isError = "errorElement";\n			}\n\n			elseif ($min and $utf8len < $min)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''min_warning'', $hlabel, $min);\n				$isError = "errorElement";\n			}\n\n			elseif ($max and $utf8len > $max)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''max_warning'', $hlabel, $max);\n				$isError = "errorElement";\n				#$value = join('''',array_slice($ar[0],0,$max));\n			}\n\n			else\n			{\n				zem_contact_store($name, $label, $value);\n			}\n		}\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', $hlabel);\n			$isError = "errorElement";\n		}\n	}\n\n	else\n	{\n		$value = $default;\n	}\n\n	$size = ($size) ? '' size="''.$size.''"'' : '''';\n	$maxlength = ($max) ? '' maxlength="''.$max.''"'' : '''';\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n        return ''<label for="''.$name.''" class="zemText ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		''<input type="text" id="''.$name.''" class="zemText ''.$zemRequired.$isError.''" name="''.$name.''" value="''.htmlspecialchars($value).''"''.$size.$maxlength.'' />'';\n}\n\nfunction zem_contact_textarea($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> br,\n		''cols''		=> 58,\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''message''),\n		''max''		=> 10000,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''rows''		=> 8\n	), $atts));\n\n	$min = intval($min);\n	$max = intval($max);\n	$cols = intval($cols);\n	$rows = intval($rows);\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = preg_replace(''/^\\s*[\\r\\n]/'', '''', rtrim(ps($name)));\n		$utf8len = preg_match_all("/./su", ltrim($value), $utf8ar);\n		$hlabel = htmlspecialchars($label);\n\n		if (strlen(ltrim($value)))\n		{\n			if (!$utf8len)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_utf8'', $hlabel);\n				$isError = "errorElement";\n			}\n\n			elseif ($min and $utf8len < $min)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''min_warning'', $hlabel, $min);\n				$isError = "errorElement";\n			}\n\n			elseif ($max and $utf8len > $max)\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''max_warning'', $hlabel, $max);\n				$isError = "errorElement";\n				#$value = join('''',array_slice($utf8ar[0],0,$max));\n			}\n\n			else\n			{\n				zem_contact_store($name, $label, $value);\n			}\n		}\n\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', $hlabel);\n			$isError = "errorElement";\n		}\n	}\n\n	else\n	{\n		$value = $default;\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<label for="''.$name.''" class="zemTextarea ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		''<textarea id="''.$name.''" class="zemTextarea ''.$zemRequired.$isError.''" name="''.$name.''" cols="''.$cols.''" rows="''.$rows.''">''.htmlspecialchars($value).''</textarea>'';\n}\n\nfunction zem_contact_email($atts)\n{\n	global $zem_contact_error, $zem_contact_submit, $zem_contact_from, $zem_contact_recipient;\n\n	extract(zem_contact_lAtts(array(\n		''default''	=> '''',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''email''),\n		''max''		=> 100,\n		''min''		=> 0,\n		''name''		=> '''',\n		''required''	=> 1,\n		''break''		=> br,\n		''size''		=> '''',\n		''send_article''	=> 0\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	$email = $zem_contact_submit ? trim(ps($name)) : $default;\n\n	if ($zem_contact_submit and strlen($email))\n	{\n		if (!is_valid_email($email))\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''invalid_email'', htmlspecialchars($email));\n			$isError = "errorElement";\n		}\n\n		else\n		{\n			preg_match("/@(.+)$/", $email, $match);\n			$domain = $match[1];\n\n			if (is_callable(''checkdnsrr'') and checkdnsrr(''textpattern.com.'',''A'') and !checkdnsrr($domain.''.'',''MX'') and !checkdnsrr($domain.''.'',''A''))\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_host'', htmlspecialchars($domain));\n				$isError = "errorElement";\n			}\n\n			else\n			{\n				if ($send_article) {\n					$zem_contact_recipient = $email;\n				}\n				else {\n					$zem_contact_from = $email;\n				}\n			}\n		}\n	}\n\n	return zem_contact_text(array(\n		''default''	=> $email,\n		''isError''	=> $isError,\n		''label''		=> $label,\n		''max''		=> $max,\n		''min''		=> $min,\n		''name''		=> $name,\n		''required''	=> $required,\n		''break''		=> $break,\n		''size''		=> $size\n	));\n}\n\nfunction zem_contact_select($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''name''		=> '''',\n		''break''		=> '' '',\n		''delimiter''	=> '','',\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''option''),\n		''list''		=> zem_contact_gTxt(''general_inquiry''),\n		''required''	=> 1,\n		''selected''	=> ''''\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	$list = array_map(''trim'', split($delimiter, preg_replace(''/[\\r\\n\\t\\s]+/'', '' '',$list)));\n\n	if ($zem_contact_submit)\n	{\n		$value = trim(ps($name));\n\n		if (strlen($value))\n		{\n			if (in_array($value, $list))\n			{\n				zem_contact_store($name, $label, $value);\n			}\n\n			else\n			{\n				$zem_contact_error[] = zem_contact_gTxt(''invalid_value'', htmlspecialchars($label), htmlspecialchars($value));\n				$isError = "errorElement";\n			}\n		}\n\n		elseif ($required)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', htmlspecialchars($label));\n			$isError = "errorElement";\n		}\n	}\n	else\n	{\n		$value = $selected;\n	}\n\n	$out = '''';\n\n	foreach ($list as $item)\n	{\n		$out .= n.t.''<option''.($item == $value ? '' selected="selected">'' : ''>'').(strlen($item) ? htmlspecialchars($item) : '' '').''</option>'';\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<label for="''.$name.''" class="zemSelect ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>''.$break.\n		n.''<select id="''.$name.''" name="''.$name.''" class="zemSelect ''.$zemRequired.$isError.''">''.\n			$out.\n		n.''</select>'';\n}\n\nfunction zem_contact_checkbox($atts)\n{\n	global $zem_contact_error, $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> '' '',\n		''checked''	=> 0,\n		''isError''	=> '''',\n		''label''		=> zem_contact_gTxt(''checkbox''),\n		''name''		=> '''',\n		''required''	=> 1\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if ($zem_contact_submit)\n	{\n		$value = (bool) ps($name);\n\n		if ($required and !$value)\n		{\n			$zem_contact_error[] = zem_contact_gTxt(''field_missing'', htmlspecialchars($label));\n			$isError = "errorElement";\n		}\n\n		else\n		{\n			zem_contact_store($name, $label, $value ? gTxt(''yes'') : gTxt(''no''));\n		}\n	}\n\n	else {\n		$value = $checked;\n	}\n\n	$zemRequired = $required ? ''zemRequired'' : '''';\n\n	return ''<input type="checkbox" id="''.$name.''" class="zemCheckbox ''.$zemRequired.$isError.''" name="''.$name.''"''.\n		($value ? '' checked="checked"'' : '''').'' />''.$break.\n		''<label for="''.$name.''" class="zemCheckbox ''.$zemRequired.$isError.'' ''.$name.''">''.htmlspecialchars($label).''</label>'';\n}\n\nfunction zem_contact_serverinfo($atts)\n{\n	global $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''label''		=> '''',\n		''name''		=> ''''\n	), $atts));\n\n	if (empty($name)) $name = zem_contact_label2name($label);\n\n	if (strlen($name) and $zem_contact_submit)\n	{\n		if (!$label) $label = $name;\n		zem_contact_store($name, $label, serverSet($name));\n	}\n}\n\nfunction zem_contact_secret($atts, $thing = '''')\n{\n	global $zem_contact_submit;\n\n	extract(zem_contact_lAtts(array(\n		''name''	=> '''',\n		''label''	=> zem_contact_gTxt(''secret''),\n		''value''	=> ''''\n	), $atts));\n\n	$name = zem_contact_label2name($name ? $name : $label);\n\n	if ($zem_contact_submit)\n	{\n		if ($thing) $value = trim(parse($thing));\n		zem_contact_store($name, $label, $value);\n	}\n\n	return '''';\n}\n\nfunction zem_contact_radio($atts)\n{\n	global $zem_contact_error, $zem_contact_submit, $zem_contact_values;\n\n	extract(zem_contact_lAtts(array(\n		''break''		=> '' '',\n		''checked''	=> 0,\n		''group''		=> '''',\n		''label''		=> zem_contact_gTxt(''option''),\n		''name''		=> ''''\n	), $atts));\n\n	static $cur_name = '''';\n	static $cur_group = '''';\n\n	if (!$name and !$group and !$cur_name and !$cur_group) {\n		$cur_group = zem_contact_gTxt(''radio'');\n		$cur_name = $cur_group;\n	}\n	if ($group and !$name and $group != $cur_group) $name = $group;\n\n	if ($name) $cur_name = $name;\n	else $name = $cur_name;\n\n	if ($group) $cur_group = $group;\n	else $group = $cur_group;\n\n	$id   = ''q''.md5($name.''=>''.$label);\n	$name = zem_contact_label2name($name);\n\n	if ($zem_contact_submit)\n	{\n		$is_checked = (ps($name) == $id);\n\n		if ($is_checked or $checked and !isset($zem_contact_values[$name]))\n		{\n			zem_contact_store($name, $group, $label);\n		}\n	}\n\n	else\n	{\n		$is_checked = $checked;\n	}\n\n	return ''<input value="''.$id.''" type="radio" id="''.$id.''" class="zemRadio ''.$name.''" name="''.$name.''"''.\n		( $is_checked ? '' checked="checked" />'' : '' />'').$break.\n		''<label for="''.$id.''" class="zemRadio ''.$name.''">''.htmlspecialchars($label).''</label>'';\n}\n\nfunction zem_contact_send_article($atts)\n{\n	if (!isset($_REQUEST[''zem_contact_send_article''])) {\n		$linktext = (empty($atts[''linktext''])) ? zem_contact_gTxt(''send_article'') : $atts[''linktext''];\n		$join = (empty($_SERVER[''QUERY_STRING''])) ? ''?'' : ''&'';\n		$href = $_SERVER[''REQUEST_URI''].$join.''zem_contact_send_article=yes'';\n		return ''<a href="''.htmlspecialchars($href).''">''.htmlspecialchars($linktext).''</a>'';\n	}\n	return;\n}\n\nfunction zem_contact_submit($atts, $thing)\n{\n	extract(zem_contact_lAtts(array(\n		''button''	=> 0,\n		''label''		=> zem_contact_gTxt(''send'')\n	), $atts));\n\n	$label = htmlspecialchars($label);\n\n	if ($button or strlen($thing))\n	{\n		return ''<button type="submit" class="zemSubmit" name="zem_contact_submit" value="''.$label.''">''.($thing ? trim(parse($thing)) : $label).''</button>'';\n	}\n	else\n	{\n		return ''<input type="submit" class="zemSubmit" name="zem_contact_submit" value="''.$label.''" />'';\n	}\n}\n\nfunction zem_contact_lAtts($arr, $atts)\n{\n	foreach(array(''button'', ''copysender'', ''checked'', ''required'', ''send_article'', ''show_input'', ''show_error'') as $key)\n	{\n		if (isset($atts[$key]))\n		{\n			$atts[$key] = ($atts[$key] === ''yes'' or intval($atts[$key])) ? 1 : 0;\n		}\n	}\n	if (isset($atts[''break'']) and $atts[''break''] == ''br'') $atts[''break''] = ''<br />'';\n	return lAtts($arr, $atts);\n}\n\nclass zemcontact_evaluation\n{\n	var $status;\n\n	function zemcontact_evaluation() {\n		$this->status = 0;\n	}\n\n	function add_zemcontact_status($check) {\n		$this->status += $check;\n	}\n\n	function get_zemcontact_status() {\n		return $this->status;\n	}\n}\n\nfunction &get_zemcontact_evaluator()\n{\n	static $instance;\n\n	if(!isset($instance)) {\n		$instance = new zemcontact_evaluation();\n	}\n	return $instance;\n}\n\nfunction zem_contact_label2name($label)\n{\n	$label = trim($label);\n	if (strlen($label) == 0) return ''invalid'';\n	if (strlen($label) <= 32 and preg_match(''/^[a-zA-Z][A-Za-z0-9:_-]*$/'', $label)) return $label;\n	else return ''q''.md5($label);\n}\n\nfunction zem_contact_store($name, $label, $value)\n{\n	global $zem_contact_form, $zem_contact_labels, $zem_contact_values;\n	$zem_contact_form[$label] = $value;\n	$zem_contact_labels[$name] = $label;\n	$zem_contact_values[$name] = $value;\n}\n\nfunction zem_contact_mailheader($string, $type)\n{\n	global $prefs;\n	if (!strstr($string,''=?'') and !preg_match(''/[\\x00-\\x1F\\x7F-\\xFF]/'', $string)) {\n		if ("phrase" == $type) {\n			if (preg_match(''/[][()<>@,;:".\\x5C]/'', $string)) {\n				$string = ''"''. strtr($string, array("\\\\" => "\\\\\\\\", ''"'' => ''\\"'')) . ''"'';\n			}\n		}\n		elseif ("text" != $type) {\n			trigger_error(''Unknown encode_mailheader type'', E_USER_WARNING);\n		}\n		return $string;\n	}\n	if ($prefs[''override_emailcharset'']) {\n		$start = ''=?ISO-8859-1?B?'';\n		$pcre  = ''/.{1,42}/s'';\n	}\n	else {\n		$start = ''=?UTF-8?B?'';\n		$pcre  = ''/.{1,45}(?=[\\x00-\\x7F\\xC0-\\xFF]|$)/s'';\n	}\n	$end = ''?='';\n	$sep = is_windows() ? "\\r\\n" : "\\n";\n	preg_match_all($pcre, $string, $matches);\n	return $start . join($end.$sep.'' ''.$start, array_map(''base64_encode'',$matches[0])) . $end;\n}\n', 'cb772f305727a9e9e5fd7d1616f56996', 0, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('zem_contact_lang', 1, 'TXP Community', 'http://forum.textpattern.com/viewtopic.php?id=12956', '4.0.3.03', 'Language plug-in for Zem Contact Reborn', '\n<div style="text-align:center;font-weight:bold;font-size:24px;text-decoration:underline;">Zem Contact Lang</div>\n	<p>This is a separate language plug-in for use with Zem Contact Reborn. Both plug-ins need to be installed and activated in order to work properly.</p>\n	<p>Separating the language in this way will enable non-english users to update the main plug-in without affecting their &#8220;localisation&#8221;.</p>\n<div id="local" style="text-align:center;font-weight:bold;font-size:24px;text-decoration:underline;">Localisation</div>\n	<p>Throughout the <code>zem_contact_reborn</code> plug-in, use has been made of a separate <code>gTxt</code> function which you can see in this plug-in&#8217;s code by clicking on the &#8220;Edit&#8221; button.</p>\n	<p>If you are using the plug-in for a non-english site you can make use of this to localise text outputs for your preferred language.</p>\n	<p>You should only edit text that appears after the <code>=&gt;</code> sign.</p>\n	<p>If you have a dual-language site and the languages use separate &#8220;sections&#8221;, you can use the &#60;txp:if&#95;section&#62; tag to enable different translations. An example of this usage is shown in the <strong><a href="http://forum.textpattern.com/viewtopic.php?id=13416">forum thread</a></strong>. Our thanks to Els (doggiez) for this example.</p>\n <br />\n\n\n\n ', '\nfunction zem_contact_gTxt($what, $var1 = '''', $var2 = '''')\n{\n	$lang = array(\n		''checkbox''		=> ''Checkbox'',\n		''contact''		=> ''Contact'',\n		''email''			=> ''Email'',\n		''email_subject''		=> "$var1 > Inquiry",\n		''email_thanks''		=> ''Thank you, your message has been sent.'',\n		''field_missing''		=> "Required field, &#8220;<strong>$var1</strong>&#8221;, is missing.",\n		''form_expired''		=> ''The form has expired, please try again.'',\n		''general_inquiry''	=> ''General inquiry'',\n		''invalid_email''		=> "&#8220;<strong>$var1</strong>&#8221; is not a valid email address.",\n		''invalid_host''		=> "&#8220;<strong>$var1</strong>&#8221; is not a valid email host.",\n		''invalid_value''		=> "Invalid value for &#8220;<strong>$var1</strong>&#8221;, &#8220;<strong>$var2</strong>&#8221; is not one of the available options.",\n		''mail_sorry''		=> ''Sorry, unable to send email.'',\n		''message''		=> ''Message'',\n		''min_warning''		=> "&#8220;<strong>$var1</strong>&#8221; must contain at least $var2 characters.",\n		''option''		=> ''Option'',\n		''radio''			=> ''Radio'',\n		''receiver''		=> ''Receiver'',\n		''send''			=> ''Send'',\n		''spam''			=> ''We do not accept spam thankyou!'',\n		''text''			=> ''Text'',\n		''to''			=> ''No &#8220;<strong>to</strong>&#8221; email address specified.'',\n		''to_missing''		=> ''&#8220;<strong>To</strong>&#8221; address is missing.''\n	);\n\n	return $lang[$what];\n}\n', '\nfunction zem_contact_gTxt($what, $var1 = '''', $var2 = '''')\n{\n	$lang = array(\n		''checkbox''		=> ''Checkbox'',\n		''contact''		=> ''Contact'',\n		''email''			=> ''Email'',\n		''email_subject''		=> "$var1 > Inquiry",\n		''email_thanks''		=> ''Thank you, your message has been sent.'',\n		''field_missing''		=> "Required field, &#8220;<strong>$var1</strong>&#8221;, is missing.",\n		''form_expired''		=> ''The form has expired, please try again.'',\n		''general_inquiry''	=> ''General inquiry'',\n		''invalid_email''		=> "&#8220;<strong>$var1</strong>&#8221; is not a valid email address.",\n		''invalid_host''		=> "&#8220;<strong>$var1</strong>&#8221; is not a valid email host.",\n		''invalid_value''		=> "Invalid value for &#8220;<strong>$var1</strong>&#8221;, &#8220;<strong>$var2</strong>&#8221; is not one of the available options.",\n		''mail_sorry''		=> ''Sorry, unable to send email.'',\n		''message''		=> ''Message'',\n		''min_warning''		=> "&#8220;<strong>$var1</strong>&#8221; must contain at least $var2 characters.",\n		''option''		=> ''Option'',\n		''radio''			=> ''Radio'',\n		''receiver''		=> ''Receiver'',\n		''send''			=> ''Send'',\n		''spam''			=> ''We do not accept spam thankyou!'',\n		''text''			=> ''Text'',\n		''to''			=> ''No &#8220;<strong>to</strong>&#8221; email address specified.'',\n		''to_missing''		=> ''&#8220;<strong>To</strong>&#8221; address is missing.''\n	);\n\n	return $lang[$what];\n}\n', '97cb558263fcf11a6b2e65b62ba7e19c', 0, 5, 0),
('rss_admin_show_adv_opts', 1, 'Rob Sable', 'http://www.wilshireone.com/', '0.3', 'Automatically expand article advanced options.', '	<p>\nh1. Show Advanced Options</p>\n\n	<p>This plugin will automatically expand the <strong>Advanced Options</strong> menu and the <strong>More</strong> menu on the Content -> Write tab.</p>', '\nif (@txpinterface == ''admin'')\n  register_callback("rss_show_adv_opts", "article");\n\nfunction rss_show_adv_opts($event, $step) {\necho <<< EOF\n<script language="JavaScript" type="text/javascript"><!--\naddEvent(window,''load'',function() {toggleDisplay(''advanced'');});\naddEvent(window,''load'',function() {toggleDisplay(''more'');});\n--></script>\nEOF;\n}', '\nif (@txpinterface == ''admin'')\n  register_callback("rss_show_adv_opts", "article");\n\nfunction rss_show_adv_opts($event, $step) {\necho <<< EOF\n<script language="JavaScript" type="text/javascript"><!--\naddEvent(window,''load'',function() {toggleDisplay(''advanced'');});\naddEvent(window,''load'',function() {toggleDisplay(''more'');});\n--></script>\nEOF;\n}', '8392204919f637cefbb86915188ba981', 1, 5, 0),
('glx_hl_current', 1, 'Johan Nilsson', 'http://johan.galaxen.net', '0.3', 'Assigns a class to the current selected article or section', '\n\n<h2>Attributes</h2>\n\n<dl>\n	<dt><code>class</code></dt>\n	<dd>The value youd like assigned to the class property of the current selected article or section</dd>\n	<dd>Default: <code>current</code></dd>\n</dl>\n\n<dl>\n	<dt><code>section</code> Only for glx_hl_current_section</dt>\n	<dd>The section you want to compare to. It takes a commaseparated list if you want to compare to several sections</dd>\n</dl>\n\n<h3>glx_current_article</h3>\n<p><code>&lt;txp:glx_if_current_article /&gt;</code></p>\n<p><code>&lt;txp:glx_if_current_article class="bold" /&gt;</code></p>\n\n<p>Just use it like this in a form</p>\n<p>\n<pre>\n<code>\n&lt;li<span style="color:#FFA81E;">&lt;txp:glx_if_current_article /&gt;</span>&gt;\n&lt;txp:glx_permlink /&gt;\n&lt;/li&gt;\n</code>\n</pre>\n</p>\n\n<h3>glx_current_section</h3>\n<p><code>&lt;txp:glx_if_current_section section="contact" /&gt;</code></p>\n<p><code>&lt;txp:glx_if_current_section section="default, article" /&gt;</code></p>\n<p><code>&lt;txp:glx_if_current_article section="contact" class="bold" /&gt;</code></p>\n\n<p>Just use it like this in a form or in a page template</p>\n<p>\n\n<pre>\n<code>\n&lt;ul id=&quot;menu&quot;&gt;\n   &lt;li<span style="color:#FFA81E;">&lt;txp:glx_hl_current_section section=&quot;default, article&quot; /&gt;</span>&gt;&lt;a href=&quot;/&quot;&gt;Start&lt;/a&gt;&lt;/li&gt;\n   &lt;li<span style="color:#FFA81E;">&lt;txp:glx_hl_current_section section=&quot;archive&quot; /&gt;</span>&gt;&lt;a href=&quot;/archive&quot;&gt;My archive&lt;/a&gt;&lt;/li&gt;\n   &lt;li<span style="color:#FFA81E;">&lt;txp:glx_hl_current_section section=&quot;contact&quot; /&gt;</span>&gt;&lt;a href=&quot;/contact&quot;&gt;Contact&lt;/a&gt;&lt;/li&gt;\n&lt;/ul&gt;\n</code>\n</pre>\n</p>\n', '\n\nfunction glx_hl_current_article($atts) \n{\n	global $thisarticle;\n	global $pretext;\n\n	if (is_array($atts)) extract($atts);\n\n	$class = (empty($class)) ? ''current'' : $class;\n	$output = '' class="''.$class.''"'';\n\n	if ($pretext["id"] == $thisarticle["thisid"])\n	{\n		return $output;\n	}\n}\n\nfunction glx_hl_current_section($atts) \n{\n	global $pretext;\n\n	if (is_array($atts)) extract($atts);\n\n	$section = (empty($section)) ? "" : $section;\n	$class = (empty($class)) ? "current" : $class;\n\n	$output = '' class="''.$class.''"'';\n\n	$sections = array_map("trim", explode(",", $section));\n	\n	$condition = in_array($pretext["s"], $sections);\n\n	return ($condition) ? $output : "";\n}\n\n', '\n\nfunction glx_hl_current_article($atts) \n{\n	global $thisarticle;\n	global $pretext;\n\n	if (is_array($atts)) extract($atts);\n\n	$class = (empty($class)) ? ''current'' : $class;\n	$output = '' class="''.$class.''"'';\n\n	if ($pretext["id"] == $thisarticle["thisid"])\n	{\n		return $output;\n	}\n}\n\nfunction glx_hl_current_section($atts) \n{\n	global $pretext;\n\n	if (is_array($atts)) extract($atts);\n\n	$section = (empty($section)) ? "" : $section;\n	$class = (empty($class)) ? "current" : $class;\n\n	$output = '' class="''.$class.''"'';\n\n	$sections = array_map("trim", explode(",", $section));\n	\n	$condition = in_array($pretext["s"], $sections);\n\n	return ($condition) ? $output : "";\n}\n\n', '788428067aae247cffa37ff14b8521e3', 0, 5, 0),
('hak_article_image', 1, 'Patrick Woods', 'http://www.hakjoon.com', '0.6.3', 'Extended Article Image tag', '<style type="text/css">\r\ncode {\r\n	background-color:#F9FABA;\r\n}\r\n\r\nul {\r\n	list-style:none;\r\n	margin:0;\r\n	padding:0;\r\n	margin-bottom:1.0em;\r\n}\r\n\r\nul ul {\r\n	margin-left:5.0em;\r\n}\r\n\r\nul li {\r\n	margin:.5em 0;\r\n}\r\n\r\nh3 {\r\n	margin-top:1.0em;\r\n}\r\n\r\n</style>\r\n\r\n<p><code>&lt;txp:hak_article_image /&gt;</code> is an enhancement to <code>&lt;txp:article_image /&gt;</code>. It supports multiple images per article along with various ways of attaching links to the images. The article image field should contain one or more numeric image IDs separated by commas.</p>\r\n\r\n\r\n<p><code>&lt;txp:hak_article_thumb /&gt;</code> displays the image thumbnail(s) with an option to link the thumbnail in various different ways</p>\r\n\r\n	<h3>Attributes:</h3>\r\n\r\n	<ul>\r\n	<li><em><strong>limit</strong>=''x''</em> : Show only x number of images. Default is all images in the article  image field.</li>\r\n		<li><em><strong>offset</strong>=''x''</em> : offset the first image shown by x images.</li>\r\n		<li><em><strong>link</strong>=''1''</em> : links the image in manner determined by <em>linktype</em>.</li>\r\n		<li><em><strong>linktype</strong>=''type''</em> : type of link to create. Options:\r\n			<ul>\r\n				<li><em><strong>image</strong></em> : link to the full size image.</li>\r\n				<li><em><strong>page</strong></em> : link to the current page passing the image id in a way that can be used by <code>&lt;txp:image_display /&gt;</code> or <code>&lt;txp:hak_article_image urloverride="1" /&gt;</code>.  Will also pass a category if present to the target page.</li>\r\n				<li><em><strong>section</strong></em> : link to a specific section defined in the <em>section</em> attribute passing the image id in a way that can be used by <code>&lt;txp:image_display /&gt;</code> or <code>&lt;txp:hak_article_image urloverride="1" /&gt;</code>.</li>\r\n				<li><em><strong>category</strong></em> : link to the image''s category page or to a specific category defined in the <em>category</em> attribute passing the image id in a way that can be used by <code>&lt;txp:image_display /&gt;</code> or <code>&lt;txp:hak_article_image urloverride="1" /&gt;</code>.</li>\r\n				<li><em><strong>section-category</strong></em> : link to a specific section defined in the <em>section</em> attribute and to the image''s category page or a specific category defined in the <em>category</em> attribute passing the image id in a way that can be used by <code>&lt;txp:image_display /&gt;</code> or <code>&lt;txp:hak_article_image urloverride="1" /&gt;</code>.</li>\r\n			</ul>\r\n		<li><em><strong>section</strong>=''section name''</em> : section to link to when using <em>linktype=''section''</em> and <em>linktype=''section-category''</em> .</li>\r\n		<li><em><strong>category</strong>=''category name''</em> : category to link to when using <em>linktype=''category''</em>, <em>linktype=''page''</em> and <em>linktype=''section-category''</em>.</li>\r\n		<li><em><strong>anchor</strong>=''anchor name''</em> : pass an anchor along with the link.  Not available when using <em>linktype=''image''</em> because there would be no point.</li>\r\n		<li><em><strong>popup</strong>=1</em> : makes the link open in a popup window.  Only available when using <em>link</em>.</li>\r\n		<li><em><strong>popwidth</strong></em>=''integer'' : increase the popup window width by this much.</li>\r\n		<li><em><strong>popheight</strong></em>=''integer'' : increase the popup window height by this much.</li>\r\n		<li><em><strong>urloverride</strong></em>=''1'' : will override the image displayed with the image passed in the url by <code>&lt;txp:image_index /&gt;</code> or <code>&lt;txp:hak_article_image displaylink="1" /&gt;</code>. Would normally be used along with <em>limit=''1''</em></li>\r\n		<li><em><strong>urlhide</strong></em>=''1'' : will not display the image if it matches the image being passed in with the url, by using <em>linktype=''section or page''</em> or <code>&lt;txp:image_index /&gt;</code></li>\r\n		<li><em><strong>rel</strong>=''attribute''</em> : passes a <em>rel</em> attribute to a link created by <em>link=''1''</em> useful for lightbox/thickbox fun.</li>\r\n		<li><em><strong>linkclass</strong>=''classname''</em> : passes a <em>class</em> attribute to a link created by <em>link=''1''</em>.</li>\r\n		<li><em><strong>linktitle</strong>=''attribute''</em> : passes a <em>title</em> attribute to a link created by <em>link=''1''</em> also useful for lightbox/thickbox fun. Can accept any text or two special keywords:\r\n			<ul>\r\n				<li><em><strong>txp:caption</strong></em> : inserts the image''s caption.</li>\r\n				<li><em><strong>txp:alt</strong></em> : inserts the image''s alt information.</li>\r\n			</ul>\r\n		</li>\r\n		<li><em><strong>form</strong>=''form name''</em> : allows you to use a form to control the image output.  See below.</li>\r\n		<li>Both tags support the standard <em><strong>break</strong></em>, <em><strong>wraptag</strong></em>, <em><strong>class</strong></em> and <em><strong>breakclass</strong></em> attributes.</li>\r\n	</ul>\r\n\r\n<h3>Using forms</h3>\r\n	<p><code>&lt;txp:hak_article_image /&gt;</code> also supports using forms to format how your article images are displayed.  Forms also give you access to the image''s caption information.  All the same attributes are available but they are distributed among different tags.</p>\r\n	\r\n	<p><code>&lt;txp:hak_article_image /&gt;</code> and <code>&lt;txp:hak_article_thumb /&gt;</code> control which images will be used. It takes the following attributes:</p>\r\n	<ul>\r\n		<li><em><strong>limit</strong></em></li>\r\n		<li><em><strong>offset</strong></em></li>\r\n		<li><em><strong>urloverride</strong></em></li>\r\n		<li><em><strong>urlhide</strong></em></li>\r\n		<li>The standard <em><strong>break</strong></em>, <em><strong>wraptag</strong></em>, <em><strong>class</strong></em> and <em><strong>breakclass</strong></em> attributes.</li>\r\n	</ul>\r\n	\r\n	<h4>Form Tags</h4>\r\n	\r\n	<p><code>&lt;txp:hak_article_image_link /&gt;</code> creates a link with various options. Should be used as a container tag with an opening and closing tag.  It takes the following attribues:</p>\r\n	<ul>\r\n		<li><em><strong>type</strong></em> same as <em><strong>linktype</strong></em></li>\r\n		<li><em><strong>section</strong></em></li>\r\n		<li><em><strong>category</strong></em></li>\r\n		<li><em><strong>anchor</strong></em></li>\r\n		<li><em><strong>popup</strong></em></li>\r\n		<li><em><strong>popwidth</strong></em> and <em><strong>popheight</strong></em></li>\r\n		<li><em><strong>rel</strong></em></li>\r\n		<li><em><strong>title</strong></em> same as <em><strong>linktitle</strong></em></li>\r\n		<li><em><strong>class</strong></em> same as <em><strong>linkclass</strong></em></li>\r\n	</ul>\r\n	\r\n	<p><code>&lt;txp:hak_image /&gt;</code> and <code>&lt;txp:hak_thumbnail /&gt;</code> display either the full size image or the thumbnail.  Accept a <code>class</code> attribute.</p>\r\n	\r\n	<p><code>&lt;txp:hak_image_caption /&gt;</code> displays the caption for the current image. Accepts the standard <em><strong>break</strong></em>, <em><strong>wraptag</strong></em>, <em><strong>class</strong></em> and <em><strong>breakclass</strong></em> attributes. </p>\r\n	\r\n		<p><code>&lt;txp:hak_image_alt /&gt;</code> displays the alt information for the current image. Accepts the standard <em><strong>break</strong></em>, <em><strong>wraptag</strong></em>, <em><strong>class</strong></em> and <em><strong>breakclass</strong></em> attributes. </p>\r\n\r\n	<h3>Examples:</h3>\r\n\r\n\r\n<dl>\r\n<dt><code>&lt;txp:hak_article_image /&gt;</code></dt>\r\n    <dd>Display all the article images &mdash; with no link</dd>\r\n<dt><code>&lt;txp:hak_article_image limit="5" /&gt;</code></dt>\r\n    <dd>Display a list of article images, up to a limit of 5</dd>\r\n<dt><code>&lt;txp:hak_article_image offset="1" /&gt;</code></dt>\r\n    <dd>Display a list of article images, but skips the first image</dd>\r\n<dt><code>&lt;txp:hak_article_thumb /&gt;</code></dt>\r\n    <dd>Display all the article thumbnails &emdash; with no link</dd>\r\n<dt><code>&lt;txp:hak_article_thumb link="1" linktype="image" /&gt;</code></dt>\r\n    <dd>Display the first article thumbnail with a link the full size version in a popup &mdash; same as old <em><strong>poplink</strong></em>.</dd>\r\n<dt><code>&lt;txp:hak_article_thumb limit="5" wraptag="ul" break="li" link="1" linktype="section" section="photos" /&gt;</code></dt>\r\n    <dd>Display up to 5 article thumbnails as an unordered list with links to the images in the <em>photos</em> section</dd>\r\n<dt><code>&lt;txp:hak_article_thumb limit="5" wraptag="ul" break="li" class="wrapclass" breakclass="breakclass" /&gt;</code></dt>\r\n    <dd>Display up to 5 article thumbnails as an unordered list with class of &#8220;wrapclass&#8221; on the ul and a class of <em>breakclass</em> for each li</dd>\r\n<dt><code>&lt;txp:hak_article_thumb limit="5" wraptag="ul" break="li" link="1" linktype="page" anchor="theAnchor" /&gt;</code></dt>\r\n	    <dd>Display up to 5 article thumbnails as an unordered list with links to the same page using the anchor <em>theAnchor</em>.  Ideally used with <code>&lt;txp:image_display /&gt;</code> or <code>&lt;txp:hak_article_image urloverride="1" limit="1"&lt;</code></dd>\r\n<dt><code>&lt;txp:hak_article_thumb limit=5 wraptag="ul" break="li" attributes="id=''theID''" /&gt;</code></dt>\r\n    <dd>Display up to 5 article thumbnails as an unordered list with an id of <em>theID</em> assigned to the ul</dd>\r\n</dl>\r\n\r\n	<h3>Deprecated options</h3>\r\n	\r\n	<p>These will still work but you should use the new options as they are more flexible</p>\r\n\r\n	<dl>\r\n		<dt><strong>poplink</strong> &ndash; same as:</dt>\r\n		<dd><code>&lt;txp:hak_article_image link="1" linktype="image" popup="1" /&gt;</code></dd>\r\n		<dt><strong>displaylink</strong> &ndash; same as:</dt>\r\n		<dd><code>&lt;txp:hak_article_image link="1" linktype="page" /&gt;</code></dd>\r\n	</dl>\r\n		\r\n	<p><strong>hak_article_image</strong> started as a simple extension to <strong>zem_article_image</strong> which allowed popup links from the thumbnails.  While the current version barely resembles that original version <a href="http://thresholdstate.com/">Zem</a> still deserves the credit for the original implementation and for getting me started working on plugins.  Also everyone on the forum who keeps coming up with new ideas for this.</p>\r\n\r\n\r\n', '\r\nfunction hak_article_thumb($atts) {\r\n	$atts["thumb"] = 1;\r\n	return hak_article_image($atts);\r\n}\r\n\r\nfunction hak_article_image($atts) {\r\n	global $thisarticle, $hak_thisimage, $p, $c, $s;\r\n	\r\n\r\n	\r\n	extract(lAtts(array(\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''limit'' => count(explode(",",$thisarticle["article_image"])),\r\n		''offset'' => 0,\r\n		''attributes'' => '''',\r\n		''poplink'' => 0,\r\n		''displaylink'' => 0,\r\n		''urlhide'' => 0,\r\n		''urloverride'' => 0,\r\n		''thumb'' => 0,\r\n		''link'' => 1,\r\n		''anchor'' => '''',\r\n		''linktype'' => ''image'',\r\n		''section'' => '''',\r\n		''category'' => $c,\r\n		''popup'' => 0,\r\n		''popwidth'' => 0,\r\n		''popheight'' => 0,\r\n		''rel'' => '''',\r\n		''linktitle'' => '''',\r\n		''linkclass'' => '''',\r\n		''form'' => '''',\r\n		),$atts));\r\n\r\n	if (!empty($thisarticle["article_image"])) {\r\n		if ($displaylink) {\r\n			$link = 1;\r\n			$linktype = ''page'';\r\n		}\r\n		if ($poplink) {\r\n			$link = 1;\r\n			$linktype = ''image'';\r\n			$popup = 1;\r\n		}\r\n		$linkAtts = ''type="''.$linktype.''" ''.\r\n					''anchor="''.$anchor.''" ''.\r\n					''section="''.$section.''" ''.\r\n					''category="''.$category.''" ''.\r\n					''popup="''.$popup.''" ''.\r\n					''popwidth="''.$popwidth.''" ''.\r\n					''popheight="''.$popheight.''" ''.\r\n					''rel="''.$rel.''" ''.\r\n					''class="''.$linkclass.''" ''.\r\n					''title="''.$linktitle.''"'';\r\n\r\n		$default_form = '''';			\r\n		$default_form .= ($link) ? ''<txp:hak_article_image_link ''.$linkAtts.''>'' : '''';\r\n		$default_form .= ($thumb) ? ''<txp:hak_thumbnail />'' : ''<txp:hak_image />'';\r\n		$default_form .= ($link) ? ''</txp:hak_article_image_link>'' : '''';\r\n		$form = (!empty($form)) ?  fetch_form($form) : $default_form;\r\n		$images = array_slice(explode(",", $thisarticle["article_image"]), $offset, $limit);\r\n		foreach ($images as $id) {\r\n			if (is_numeric($id)) {\r\n				$id = ($urloverride && $p) ? $p : $id;\r\n				$rs = (!($urlhide && $p && ($p == $id))) ? safe_row("*", "txp_image", "id=''$id'' limit 1") : 0;\r\n				if ($rs) {\r\n					$GLOBALS[''hak_thisimage''] = $rs;\r\n					$out[] = parse($form);\r\n					unset($GLOBALS[''hak_thisimage'']);\r\n				}\r\n			} else {\r\n				$out[] = ''<img src="''.$id.''" alt="" />'';\r\n			}\r\n		} \r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\nfunction hak_article_image_link($atts, $thing) {\r\n	global $hak_thisimage, $img_dir, $thisarticle, $permlink_mode, $s, $c;\r\n	\r\n	extract(lAtts(array(\r\n		''section'' => $s,\r\n		''category'' => $c,\r\n		''anchor'' => '''',\r\n		''type'' => ''image'',\r\n		''popup'' => 0,\r\n		''popwidth'' => 0,\r\n		''popheight'' => 0,\r\n		''rel'' => '''',\r\n		''title'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n		\r\n		$anchor = (!empty($anchor)) ? ''#''.$anchor : $anchor;\r\n		switch(strtolower($type)) {\r\n			case "image" :\r\n				$url = hu.$img_dir.''/''.$hak_thisimage[''id''].$hak_thisimage[''ext''];\r\n				break;\r\n			case "section" :\r\n				$url = pagelinkurl(array(''s''=>$section));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.''?p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			case "page" :\r\n				$url = permlinkurl($thisarticle);\r\n				$category = (!empty($category)) ? a.''c=''.$category : '''';\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$category.$anchor : $url.''/?p=''.$hak_thisimage[''id''].$category.$anchor;\r\n				break;\r\n			case "category" :\r\n				$category = (empty($category)) ? $hak_thisimage[''category''] : $category;\r\n				$url =  pagelinkurl(array(''c''=>$category));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.''?p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			case "section-category" :\r\n				$category = (empty($category)) ? $hak_thisimage[''category''] : $category;\r\n				$url =  pagelinkurl(array(''s''=>$section, ''c''=>$category));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.a.''p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			default :\r\n				$url = '''';\r\n		}\r\n		if (!empty($title)) {\r\n			switch(strtolower($title)) {\r\n				case "txp:caption" :\r\n					$attribs .= '' title="''.$hak_thisimage[''caption''].''"'';\r\n					break;\r\n				case "txp:alt" :\r\n					$attribs .= '' title="''.$hak_thisimage[''alt''].''"'';\r\n					break;\r\n				default :\r\n					$attribs .= '' title="''.$title.''"'';\r\n			}\r\n		}\r\n		\r\n		if (!empty($class)) {\r\n			$attribs .= '' class="''.$class.''"'';\r\n		}\r\n		\r\n		$popwin = ($popup) ? '' onclick="window.open(this.href, \\''popupwindow\\'', \\''width=''.\r\n              ($hak_thisimage[''w''] + $popwidth).'',height=''.($hak_thisimage[''h''] + $popheight).'',scrollbars,resizable\\''); return false;"'' : '''';\r\n\r\n		$attribs = (($rel) ? '' rel="''.$rel.''"'' : '''').$attribs.$popwin;\r\n		$out = tag(parse($thing), ''a'', '' href="''.$url.''"''.$attribs);\r\n	return $out;\r\n}\r\n\r\nfunction hak_image($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n	\r\n	if($hak_thisimage) {\r\n		$img = image(array(\r\n						"id"=>$hak_thisimage[''id''],\r\n						"class"=>$class,\r\n					));\r\n		return $img;\r\n	}\r\n}\r\n\r\nfunction hak_thumbnail($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n	\r\n	if (!empty($hak_thisimage["thumbnail"])) {\r\n		$img = thumbnail(array(\r\n							"id"=>$hak_thisimage[''id''],\r\n							"class"=>$class,\r\n							));\r\n		return $img;\r\n	}\r\n}\r\n\r\nfunction hak_image_caption($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''attributes'' => '''',\r\n		), $atts));\r\n\r\n	if (!empty($hak_thisimage["caption"])) {\r\n		$out[] = $hak_thisimage["caption"];\r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\nfunction hak_image_alt($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''attributes'' => '''',\r\n		), $atts));\r\n\r\n	if (!empty($hak_thisimage["alt"])) {\r\n		$out[] = $hak_thisimage["alt"];\r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\n', '\r\nfunction hak_article_thumb($atts) {\r\n	$atts["thumb"] = 1;\r\n	return hak_article_image($atts);\r\n}\r\n\r\nfunction hak_article_image($atts) {\r\n	global $thisarticle, $hak_thisimage, $p, $c, $s;\r\n	\r\n\r\n	\r\n	extract(lAtts(array(\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''limit'' => count(explode(",",$thisarticle["article_image"])),\r\n		''offset'' => 0,\r\n		''attributes'' => '''',\r\n		''poplink'' => 0,\r\n		''displaylink'' => 0,\r\n		''urlhide'' => 0,\r\n		''urloverride'' => 0,\r\n		''thumb'' => 0,\r\n		''link'' => 1,\r\n		''anchor'' => '''',\r\n		''linktype'' => ''image'',\r\n		''section'' => '''',\r\n		''category'' => $c,\r\n		''popup'' => 0,\r\n		''popwidth'' => 0,\r\n		''popheight'' => 0,\r\n		''rel'' => '''',\r\n		''linktitle'' => '''',\r\n		''linkclass'' => '''',\r\n		''form'' => '''',\r\n		),$atts));\r\n\r\n	if (!empty($thisarticle["article_image"])) {\r\n		if ($displaylink) {\r\n			$link = 1;\r\n			$linktype = ''page'';\r\n		}\r\n		if ($poplink) {\r\n			$link = 1;\r\n			$linktype = ''image'';\r\n			$popup = 1;\r\n		}\r\n		$linkAtts = ''type="''.$linktype.''" ''.\r\n					''anchor="''.$anchor.''" ''.\r\n					''section="''.$section.''" ''.\r\n					''category="''.$category.''" ''.\r\n					''popup="''.$popup.''" ''.\r\n					''popwidth="''.$popwidth.''" ''.\r\n					''popheight="''.$popheight.''" ''.\r\n					''rel="''.$rel.''" ''.\r\n					''class="''.$linkclass.''" ''.\r\n					''title="''.$linktitle.''"'';\r\n\r\n		$default_form = '''';			\r\n		$default_form .= ($link) ? ''<txp:hak_article_image_link ''.$linkAtts.''>'' : '''';\r\n		$default_form .= ($thumb) ? ''<txp:hak_thumbnail />'' : ''<txp:hak_image />'';\r\n		$default_form .= ($link) ? ''</txp:hak_article_image_link>'' : '''';\r\n		$form = (!empty($form)) ?  fetch_form($form) : $default_form;\r\n		$images = array_slice(explode(",", $thisarticle["article_image"]), $offset, $limit);\r\n		foreach ($images as $id) {\r\n			if (is_numeric($id)) {\r\n				$id = ($urloverride && $p) ? $p : $id;\r\n				$rs = (!($urlhide && $p && ($p == $id))) ? safe_row("*", "txp_image", "id=''$id'' limit 1") : 0;\r\n				if ($rs) {\r\n					$GLOBALS[''hak_thisimage''] = $rs;\r\n					$out[] = parse($form);\r\n					unset($GLOBALS[''hak_thisimage'']);\r\n				}\r\n			} else {\r\n				$out[] = ''<img src="''.$id.''" alt="" />'';\r\n			}\r\n		} \r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\nfunction hak_article_image_link($atts, $thing) {\r\n	global $hak_thisimage, $img_dir, $thisarticle, $permlink_mode, $s, $c;\r\n	\r\n	extract(lAtts(array(\r\n		''section'' => $s,\r\n		''category'' => $c,\r\n		''anchor'' => '''',\r\n		''type'' => ''image'',\r\n		''popup'' => 0,\r\n		''popwidth'' => 0,\r\n		''popheight'' => 0,\r\n		''rel'' => '''',\r\n		''title'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n		\r\n		$anchor = (!empty($anchor)) ? ''#''.$anchor : $anchor;\r\n		switch(strtolower($type)) {\r\n			case "image" :\r\n				$url = hu.$img_dir.''/''.$hak_thisimage[''id''].$hak_thisimage[''ext''];\r\n				break;\r\n			case "section" :\r\n				$url = pagelinkurl(array(''s''=>$section));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.''?p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			case "page" :\r\n				$url = permlinkurl($thisarticle);\r\n				$category = (!empty($category)) ? a.''c=''.$category : '''';\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$category.$anchor : $url.''/?p=''.$hak_thisimage[''id''].$category.$anchor;\r\n				break;\r\n			case "category" :\r\n				$category = (empty($category)) ? $hak_thisimage[''category''] : $category;\r\n				$url =  pagelinkurl(array(''c''=>$category));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.''?p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			case "section-category" :\r\n				$category = (empty($category)) ? $hak_thisimage[''category''] : $category;\r\n				$url =  pagelinkurl(array(''s''=>$section, ''c''=>$category));\r\n				$url = ($permlink_mode == ''messy'') ? $url.a.''p=''.$hak_thisimage[''id''].$anchor : $url.a.''p=''.$hak_thisimage[''id''].$anchor;\r\n				break;\r\n			default :\r\n				$url = '''';\r\n		}\r\n		if (!empty($title)) {\r\n			switch(strtolower($title)) {\r\n				case "txp:caption" :\r\n					$attribs .= '' title="''.$hak_thisimage[''caption''].''"'';\r\n					break;\r\n				case "txp:alt" :\r\n					$attribs .= '' title="''.$hak_thisimage[''alt''].''"'';\r\n					break;\r\n				default :\r\n					$attribs .= '' title="''.$title.''"'';\r\n			}\r\n		}\r\n		\r\n		if (!empty($class)) {\r\n			$attribs .= '' class="''.$class.''"'';\r\n		}\r\n		\r\n		$popwin = ($popup) ? '' onclick="window.open(this.href, \\''popupwindow\\'', \\''width=''.\r\n              ($hak_thisimage[''w''] + $popwidth).'',height=''.($hak_thisimage[''h''] + $popheight).'',scrollbars,resizable\\''); return false;"'' : '''';\r\n\r\n		$attribs = (($rel) ? '' rel="''.$rel.''"'' : '''').$attribs.$popwin;\r\n		$out = tag(parse($thing), ''a'', '' href="''.$url.''"''.$attribs);\r\n	return $out;\r\n}\r\n\r\nfunction hak_image($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n	\r\n	if($hak_thisimage) {\r\n		$img = image(array(\r\n						"id"=>$hak_thisimage[''id''],\r\n						"class"=>$class,\r\n					));\r\n		return $img;\r\n	}\r\n}\r\n\r\nfunction hak_thumbnail($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''class'' => '''',\r\n		), $atts));\r\n	\r\n	if (!empty($hak_thisimage["thumbnail"])) {\r\n		$img = thumbnail(array(\r\n							"id"=>$hak_thisimage[''id''],\r\n							"class"=>$class,\r\n							));\r\n		return $img;\r\n	}\r\n}\r\n\r\nfunction hak_image_caption($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''attributes'' => '''',\r\n		), $atts));\r\n\r\n	if (!empty($hak_thisimage["caption"])) {\r\n		$out[] = $hak_thisimage["caption"];\r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\nfunction hak_image_alt($atts) {\r\n	global $hak_thisimage;\r\n	\r\n	extract(lAtts(array(\r\n		''name'' => '''',\r\n		''id'' => '''',\r\n		''break'' => '''',\r\n		''wraptag'' => '''',\r\n		''breakclass'' => '''',\r\n		''class'' => '''',\r\n		''attributes'' => '''',\r\n		), $atts));\r\n\r\n	if (!empty($hak_thisimage["alt"])) {\r\n		$out[] = $hak_thisimage["alt"];\r\n		return doWrap($out, $wraptag, $break, $class, $breakclass, $attributes);\r\n	}\r\n}\r\n\r\n', 'e6045b3f59855e79df4b05b10a56a065', 0, 5, 0),
('cbs_category_list', 1, 'Christophe Beyls', 'http://www.digitalia.be/', '0.9', 'Section-sensitive article category list', '\n	<h3>Description</h3>\n\n	<p>This simple plugin is basically equivalent to category_list, excepted that it is <strong>section-sensitive</strong> and only lists <strong>article</strong> categories.</p>\n\n	<p>Categories are displayed using their title and optionally their article count for the selected section and sorted alphabetically by title.</p>\n\n	<p>If no article is found in the section for a category, this category will not be shown.</p>\n\n	<h3>Syntax</h3>\n\n<pre><code>&lt;txp:cbs_category_list /&gt;</code></pre>\n\n	<h3>Attributes</h3>\n\n	<h4>Required</h4>\n\n	<p>none.</p>\n\n	<h4>Optional</h4>\n\n	<ul>\n		<li><strong>section</strong>: The section to display the related article categories. When &#8216;default&#8217; (the frontpage) is specified, it shows all categories related to sections which are part of the frontpage. Default is the <em>current section</em>.</li>\n		<li><strong>sticky</strong>: When set to <code>true</code>, will display the category list of &#8216;sticky&#8217; articles instead of &#8216;live&#8217; articles. Default is <code>false</code>.</li>\n		<li><strong>posted</strong>: Either &#8216;past&#8217;, &#8216;future&#8217; or &#8216;all&#8217;. Will only count articles with the specified publication date. &#8216;all&#8217; is slightly faster because MySQL will be able to cache the query. Default is &#8216;past&#8217;.</li>\n		<li><strong>parent</strong>: The parent category name. If specified, will return only the <em>direct</em> children categories for this parent. It will not show children of children. Default is any category.</li>\n		<li><strong>showcount</strong>: When set to <code>true</code>, displays the number of related articles next to each category. Default is <code>false</code>.</li>\n		<li><strong>label</strong>: Label for the top of the list. Default is no label.</li>\n		<li><strong>labeltag</strong>:  Independent wraptag for label for the top of the list. Default is empty.</li>\n		<li><strong>break</strong>: HTML tag to be used for line breaks, without brackets. Default is <code>br</code>.</li>\n		<li><strong>wraptag</strong>: HTML tag to be used as the wraptag, without brackets. Default is empty.</li>\n		<li><strong>class</strong>: CSS class attribute for wraptag, default is <code>cbs_category_list</code>.</li>\n		<li><strong>activeclass</strong>: CSS class attribute for the currently active category, if any. By default, the active category is displayed like the other ones.</li>\n	</ul>\n\n\n\n ', '\n/*\n   cbs_category_list v0.9\n   by Christophe Beyls [http://www.digitalia.be]\n*/\n\nfunction cbs_category_list($atts)\n{\n	global $s, $c;\n\n	extract(lAtts(array(\n		''label''    => '''',\n		''labeltag'' => '''',\n		''break''    => ''br'',\n		''wraptag''  => '''',\n		''parent''   => '''',\n		''posted''   => '''',\n		''section''  => $s,\n		''sticky''   => '''',\n		''showcount''=> '''',\n		''class''    => __FUNCTION__,\n		''activeclass''=> ''''\n	),$atts));\n\n	$parent = ($parent) ? " AND c.parent = ''$parent''" : '''';\n	$showcount = ($showcount == ''true'') ? '', COUNT(*) AS count'' : '''';\n	$sticky = ($sticky == ''true'') ? ''5'' : ''4'';\n\n	switch($posted) {\n		case ''all'':\n			$posted = '''';\n			break;\n		case ''future'':\n			$posted = '' AND t.Posted >= now()'';\n			break;\n		default:\n			$posted = '' AND t.Posted < now()'';\n	}\n\n	if($section == ''default'') {\n		$table = '', `''.PFX.''txp_section` AS s'';\n		$sqlsection = ''s.name AND s.on_frontpage = 1'';\n	} else {\n		$table = '''';\n		$sqlsection = "''$section''";\n	}\n\n	$rs = startRows(\n	  ''SELECT c.name, c.title''.$showcount.'' FROM `''.PFX.''txp_category` AS c, `''.PFX.''textpattern` AS t''.$table\n	  .'' WHERE (c.name = t.Category1 OR c.name = t.Category2) AND c.type = \\''article\\'' AND t.Section = ''.$sqlsection.$parent.$posted\n	  .'' AND t.Status = ''.$sticky.'' GROUP BY c.name ORDER BY c.title''\n	);\n	if(!$rs)\n		return '''';\n\n	$count = '''';\n	$out = array();\n	while($a = nextRow($rs)) {\n		extract($a);\n		$out[] = tag(str_replace(''& '',''&#38; '', $title), ''a'', '' href="''.pagelinkurl(array(''c''=>$name, ''s''=>$section))\n				.(($activeclass && ($s == $section) && ($c == $name)) ? ''" class="''.$activeclass.''"'' : ''"'')).($count ? '' (''.$count.'')'' : '''');\n	}\n	return doLabel($label, $labeltag).doWrap($out, $wraptag, $break, $class);\n}\n', '\n/*\n   cbs_category_list v0.9\n   by Christophe Beyls [http://www.digitalia.be]\n*/\n\nfunction cbs_category_list($atts)\n{\n	global $s, $c;\n\n	extract(lAtts(array(\n		''label''    => '''',\n		''labeltag'' => '''',\n		''break''    => ''br'',\n		''wraptag''  => '''',\n		''parent''   => '''',\n		''posted''   => '''',\n		''section''  => $s,\n		''sticky''   => '''',\n		''showcount''=> '''',\n		''class''    => __FUNCTION__,\n		''activeclass''=> ''''\n	),$atts));\n\n	$parent = ($parent) ? " AND c.parent = ''$parent''" : '''';\n	$showcount = ($showcount == ''true'') ? '', COUNT(*) AS count'' : '''';\n	$sticky = ($sticky == ''true'') ? ''5'' : ''4'';\n\n	switch($posted) {\n		case ''all'':\n			$posted = '''';\n			break;\n		case ''future'':\n			$posted = '' AND t.Posted >= now()'';\n			break;\n		default:\n			$posted = '' AND t.Posted < now()'';\n	}\n\n	if($section == ''default'') {\n		$table = '', `''.PFX.''txp_section` AS s'';\n		$sqlsection = ''s.name AND s.on_frontpage = 1'';\n	} else {\n		$table = '''';\n		$sqlsection = "''$section''";\n	}\n\n	$rs = startRows(\n	  ''SELECT c.name, c.title''.$showcount.'' FROM `''.PFX.''txp_category` AS c, `''.PFX.''textpattern` AS t''.$table\n	  .'' WHERE (c.name = t.Category1 OR c.name = t.Category2) AND c.type = \\''article\\'' AND t.Section = ''.$sqlsection.$parent.$posted\n	  .'' AND t.Status = ''.$sticky.'' GROUP BY c.name ORDER BY c.title''\n	);\n	if(!$rs)\n		return '''';\n\n	$count = '''';\n	$out = array();\n	while($a = nextRow($rs)) {\n		extract($a);\n		$out[] = tag(str_replace(''& '',''&#38; '', $title), ''a'', '' href="''.pagelinkurl(array(''c''=>$name, ''s''=>$section))\n				.(($activeclass && ($s == $section) && ($c == $name)) ? ''" class="''.$activeclass.''"'' : ''"'')).($count ? '' (''.$count.'')'' : '''');\n	}\n	return doLabel($label, $labeltag).doWrap($out, $wraptag, $break, $class);\n}\n', '3830dab0c260af4ecfd73aed6d583b3c', 0, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('glz_custom_fields', 0, 'Gerhard Lazu', 'http://gerhardlazu.com/', '1.1.3', 'Limitless Pimped Custom Fields', '<style type="text/css" media="screen,projection">\n<!--\n  #glz_custom_fields_help {\n    margin: 0 auto;\n    font-family: Verdana,"Lucida Grande",sans-serif;\n    padding: 15px;\n    background-color: #F7F7F7;\n    border: 1px dotted #EBEBEB;\n    width: 700px;\n    color: #333;\n    font-size: 12px;\n    line-height:15px;\n  }\n  #glz_custom_fields_help p, #glz_custom_fields_help ul, #glz_custom_fields_help ol, #glz_custom_fields_help dl {\n    font-size: 12px;\n    line-height: 15px;\n    margin-bottom: 15px;\n  }\n  #glz_custom_fields_help ul {\n    padding-left: 15px;\n    list-style: square;\n  }\n  #glz_custom_fields_help ol {\n    padding-left: 20px;\n  }\n  #glz_custom_fields_help a, #glz_custom_fields_help a.visited {\n    color: #FF7B00;\n    text-decoration: none;\n    padding-bottom: 1px;\n    border-bottom: 1px dotted #FF7B00;\n  }\n  #glz_custom_fields_help a:hover {\n    color: #FFF;\n    background-color: #FF7B00;\n    border-bottom: 1px dotted #FF7B00;\n  }\n  #glz_custom_fields_help h1 {\n    font-family: Georgia,Times,Serif;\n    font-size: 24px;\n    font-weight: normal;\n    text-decoration: none;\n    line-height: 30px;\n    margin-bottom: 15px;\n    color: #333;\n    letter-spacing: -1px;\n  }\n  #glz_custom_fields_help h2 {\n    font-family: Georgia,Times,Serif;\n    color: #FFF;\n    font-size: 18px;\n    font-weight: normal;\n    line-height: 30px;\n    margin-bottom: 15px;\n    background-color: #C1C4C3;\n    padding-left: 15px;\n  }\n  #glz_custom_fields_help strong {\n\n  }\n  #glz_custom_fields_help code {\n    line-height: 16px;\n    font-style: italic;\n    font-family: Monaco,"Courier New", Courier, monospace;\n    font-size-adjust: none;\n    font-stretch: normal;\n    font-variant: normal;\n    font-weight: normal;\n    font-size: 12px;\n    color: #000;\n  }\n  #glz_custom_fields_help code.txp {\n    background-color: #FFF6A9;\n    font-family: Monaco,"Courier New", Courier, monospace;\n    line-height: 30px;\n    font-style: normal;\n    padding: 2px 0;\n    color: #000;\n  }\n-->\n</style>\n\n<div id="glz_custom_fields_help">\n  <h1>glz_custom_fields, limitless pimped custom fields</h1>\n\n  <p>If you feel totally lost, there is a screencast showing the basics of this plugin: <a href="http://gerhardlazu.com/file_download/15" title="glz_custom_fields, Limitless Pimped Custom Fields">glz_custom_fields_unveiled.mov</a>. The post associated with this screencast is here: <a href="http://gerhardlazu.com/blog/50/glz_custom_fields-unveiled" title="glz_custom_fields unveiled">glz_custom_fields unveiled</a>.</p>\n\n  <h2>Requirements</h2>\n  <ul>\n   <li>minimum <strong>TXP 4</strong> (<a href="http://www.tetpattern.com/download" title="Latest Stable Textpattern Download">TXP 4.0.6</a> recommended)</li>\n   <li><strong>jquery.js</strong> present in <strong>textpattern</strong> folder. Please make sure your jQuery is at least v1.2.</li>\n   <li>preferably, a clean TXP install. If you have been using various custom fields hacks, <strong>PLEASE back-up your database</strong> before using this plugin. While it won''t delete anything automatically, you can delete all your custom fields data (<strong>and I really mean all of it</strong>) if not careful. We still don''t have an automatic import functionality, but if you haven''t performed any hacks on the TXP''s default custom fields don''t worry, integration will be seamless (as will all upgrades from glz_custom_fields 1.0). Having said that, database back-up is <strong>STRONGLY</strong> advised.</li>\n   <li>unfortunately, a hack is required to get custom fields > 10 on single articles, please check this page: <a href="http://gerhardlazu.com/blog/51/glz_custom_fields-11-released">glz_custom_fields 1.1 released</a></li>\n  </ul>\n\n  <h2>Usage and Options</h2>\n  <p>Because this is a back-end plugin primarily, there are only a few front-end functions:</p>\n\n  <code class="txp">&lt;txp:glz_if_custom_field name="Rating" val="4">...&lt;/txp:glz_if_custom_field></code>\n  <p>This is similar to <code>&lt;txp:if_custom_field /&gt;</code> so please refer to the <a href="#">TextBook</a> for documentation. The only difference is that this tag will look past custom_10 and it searches custom fields with multiple values nicely (e.g. value 1|value 2|value 3).</p>\n\n  <code class="txp">&lt;txp:glz_custom_field name="Rating" /></code>\n  <p>This is similar to <code>&lt;txp:custom_field /&gt;</code> so please refer to the <a href="#">TextBook</a> for documentation. The only difference is that this tag will look past custom_10.</p>\n\n  <code class="txp">&lt;txp:glz_custom_fields_search_form results_page="listings" searchby="Area,City,Price" /></code>\n  <p>This builds a nice search form on the front-end (only selects so far).</p>\n  <ul>\n  <li><code>results_page</code> - which page (section) should the submit button take the user to (<code>default</code> by default)</li>\n  <li><code>searchby (MUST BE SET)</code> - which custom fields should we search by and in what order? (<code>empty</code> by default)</li>\n  </ul>\n\n  <code class="txp">&lt;txp:glz_article limit="100" no_results="no_results" /></code>\n  <p>This needs to be used instead of <code>txp:article</code> where you want the search results to appear. We need to "inject" our extra custom fields in the search query and modify it slightly. This is a straight copy of <code>article()</code> from TXP 4.0.6 (r2085) with some "magical dust" : ).</p>\n  <p>This is not too obvious so please pay attention. If our custom search tag is set to land on section "listings" (which we define in <code>glz_custom_fields_search_form</code>), the articles you want searching <b>must</b> be belong to this section. For example, if our search points to section "listings" but our articles are saved under section "properties", there will be no matches and thus we will be forwarded to <b>no_results</b> page. In this case, what we might want to do, is set <b>results_page</b> in <code>glz_custom_fields_search_form</code> to "properties".</p>\n  <ul>\n  <li><code>no_results</code> - if no matching results are found, this is the page (section) where the users will be redirected (<code>no_results</code> by default)</li>\n  </ul>\n\n  <code class="txp">&lt;txp:glz_article_custom limit="5" no_results="no_results" form="some_form" /></code>\n  <p>This needs to be used instead of <code>txp:article_custom</code> where you want the search results to appear. We need to "inject" our extra custom fields in the search query and modify it slightly. This is a straight copy of <code>article_custom()</code> from TXP 4.0.6 (r2085) with some "magical dust" : ).</p>\n  <p>This is not too obvious so please pay attention. If our custom search tag is set to land on section "listings" (which we define in <code>glz_custom_fields_search_form</code>), the articles you want searching <b>must</b> be belong to this section. For example, if our search points to section "listings" but our articles are saved under section "properties", there will be no matches and thus we will be forwarded to <b>no_results</b> page. In this case, what we might want to do, is set <b>results_page</b> in <code>glz_custom_fields_search_form</code> to "properties".</p>\n  <ul>\n  <li><code>no_results</code> - if no matching results are found, this is the page (section) where the users will be redirected (<code>no_results</code> by default)</li>\n  </ul>\n\n  <h2>Tricks</h2>\n  <p>Actually, there is a single one, but I''ll let you know if I''ll implement more ; ).</p>\n  <p>If you want to define ranges, do the following for the range name (custom set name): <code>Price range &amp;pound;(after)</code></p>\n  <ul>\n  <li><code>range</code> - this is really important and <strong>must</strong> be present for the range to work correctly</li>\n  <li><code>&amp;pound;</code> - don''t use straight symbols (like &euro; or &pound;) but entity ones e.g. &amp;pound; &amp; &amp;euro;</li>\n  <li><code>(after)</code> - don''t leave any spaces after the measuring unit and (after). This basically says where to add the measuring unit - <code>(before)</code> is default. You might have &pound;10-&pound;20 but 10m<sup>3</sup>-20m<sup>3</sup></li>\n  </ul>\n\n  <p>Ranges are defined 10-20, 21-30 etc. (no measuring units - they get pulled from the custom set name).</p>\n\n  <h2>HEEEELP!</h2>\n  <p>If anything goes wrong or you discover a bug:</p>\n  <ol>\n   <li>stay calm : )</li>\n   <li>drop a line to <a href="mailto:mail@gerhardlazu.com?Subject:glz_custom_fields needs your attention">mail@gerhardlazu.com</a></li>\n   <li>wait patiently for an answer - sometimes things can get really hectic on my end as well</li>\n   <li>enjoy the little things in life (like TXP)</li>\n  </ol>\n\n  <h2>Colophon & Limitations</h2>\n  <p><strong>PLEASE back-up your database</strong> before using this plugin. It won''t delete anything automatically, but if something goes wrong, you might end up deleting all your custom fields data (<strong>and I really mean all of it</strong>).</p>\n  <p>If you don''t have JavaScript enabled in your browser, things will blow up - GUARANTEED.</p>\n</div>', '//<?php\n/**\nglz_custom_fields plugin, FROM UK WITH LOVE\n\n@author Gerhard Lazu\n@version 1.1.3\n@copyright Gerhard Lazu, 3 March, 2008\n@package TXP 4.0.6 (2805)\n@contributors:  Sam Weiss, redbot, Manfre, Vladimir\n*/\n\n// checks if all tables exists and everything is setup properly\nglz_custom_fields_install();\n\nif (txpinterface == "admin") {\n  // we need some stylesheets & JS\n  ob_start("glz_custom_fields_css_js");\n\n  // Custom Fields tab under Extensions\n  add_privs(''glz_custom_fields'', ''1'');\n  register_tab("extensions", "glz_custom_fields", "Custom Fields");\n  register_callback("glz_custom_fields", "glz_custom_fields");\n\n  // we need to make sure that all custom field values will be converted to strings first - think checkboxes & multi-selects\n  if ( (gps("step") == "edit") || (gps("step") == "create") ) register_callback("glz_custom_fields_before_save", "article", '''', 1);\n  // if we edit or create a new article, save our extra custom fields (> 10)\n  if ( (gps("step") == "edit") || (gps("step") == "create") ) register_callback("glz_custom_fields_save", "article");\n\n  // YES, finally the default custom fields are replaced by the new, pimped ones : )\n  register_callback("glz_custom_fields_replace", "article");\n\n}\n\n// -------------------------------------------------------------\n// everything is happening in this function... generates the content for Extensions > Custom Fields\nfunction glz_custom_fields() {\n  global $event;\n\n  // set message to empty if no events will be present\n  $message = '''';\n\n  // let''s get the data we hold in txp_prefs table for custom fields\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n\n  // we have $_POST, let''s see if there is any CRUD\n  if ( $_POST ) {\n    $incoming = stripPost();\n    extract($incoming);\n\n    // create an empty $value if it''s not set in the $_POST\n    if ( !isset($value) ) $value = '''';\n\n    // we are deleting a new custom field\n    if ( gps(''delete'') ) {\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."txp_prefs");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."txp_lang");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n\n      glz_custom_fields_MySQL("delete", glz_custom_number($custom_set), PFX."textpattern");\n\n      $message = glz_custom_fields_gTxt("deleted", array(''{custom_set_name}'' => $custom_set_name));\n    }\n\n    // we are resetting one of the mighty 10\n    if ( gps(''reset'') ) {\n      glz_custom_fields_MySQL("reset", $custom_set, PFX."txp_prefs");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n\n      glz_custom_fields_MySQL("reset", glz_custom_number($custom_set), PFX."textpattern");\n\n      $message = glz_custom_fields_gTxt("reset", array(''{custom_set_name}'' => $custom_set_name));\n    }\n\n    // we are adding a new custom field\n    if ( gps("custom_field_number") ) {\n      $custom_set_name = gps("custom_set_name");\n\n      // if no name was specified, abort\n      if ( !$custom_set_name ) {\n        $message = glz_custom_fields_gTxt("no_name");\n      }\n      else {\n        $name_exists = glz_check_custom_set_name($arr_custom_fields, $custom_set_name);\n\n        // if name doesn''t exist\n        if ( $name_exists == FALSE ) {\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."txp_prefs", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''custom_set_type''   => $custom_set_type\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."txp_lang", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''lang''          => $GLOBALS[''prefs''][''language'']\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."textpattern", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''after''         => intval($custom_field_number-1)\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."custom_fields", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''value'' => $value\n          ));\n\n          $message = glz_custom_fields_gTxt("created", array(''{custom_set_name}'' => $custom_set_name));\n        }\n        // name exists, abort\n        else {\n          $message = glz_custom_fields_gTxt("exists", array(''{custom_set_name}'' => $custom_set_name));\n        }\n      }\n    }\n\n    // we are editing an existing custom field\n    if ( gps(''save'') ) {\n      if ( !empty($custom_set_name) ) {\n        $name_exists = glz_check_custom_set_name($arr_custom_fields, $custom_set_name, $custom_set);\n\n        // if name doesn''t exist\n        if ( $name_exists == FALSE ) {\n          glz_custom_fields_MySQL("update", $custom_set, PFX."txp_prefs", array(\n            ''custom_set_type''   => $custom_set_type,\n            ''custom_set_name''   => $custom_set_name\n          ));\n          glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."custom_fields", array(\n            ''custom_set''    => $custom_set,\n            ''value'' => $value\n          ));\n\n          $message = glz_custom_fields_gTxt("updated", array(''{custom_set_name}'' => $custom_set_name));\n        }\n        // name exists, abort\n        else {\n          $message = glz_custom_fields_gTxt("exists", array(''{custom_set_name}'' => $custom_set_name));\n        }\n      }\n      else {\n        $message = glz_custom_fields_gTxt(''no_name'');\n      }\n    }\n\n    // need to re-fetch data since things modified\n    $arr_custom_fields = glz_custom_fields_MySQL("all");\n\n  }\n\n  pagetop("Custom Fields", $message);\n\n  // the table with all custom fields follows\n  echo\n    n.''<table cellspacing="0" id="glz_custom_fields" class="stripeMe">''.n.\n    '' <thead>''.n.\n    ''   <tr>''.n.\n    ''     <td>Custom set</td>''.n.\n    ''     <td>Name</td>''.n.\n    ''     <td colspan="2">Type</td>''.n.\n    ''   </tr>''.n.\n    '' </thead>''.n.\n    '' <tbody>''.n;\n\n  // used a for so that we didn''t have to create a counter separately\n  for ( $i=0; $i < count($arr_custom_fields) ; $i++ ) {\n    extract($arr_custom_fields[$i]);\n\n    // first 10 fields cannot be deleted, just reset\n    if ( $i < 10 ) {\n      // can''t reset a custom field that is not set\n      $reset_delete = ( $custom_set_name ) ? glz_form_buttons("reset", "Reset", $custom_set, $custom_set_name, $custom_set_type, ''return confirm(\\''By proceeding you will RESET ALL data in `textpattern` and `custom_fields` tables for `''.$custom_set_name.''`. Are you sure?\\'');'') : NULL;\n    }\n    else {\n      $reset_delete = glz_form_buttons("delete", "Delete", $custom_set, $custom_set_name, $custom_set_type, ''return confirm(\\''By proceeding you will DELETE ALL data in `textpattern` and `custom_fields` tables for `''.$custom_set_name.''`. Are you sure?\\'');'');\n    }\n\n    echo\n    ''   <tr>''.n.\n    ''     <td class="custom_set">''.$custom_set.''</td>''.n.\n    ''     <td class="custom_set_name">''.$custom_set_name.''</td>''.n.\n    ''     <td class="type">''.glz_custom_fields_gTxt($custom_set_type).''</td>''.n.\n    ''     <td class="events">''.$reset_delete.sp.glz_form_buttons("edit", "Edit", $custom_set, htmlspecialchars($custom_set_name), $custom_set_type).''</td>''.n.\n    ''   </tr>''.n;\n  }\n\n  echo\n    '' </tbody>''.n.\n    ''</table>''.n;\n\n  // the form where custom fields are being added/edited\n  $legend = gps(''edit'') ?\n    ''Edit ''.gps(''custom_set'') :\n    ''Add new custom field'';\n\n  $custom_field = gps(''edit'') ?\n    ''<input name="custom_set" value="''.gps(''custom_set'').''" type="hidden" />'' :\n    ''<input name="custom_field_number" value="''.glz_custom_next($arr_custom_fields).''" type="hidden" />'';\n\n  $custom_set = gps(''edit'') ?\n    gps(''custom_set'') :\n    NULL;\n\n  $custom_name = gps(''edit'') ?\n    gps(''custom_set_name'') :\n    NULL;\n\n  $arr_custom_set_types = glz_custom_set_types();\n\n  $custom_set_types = NULL;\n  foreach ( $arr_custom_set_types as $custom_type ) {\n    $selected = ( gps(''edit'') && gps(''custom_set_type'') == $custom_type ) ? '' selected="selected"'' : NULL;\n    $custom_set_types .= ''<option value="''.$custom_type.''"''.$selected.''>''.glz_custom_fields_gTxt($custom_type).''</option>''.n;\n  }\n  // fetching the values for this custom field\n  if ( gps(''edit'') ) {\n    $arr_values = glz_custom_fields_MySQL("values", $custom_set, '''', array(''custom_set_name'' => $custom_field[''custom_set_name'']));\n    $values = ( $arr_values ) ? implode("\\r\\n", $arr_values) : '''';\n  }\n  else {\n    $values = '''';\n  }\n\n  $action = gps(''edit'') ?\n    ''<input name="save" value="Save" type="submit" class="publish" />'' :\n    ''<input name="add_new" value="Add new" type="submit" class="publish" />'';\n\n  // ok, all is set, let''s build the form\n  echo\n    ''<form method="post" action="index.php" id="add_edit_custom_field">''.n.\n    ''<input name="event" value="glz_custom_fields" type="hidden" />''.n.\n    $custom_field.n.\n    ''<fieldset>''.n.\n    '' <legend>''.$legend.''</legend>''.n.\n    '' <p>\n        <label for="custom_set_name">Name:</label>\n        <input name="custom_set_name" value="''.htmlspecialchars($custom_name).''" id="custom_set_name" />\n      </p>''.n.\n    '' <p>\n        <label for="custom_set_type">Type:</label>\n        <select name="custom_set_type" id="type">\n    ''.      $custom_set_types.''\n        </select>\n      </p>''.n.\n    '' <p>\n        <label for="value">Value:<br /><em>Each value on a separate line</em></label>\n        <textarea name="value" id="value">''.$values.''</textarea>\n      </p>''.n.\n    '' ''.$action.n.\n    ''</fieldset>''.n.\n    ''</form>''.n;\n\n}\n\n// -------------------------------------------------------------\n// replaces the default custom fields under write tab\nfunction glz_custom_fields_replace() {\n  // get all custom fields & keep only the ones which are set\n  $arr_custom_fields = array_filter(glz_custom_fields_MySQL("all"), "glz_check_custom_set");\n\n  // DEBUG\n  // dmp($arr_custom_fields);\n\n  if ( is_array($arr_custom_fields) && !empty($arr_custom_fields) ) {\n    // get all custom fields values for this article\n    $arr_article_customs = glz_custom_fields_MySQL("article_customs", glz_get_article_id(), '''', $arr_custom_fields);\n    if ( is_array($arr_article_customs) ) extract($arr_article_customs);\n\n    // let''s initialize our output\n    $out = NULL;\n\n    // let''s see which custom fields are set\n    foreach ( $arr_custom_fields as $custom_field ) {\n      // get all possible/default value(s) for this custom field from custom_fields table\n      $arr_custom_field_values = glz_custom_fields_MySQL("values", $custom_field[''custom_set''], '''', array(''custom_set_name'' => $custom_field[''custom_set_name'']));\n\n      // DEBUG\n      // dmp($arr_custom_field_values);\n\n      //custom_set without "_set" e.g. custom_1_set => custom_1\n      $custom_set = glz_custom_number($custom_field[''custom_set'']);\n\n      // if current article holds no value for this custom field, make it empty\n      $custom_value = ( !empty($$custom_set) ) ? $$custom_set : '''';\n\n      // the way our custom field value is going to look like\n      switch ( $custom_field[''custom_set_type''] ) {\n        case "text_input":\n          $custom_set_value = fInput("text", $custom_set, $custom_value, "edit", "", "", "22", "", $custom_set);\n          break;\n\n        case "select":\n          $custom_set_value = glz_selectInput($custom_set, $arr_custom_field_values, $custom_value, 1);\n          break;\n\n        case "multi-select":\n          $custom_set_value = glz_selectInput($custom_set, $arr_custom_field_values, $custom_value, '''', 1);\n          break;\n\n        case "checkbox":\n            $custom_set_value = glz_checkbox($custom_set, $arr_custom_field_values, $custom_value);\n          break;\n\n        case "radio":\n          $custom_set_value = glz_radio($custom_set, $arr_custom_field_values, $custom_value);\n          break;\n\n        // if none of the custom_set_types fit - WHICH HINTS TO A BUG - text input is default\n        default:\n          // $custom_set_value = fInput("text", $custom_set, $$custom_set, "edit", "", "", "22", "", $custom_set);\n          $custom_set_value = ''Type not supported yet.'';\n          break;\n      }\n\n      //\n      $out .= graf(\n        "<label for=\\"$custom_set\\">{$custom_field[''custom_set_name'']}</label><br />$custom_set_value", " class=\\"glz_custom_field\\""\n      );\n    }\n    // DEBUG\n    // dmp($out);\n\n    echo\n    ''<script type="text/javascript">\n    <!--//--><![CDATA[//><!--\n\n    $(document).ready(function() {\n      // removes all existing custom fields\n      $("p:has(label[for*=custom-])").remove();\n      $("p:has(label[@for=override-form])").after(\\''''.$out.''\\'');\n    });\n    //--><!]]>\n    </script>'';\n  }\n}\n\n// -------------------------------------------------------------\n// prep our custom fields for the db (watch out for checkboxes, they might have multiple values)\nfunction glz_custom_fields_before_save() {\n  // keep only the custom fields\n  foreach ($_POST as $key => $value) {\n    //check for custom fields with multiple values e.g. arrays\n    if ( strstr($key, ''custom_'') && is_array($value) ) {\n      $value = implode($value, ''|'');\n      // feed our custom fields back into the $_POST\n      $_POST[$key] = $value;\n    }\n  }\n  // DEBUG\n  // dmp($_POST);\n}\n\n// -------------------------------------------------------------\n// save our pimped custom fields (the ones above 10)\nfunction glz_custom_fields_save() {\n  $ID = glz_get_article_id();\n  if ( $ID ) {\n    //initialize $set\n    $set = '''';\n    // see whether we have custom fields > 10\n    foreach ($_POST as $key => $value) {\n      if (strstr($key, ''custom_'')) {\n        list($rubbish, $digit) = explode("_", $key);\n        // keep only the values that are above 10\n        if ( $digit > 10 ) $set[] = "`$key`=''$value''";\n      }\n    }\n    // anything worthy saving?\n    if ( is_array($set) ) {\n      // DEBUG\n      // dmp($set);\n      // ok, update the custom fields\n      safe_update("textpattern", implode($set, '',''), "`ID`=''$ID''");\n    }\n  }\n}\n\n\n\n###################\n##### HELPERS #####\n###################\n\n// -------------------------------------------------------------\n// edit/delete buttons in custom_fields table require a form each\nfunction glz_form_buttons($action, $value, $custom_set, $custom_set_name, $custom_set_type, $onsubmit='''') {\n  $onsubmit = ($onsubmit) ? ''onsubmit="''.$onsubmit.''"'' : '''';\n\n  return\n    ''<form method="post" action="index.php" ''.$onsubmit.''>\n      <input name="custom_set" value="''.$custom_set.''" type="hidden" />\n      <input name="custom_set_name" value="''.$custom_set_name.''" type="hidden" />\n      <input name="custom_set_type" value="''.$custom_set_type.''" type="hidden" />\n      <input name="event" value="glz_custom_fields" type="hidden" />\n      <input name="''.$action.''" value="''.$value.''" type="submit" />\n    </form>'';\n}\n\n// -------------------------------------------------------------\n// the types our custom fields can take\nfunction glz_custom_set_types() {\n  return array(\n    ''text_input'',\n    ''select'',\n    ''multi-select'',\n//    ''textarea'', // not too practical, might need to disable altogether\n    ''checkbox'',\n    ''radio''\n  );\n}\n\n// -------------------------------------------------------------\n// outputs only custom fields that have been set, i.e. have a name assigned to them\nfunction glz_check_custom_set($var) {\n  return !empty($var[''custom_set_name'']);\n}\n\n// -------------------------------------------------------------\n// custom_set without "_set" e.g. custom_1_set => custom_1\nfunction glz_custom_number($custom_set) {\n  return substr($custom_set, 0, -4);\n}\n\n// -------------------------------------------------------------\n// custom_set digit e.g. custom_1_set => 1\nfunction glz_custom_digit($custom_set) {\n  $out = explode("_", $custom_set);\n  // $out[0] will always be custom\n  return $out[1];\n}\n\n// -------------------------------------------------------------\n// removes empty values from arrays - used for new custom fields\nfunction glz_arr_empty_values($value) {\n  if ( !empty($value) ) return $value;\n}\n\n// -------------------------------------------------------------\n// returns the custom set from a custom set name e.g. "Rating" gives us custom_1_set\nfunction glz_get_custom_set($value) {\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n  // go through all custom fields and see if the one we''re looking for exists\n  foreach ( $arr_custom_fields as $custom_field ) {\n    if ( $custom_field[''custom_set_name''] == $value ) {\n      return $custom_field[''custom_set''];\n    }\n  }\n  // if it doesn''t return error message\n  trigger_error(glz_custom_fields_gTxt(''doesnt_exist'', array(''{custom_set_name}'' => $value)));\n}\n\n// -------------------------------------------------------------\n// get the article ID, EVEN IF it''s newly saved\nfunction glz_get_article_id() {\n  return ( !empty($GLOBALS[''ID'']) ? $GLOBALS[''ID''] : gps(''ID'') );\n}\n\n// -------------------------------------------------------------\n// helps with range formatting - just DRY\nfunction glz_format_ranges($arr_values, $custom_set_name) {\n  //initialize $out\n  $out = '''';\n  foreach ( $arr_values as $key => $value ) {\n    $key = $value;\n    $out[$key] = ( strstr($custom_set_name, ''range'') ) ? glz_custom_fields_range($value, $custom_set_name) : $value;\n  }\n  return $out;\n}\n\n// -------------------------------------------------------------\n// acts as a callback for the above function\nfunction glz_custom_fields_range($custom_value, $custom_set_name) {\n  // last part of the string will be the range unit (e.g. $, &pound;, m<sup>3</sup> etc.)\n  $nomenclature = array_pop(explode('' '', $custom_set_name));\n\n  // see whether range unit should go after\n  if ( strstr($nomenclature, ''(after)'') ) {\n    // trim ''(after)'' from the measuring unit\n    $nomenclature = substr($nomenclature, 0, -7);\n    $after = 1;\n  }\n\n  // check whether it''s a range or single value\n  $arr_value = explode(''-'', $custom_value);\n  if ( is_array($arr_value) ) {\n    // initialize $out\n    $out = '''';\n    foreach ( $arr_value as $value ) {\n      // check whether nomenclature goes before or after\n      $out[] = ( !isset($after) ) ? $nomenclature.number_format($value) : number_format($value).$nomenclature;\n    }\n    return implode(''-'',$out);\n  }\n  // our range is a single value\n  else {\n    // check whether nomenclature goes before or after\n    return ( !isset($after) ) ? $nomenclature.number_format($value) : number_format($value).$nomenclature;\n  }\n}\n\n// -------------------------------------------------------------\n// returns the next available number for custom field\nfunction glz_custom_next($arr_custom_fields) {\n  // our custom field cannot be < 10\n  for ( $i=9; $i < count($arr_custom_fields)+1; $i++ ) {\n    if ( !isset($arr_custom_fields[$i]) ) {\n      return intval($i+1);\n    }\n\n    extract($arr_custom_fields[$i]);\n    $arr_custom = explode(''_'', $custom_set);\n\n    if ( intval($i+1) < intval($arr_custom[''1'']) ) {\n      return intval($i+1);\n    }\n  }\n}\n\n// -------------------------------------------------------------\n// checks if the custom field name isn''t already taken\nfunction glz_check_custom_set_name($arr_custom_fields, $custom_set_name, $custom_set='''') {\n  foreach ( $arr_custom_fields as $arr_custom_set ) {\n    if ( ($custom_set_name == $arr_custom_set[''custom_set_name'']) && (!empty($custom_set) && $custom_set != $arr_custom_set[''custom_set'']) ) {\n      return TRUE;\n    }\n  }\n\n  return FALSE;\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default selectInput() because trimming \\t and \\n didn''t work + some other mods\nfunction glz_selectInput($name = '''', $arr_values = '''', $custom_value = '''', $blank_first = '''', $multi = '''') {\n  $out = array();\n\n  foreach ($arr_values as $key => $value) {\n    $selected = glz_selected_checked(''selected'', $key, $custom_value);\n    $out[] = "<option value=\\"$key\\"{$selected}>$value</option>";\n  }\n\n  // we''ll need the extra attributes as well as a name that will produce an array\n  /*\n    TODO Make this user-configurable\n  */\n  if ($multi) {\n    $multi = '' multiple="multiple" size="3"'';\n    $name .= "[]";\n  }\n\n  return "<select id=\\"$name\\" name=\\"$name\\" class=\\"list\\"$multi>".\n    ($blank_first ? "<option value=\\"\\"$selected></option>" : '''').\n    ( $out ? join('''', $out) : '''').\n    "</select>";\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default checkbox() to keep the looping in here and check against existing value/s\nfunction glz_checkbox($name = '''', $arr_values = '''', $custom_value = '''') {\n  $out = array();\n\n  foreach ( $arr_values as $key => $value ) {\n    $checked = glz_selected_checked(''checked'', $key, $custom_value);\n\n    $value = htmlspecialchars($value);\n    $out[] = "<input type=\\"checkbox\\" name=\\"{$name}[]\\" value=\\"$key\\" class=\\"checkbox\\" id=\\"$key\\"{$checked} /><label for=\\"$key\\">$value</label><br />";\n  }\n\n  return join('''', $out);\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default radio() to keep the looping in here and check against existing value\n/*\n  TODO How do we reset radio fields?\n*/\nfunction glz_radio($name = '''', $arr_values = '''', $custom_value = '''') {\n  $out = array();\n\n  foreach ( $arr_values as $key => $value ) {\n    $checked = glz_selected_checked(''checked'', $key, $custom_value);\n\n    $value = htmlspecialchars($value);\n    $out[] = "<input type=\\"radio\\" name=\\"$name\\" value=\\"$key\\" class=\\"radio\\" id=\\"$name-$key\\"{$checked} /><label for=\\"$name-$key\\">$value</label><br />";\n  }\n\n  return join('''', $out);\n}\n\n\n// -------------------------------------------------------------\n// checking if this custom field has selected or checked values\nfunction glz_selected_checked($nomenclature, $value, $custom_value = '''') {\n  // make an array if $custom_value contains multiple values\n  if ( strstr($custom_value, ''|'') ) $arr_custom_value = explode(''|'', $custom_value);\n\n  if ( isset($arr_custom_value) ) {\n    $out = ( in_array($value, $arr_custom_value) ) ? " $nomenclature=\\"$nomenclature\\"" : "";\n  }\n  else {\n    $out = ($value == $custom_value) ? " $nomenclature=\\"$nomenclature\\"" : "";\n  }\n\n  return $out;\n}\n\n// -------------------------------------------------------------\n// PHP4 doesn''t come with array_combine... Thank you redbot!\nfunction php4_array_combine($keys, $values) {\n  $result = array(); // initializing the array\n\n  foreach ( array_map(null, $keys, $values) as $pair ) {\n    $result[$pair[0]] = $pair[1];\n  }\n\n  return $result;\n}\n\n// -------------------------------------------------------------\n// messages that will be available throughout this plugin\nfunction glz_custom_fields_gTxt($get, $atts = array()) {\n  $lang = array(\n    ''no_name''         => ''Ooops! <strong>custom set</strong> must have a name'',\n    ''deleted''         => ''<strong>{custom_set_name}</strong> was deleted'',\n    ''reset''           => ''<strong>{custom_set_name}</strong> was reset'',\n    ''created''         => ''<strong>{custom_set_name}</strong> was created'',\n    ''updated''         => ''<strong>{custom_set_name}</strong> was updated'',\n    ''exists''          => ''Ooops! <strong>{custom_set_name}</strong> already exists'',\n    ''doesnt_exist''    => ''Ooops! <strong>{custom_set_name}</strong> is not set'',\n    ''text_input''      => ''Text Input'',\n    ''select''          => ''Select'',\n    ''multi-select''    => ''Multi-Select'',\n    ''textarea''        => ''Textarea'',\n    ''checkbox''        => ''Checkbox'',\n    ''radio''           => ''Radio'',\n    ''no_do''           => ''Ooops! No action specified for method, abort.'',\n    ''not_specified''   => ''Ooops! {what} is not specified'',\n    ''searchby_not_set'' => ''<strong>searcby</strong> cannot be left blank'',\n    ''jquery_missing''  => ''Upgrade TXP to at least 4.0.5 or put <strong>jquery.js</strong> in your /textpattern folder. <a href="http://jquery.com" title="jQuery website">jQuery website</a>''\n  );\n\n  $out = ( strstr($lang[$get], "Ooops!") ) ? // Ooops! would appear 0 in the string...\n      "<span class=\\"red\\">{$lang[$get]}</span>" :\n      $lang[$get];\n\n  return strtr($out, $atts);\n}\n\n\n\n#################\n##### MYSQL #####\n#################\n\nfunction glz_custom_fields_MySQL($do, $name='''', $table='''', $extra='''') {\n  if ( !empty($do) ) {\n    switch ( $do ) {\n      case ''all'':\n        return glz_all_custom_fields();\n        break;\n\n      case ''values'':\n        return glz_values_custom_field($name, $extra);\n        break;\n\n      case ''all_values'' :\n        return glz_all_existing_custom_values($name, $extra);\n        break;\n\n      case ''article_customs'':\n        return glz_article_custom_fields($name, $extra);\n        break;\n\n      case ''next_custom'':\n         return glz_next_empty_custom();\n        break;\n\n      case ''new'':\n        return glz_new_custom_field($name, $table, $extra);\n        break;\n\n      case ''update'':\n        return glz_update_custom_field($name, $table, $extra);\n        break;\n\n      case ''reset'':\n        glz_reset_custom_field($name, $table);\n        break;\n\n      case ''delete'':\n        glz_delete_custom_field($name, $table);\n        break;\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''no_do''));\n  }\n}\n\nfunction glz_all_custom_fields() {\n  $arr_custom_fields = getRows("\n    SELECT\n      `name` AS custom_set,\n      `val` AS custom_set_name,\n      `html` AS custom_set_type\n    FROM\n      `".PFX."txp_prefs`\n    WHERE\n      `event`=''custom''\n    ORDER BY\n      `position`\n  ");\n  return $arr_custom_fields;\n}\n\nfunction glz_values_custom_field($name, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( !empty($name) ) {\n      $arr_values = getThings("\n        SELECT\n          `value`\n        FROM\n          `".PFX."custom_fields`\n        WHERE\n          `name` = ''{$name}''\n        ORDER BY\n          `value`\n      ");\n\n      // have our values nicely sorted\n      natsort($arr_values);\n\n      // if this is a range, format ranges accordingly\n      return glz_format_ranges($arr_values, $custom_set_name);\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_all_existing_custom_values($name, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( !empty($name) ) {\n      $arr_values = getThings("\n        SELECT DISTINCT\n          `$name`\n        FROM\n          `".PFX."textpattern`\n        WHERE\n          `Status` = ''4''\n        AND\n          `$name` <> ''''\n        ORDER BY\n          `$name`\n        ");\n\n      // have our values nicely sorted\n      natsort($arr_values);\n\n      // prepare our array for checking\n      $values_check = implode(''::'', $arr_values);\n\n      // check if some of the values are multiple ones\n      if ( strstr($values_check, ''|'') ) {\n        // initialize $out\n        $out = array();\n        // put all values in an array\n        foreach ( $arr_values as $value ) {\n          $arr_values = explode(''|'', $value);\n          $out = array_merge($out, $arr_values);\n        }\n        // keep only the unique ones\n        $out = array_unique($out);\n        // keys and values need to be the same\n        return php4_array_combine($out, $out);\n      }\n      // check if this is a range\n      else if ( strstr($values_check, ''-'') && strstr($custom_set_name, ''range'') ) {\n        // keys won''t have the unit ($, £, m<sup>3</sup>, etc.) values will\n        return glz_format_ranges($arr_values, $custom_set_name);\n      }\n      else {\n        // keys and values need to be the same\n        return php4_array_combine($arr_values, $arr_values);\n      }\n\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_article_custom_fields($name, $extra) {\n  if ( is_array($extra) ) {\n    // see what custom fields we need to query for\n    foreach ( $extra as $custom_set ) {\n      $select[] = glz_custom_number($custom_set[''custom_set'']);\n    }\n    // prepare the select elements\n    $select = implode('','', $select);\n\n    $arr_article_customs = getRow("\n      SELECT\n        $select\n      FROM\n        `".PFX."textpattern`\n      WHERE\n        `ID`=''$name''\n    ");\n\n    return $arr_article_customs;\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_next_empty_custom() {\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n  foreach ( $arr_custom_fields as $custom ) {\n    if ( empty($custom[''custom_set_name'']) ) {\n      return $custom[''custom_set''];\n    }\n  }\n}\n\nfunction glz_new_custom_field($name, $table, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n    // DRYing up, we''ll be using this variable quiet often\n    $custom_set = ( isset($custom_field_number) ) ? "custom_{$custom_field_number}_set" : $custom_set;\n\n    if ( ($table == PFX."txp_prefs")  ) {\n      $query = "\n        INSERT INTO\n          `".PFX."txp_prefs` (`prefs_id`,`name`,`val`,`type`,`event`,`html`,`position`)\n        VALUES\n          (''1'',''{$custom_set}'',''{$name}'',''1'',''custom'',''{$custom_set_type}'',{$custom_field_number})\n      ";\n    }\n    else if ( $table == PFX."txp_lang" ) {\n      $query = "\n        INSERT INTO\n          `".PFX."txp_lang` (`id`,`lang`,`name`,`event`,`data`,`lastmod`)\n        VALUES\n          ('''',''{$lang}'',''{$custom_set}'',''prefs'',''{$name}'',now())\n      ";\n    }\n    else if ( $table == PFX."textpattern" ) {\n      $query = "\n        ALTER TABLE\n          `".PFX."textpattern`\n        ADD\n          `custom_{$custom_field_number}` varchar(255) NOT NULL DEFAULT ''''\n        AFTER\n          `custom_{$after}`\n      ";\n    }\n    else if ( $table == PFX."custom_fields" ) {\n      $arr_values = array_filter(explode("\\r\\n", $value), ''glz_arr_empty_values'');\n      if ( is_array($arr_values) && !empty($arr_values) ) {\n        // initialize null\n        $insert = '''';\n        foreach ( $arr_values as $value ) {\n          // don''t insert empty values\n          if ( !empty($value) ) {\n            // if this is the last value, query will have to be different\n            $insert .= ( end($arr_values) != $value ) ? "(''{$custom_set}'',''{$value}'')," : "(''{$custom_set}'',''{$value}'')";\n          }\n        }\n        $query = "\n          INSERT INTO\n            `".PFX."custom_fields` (`name`,`value`)\n          VALUES\n            {$insert}\n        ";\n      }\n    }\n    if ( isset($query) && !empty($query) ) safe_query($query);\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_update_custom_field($name, $table, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( ($table == PFX."txp_prefs")  ) {\n      $query = "\n        UPDATE\n          `".PFX."txp_prefs`\n        SET\n          `val` = ''{$custom_set_name}'',\n          `html` = ''{$custom_set_type}''\n        WHERE\n          `name`=''{$name}''\n      ";\n    }\n\n    safe_query($query);\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_reset_custom_field($name, $table) {\n  if ( $table == PFX."txp_prefs" ) {\n    $query = "\n      UPDATE\n        `".PFX."txp_prefs`\n      SET\n        `val` = '''',\n        `html` = ''text_input''\n      WHERE\n        `name`=''{$name}''\n    ";\n  }\n  else if ( $table == PFX."textpattern" ) {\n    $query = "\n      UPDATE\n        `".PFX."textpattern`\n      SET\n        `{$name}` = ''''\n    ";\n  }\n\n  safe_query($query);\n}\n\nfunction glz_delete_custom_field($name, $table) {\n  // remember, custom fields under 10 MUST NOT be deleted\n  if ( glz_custom_digit($name) > 10 ) {\n    if ( in_array($table, array(PFX."txp_prefs", PFX."txp_lang", PFX."custom_fields")) ) {\n      $query = "\n        DELETE FROM\n          `{$table}`\n        WHERE\n          `name`=''{$name}''\n      ";\n    }\n    else if ( $table == PFX."textpattern" ) {\n      $query = "\n        ALTER TABLE\n          `".PFX."textpattern`\n        DROP\n          `{$name}`\n      ";\n    }\n    safe_query($query);\n  }\n  else {\n    if ( $table == PFX."txp_prefs" ) {\n      glz_custom_fields_MySQL("reset", $name, $table);\n    }\n    else if ( ($table == PFX."custom_fields") ) {\n      $query = "\n        DELETE FROM\n          `{$table}`\n        WHERE\n          `name`=''{$name}''\n      ";\n      safe_query($query);\n    }\n  }\n}\n\n// -------------------------------------------------------------\n// we might have multiple values for custom fields, like must use grep % %\nfunction glz_buildCustomSql($custom,$pairs) {\n  if ($pairs) {\n    $pairs = doSlash($pairs);\n    foreach( $pairs as $k => $v ) {\n      if ( in_array(strtolower($k),$custom) ) {\n        $no = array_keys($custom,strtolower($k));\n        $arr_values = explode(",",$v);\n        if ( is_array($arr_values) ) {\n          if ( count($arr_values) > 1 ) {\n            $custom_query = "AND (custom_{$no[0]} LIKE ''%{$arr_values[0]}%''";\n            for ( $i=1; $i < count($arr_values); $i++ ) {\n              $custom_query .= " OR custom_{$no[0]} LIKE ''%{$arr_values[$i]}%''";\n            }\n            $custom_query .= ")";\n            $out[] = $custom_query;\n          }\n          else {\n            $out[] = "AND custom_{$no[0]} LIKE ''%{$arr_values[0]}%''";\n          }\n        }\n      }\n    }\n  }\n  return (!empty($out)) ? '' ''.join('' '',$out).'' '' : FALSE;\n}\n\n\n\n#####################\n##### FRONT-END #####\n#####################\n\n// -------------------------------------------------------------\n/**\n * DROP-DOWN SEARCH FORM\n Call it like this: <txp:glz_custom_fields_search_form results_page="listings" searchby="Area,City,Price" />\n */\nfunction glz_custom_fields_search_form($atts) {\n  // DEBUG\n  // dmp($GLOBALS[''prefs'']);\n\n  extract(lAtts(array(\n    ''results_page''  => "default",\n    ''searchby''    => "",\n  ),$atts));\n\n  if ( !empty($searchby) ) {\n    // see which customs are searchby values associated to\n    if ( strstr($searchby, ",") ) {\n      // create an array from the searchby values\n      foreach ( explode(",", $searchby) as $custom ) {\n        // get the corresponding custom fields for the searchby values\n        $arr_custom[$custom] = array_search($custom, $GLOBALS[''prefs'']);\n      }\n    }\n    else {\n      // get the custom field for the searchby value\n      $arr_custom[$searchby] = array_search($searchby, $GLOBALS[''prefs'']);\n    }\n    // DEBUG\n    // dmp($arr_custom);\n\n    // start our form\n    $out[] = "<form method=\\"post\\" action=\\"".hu."$results_page\\">".n\n         .hInput("glz_custom_fields_search", 1).n;\n\n    // build our selects\n    foreach ( $arr_custom as $name => $custom ) {\n      // get all existing custom values for live articles\n      $arr_values = glz_custom_fields_MySQL(''all_values'', glz_custom_number($custom), '''', array(''custom_set_name'' => $name));\n\n      if ( is_array($arr_values) ) {\n        // trim ''_set'' from $custom\n        $custom = substr($custom, 0, -4);\n\n        // we are ready to start building the select\n        //"<label for=\\"$custom\\">$name</label>".n.\n        $out[] = "<select name=\\"$custom\\">".n;\n\n        // first value is select name\n        $out[] = "  <option value=\\"$custom\\">Select $name</option>".n;\n        // now let''s go through the values in $arr_values\n        foreach ( $arr_values as $key => $value ) {\n          $out[] = "  <option value=\\"$key\\">$value</option>".n;\n        }\n\n        // end this select\n        $out[] = "</select>".n;\n      }\n      else {\n        return ''<p>No articles with custom fields have been found.</p></form>'';\n      }\n    }\n\n    // end our form\n    $out[] = fInput("submit", "submit", "Submit").n\n         ."</form>".n.n;\n    // DEBUG\n    // dmp(join($out));\n\n    return join($out);\n  }\n  else {\n    return trigger_error(glz_custom_fields_gTxt(''searchby_not_set''));;\n  }\n}\n\n// -------------------------------------------------------------\n// Slightly altered getCustomFields (that silly 10 hard limit...). Internal, no need to call it.\nfunction glz_getCustomFields() {\n  global $prefs;\n  $out = array();\n  $i = 0;\n  $custom_fields_num = count(preg_grep("(^custom_\\d+_set$)", array_keys($prefs)));\n  while ($i < $custom_fields_num) {\n    $i++;\n    if (!empty($prefs[''custom_''.$i.''_set''])) {\n      $out[$i] = strtolower($prefs[''custom_''.$i.''_set'']);\n    }\n  }\n  return $out;\n}\n\n// -------------------------------------------------------------\n// Slightly altered custom_field - multiple values custom fields\nfunction glz_custom_field($atts) {\n  global $thisarticle, $prefs;\n  assert_article();\n\n  extract(lAtts(array(\n    ''name'' => @$prefs[''custom_1_set''],\n    ''escape'' => '''',\n    ''default'' => '''',\n  ),$atts));\n\n  $name = strtolower($name);\n  if (!empty($thisarticle[$name])) {\n    // if this custom field contains |, we need to compare against all the values\n    if ( strstr($thisarticle[$name], ''|'') ) {\n      /*\n        TODO Using a loop here because later on I might want to output a list of values in a specific format (wrap, break etc.)\n      */\n      foreach ( explode(''|'', $thisarticle[$name]) as $value ) {\n        $arr_values[] = $value;\n      }\n      $out = join(", ", $arr_values);\n    }\n    else {\n      $out = $thisarticle[$name];\n    }\n  }\n  else\n    $out = $default;\n\n  return ($escape == ''html'' ? escape_output($out) : $out);\n}\n\n// -------------------------------------------------------------\n// Slightly altered if_custom_field to deal with custom fields > 10 & multiple values\nfunction glz_if_custom_field($atts, $thing) {\n  global $thisarticle, $prefs;\n  assert_article();\n  extract(lAtts(array(\n    ''name'' => @$prefs[''custom_1_set''],\n    ''val'' => NULL,\n  ),$atts));\n  $name = strtolower($name);\n\n  if ($val !== NULL) {\n    $cond = (@$thisarticle[$name] == $val);\n    // if this custom field contains |, we need to compare against all set values\n    if ( strstr($thisarticle[$name], ''|'') ) {\n      foreach ( explode(''|'', $thisarticle[$name]) as $value ) {\n        if ( $val == $value ) {\n          $cond = TRUE;\n          break;\n        }\n      }\n    }\n  }\n  else\n    $cond = !empty($thisarticle[$name]);\n\n  return parse(EvalElse($thing, $cond));\n}\n\n// -------------------------------------------------------------\n/**\n * DEALS WITH ARTICLES - PROPERTIES - LISTS, SLIGHTLY ALTERED TO USE OUR CUSTOM SEARCH VALUES WHEN FILTERING & DISPLAY CUSTOM FIELDS > 10\nCall it like you would article()\nhttp://textpattern.net/wiki/index.php?title=Txp:article_/\n */\nfunction glz_article($atts) {\n  global $is_article_body, $has_article_tag;\n  if ($is_article_body) {\n    trigger_error(gTxt(''article_tag_illegal_body''));\n    return '''';\n  }\n  $has_article_tag = true;\n  return glz_parseArticles($atts);\n}\n\n// -------------------------------------------------------------\n/**\n * DEALS WITH ARTICLES - PROPERTIES - LISTS, SLIGHTLY ALTERED TO USE OUR CUSTOM SEARCH VALUES WHEN FILTERING & DISPLAY CUSTOM FIELDS > 10\nCall it like you would article_cutom()\nhttp://textpattern.net/wiki/index.php?title=Txp:article_custom/\n */\nfunction glz_article_custom($atts) {\n  return glz_parseArticles($atts, ''1'');\n}\n\n// -------------------------------------------------------------\n// this decides what needs to be called, doArticle or doArticles. Using this to call our modified glz_doArticles & glz_doArticle\nfunction glz_parseArticles($atts, $iscustom = '''') {\n  global $pretext, $is_article_list;\n  $old_ial = $is_article_list;\n  $is_article_list = ($pretext[''id''] && !$iscustom)? false : true;\n  article_push();\n  $r = ($is_article_list) ? glz_doArticles($atts, $iscustom) : glz_doArticle($atts);\n  article_pop();\n  $is_article_list = $old_ial;\n\n  return $r;\n}\n\n\n// -------------------------------------------------------------\n// Slightly altered populateArticleData - needed to call glz_getCustomFields instead\nfunction glz_populateArticleData($rs) {\n  extract($rs);\n\n  trace_add("[".gTxt(''Article'')." $ID]");\n  $out[''thisid'']          = $ID;\n  $out[''posted'']          = $uPosted;\n  $out[''modified'']        = $LastMod;\n  $out[''annotate'']        = $Annotate;\n  $out[''comments_invite''] = $AnnotateInvite;\n  $out[''authorid'']        = $AuthorID;\n  $out[''title'']           = $Title;\n  $out[''url_title'']       = $url_title;\n  $out[''category1'']       = $Category1;\n  $out[''category2'']       = $Category2;\n  $out[''section'']         = $Section;\n  $out[''keywords'']        = $Keywords;\n  $out[''article_image'']   = $Image;\n  $out[''comments_count'']  = $comments_count;\n  $out[''body'']            = $Body_html;\n  $out[''excerpt'']         = $Excerpt_html;\n  $out[''override_form'']   = $override_form;\n  $out[''status'']          = $Status;\n\n  $custom = glz_getCustomFields();\n  if ($custom) {\n    foreach ($custom as $i => $name)\n      $out[$name] = $rs[''custom_'' . $i];\n  }\n\n  global $thisarticle;\n  $thisarticle = $out;\n}\n\n// -------------------------------------------------------------\n// using this to "inject" our custom search values\nfunction glz_doArticle($atts) {\n  global $pretext, $prefs, $thisarticle;\n  extract($prefs);\n  extract($pretext);\n\n  extract(gpsa(array(''parentid'', ''preview'')));\n\n  extract(lAtts(array(\n    ''allowoverride'' => ''1'',\n    ''form''          => ''default'',\n    ''status''        => ''4'',\n  ),$atts, 0));\n\n  if ($status and !is_numeric($status))\n  {\n    $status = getStatusNum($status);\n  }\n\n  if (empty($thisarticle) or $thisarticle[''thisid''] != $id)\n  {\n    $thisarticle = NULL;\n\n    $q_status = ($status ? ''and Status = ''.intval($status) : ''and Status in (4,5)'');\n\n    $rs = safe_row("*, unix_timestamp(Posted) as uPosted",\n        "textpattern", ''ID = ''.intval($id)." $q_status limit 1");\n\n    if ($rs) {\n      extract($rs);\n      glz_populateArticleData($rs);\n    }\n  }\n\n  if (!empty($thisarticle) and $thisarticle[''status''] == $status)\n  {\n    extract($thisarticle);\n    $thisarticle[''is_first''] = 1;\n    $thisarticle[''is_last''] = 1;\n\n    $form = ($allowoverride and $override_form) ? $override_form : $form;\n\n    $article = parse_form($form);\n\n    if ($use_comments and $comments_auto_append)\n    {\n      $article .= parse_form(''comments_display'');\n    }\n\n    unset($GLOBALS[''thisarticle'']);\n\n    return $article;\n  }\n}\n\n// -------------------------------------------------------------\n// using this to "inject" our custom search values\nfunction glz_doArticles($atts, $iscustom) {\n  global $pretext, $prefs, $txpcfg;\n  extract($pretext);\n  extract($prefs);\n  $customFields = glz_getCustomFields();\n  $customlAtts = array_null(array_flip($customFields));\n\n  //getting attributes\n  $theAtts = lAtts(array(\n    ''form''      => ''default'',\n    ''listform''  => '''',\n    ''searchform''=> '''',\n    ''limit''     => 10,\n    ''pageby''    => '''',\n    ''category''  => '''',\n    ''section''   => '''',\n    ''excerpted'' => '''',\n    ''author''    => '''',\n    ''sort''      => '''',\n    ''sortby''    => '''',\n    ''sortdir''   => '''',\n    ''month''     => '''',\n    ''keywords''  => '''',\n    ''frontpage'' => '''',\n    ''id''        => '''',\n    ''time''      => ''past'',\n    ''status''    => ''4'',\n    ''pgonly''    => 0,\n    ''searchall'' => 1,\n    ''searchsticky'' => 0,\n    ''allowoverride'' => (!$q and !$iscustom),\n    ''offset''    => 0,\n    ''no_results'' => ''no_results''\n  )+$customlAtts,$atts);\n\n  // if an article ID is specified, treat it as a custom list\n  $iscustom = (!empty($theAtts[''id''])) ? true : $iscustom;\n\n  //for the txp:article tag, some attributes are taken from globals;\n  //override them before extract\n  if (!$iscustom) {\n    if ($c && !empty($theAtts[''category''])) $theAtts[''category''] = $c;\n    $theAtts[''section''] = ($s && $s!=''default'')? $s : '''';\n    $theAtts[''author''] = (!empty($author)? $author: '''');\n    $theAtts[''month''] = (!empty($month)? $month: '''');\n    $theAtts[''frontpage''] = ($s && $s==''default'')? true: false;\n    $theAtts[''excerpted''] = '''';\n  }\n  extract($theAtts);\n\n  $pageby = (empty($pageby) ? $limit : $pageby);\n\n  // treat sticky articles differently wrt search filtering, etc\n  if (!is_numeric($status))\n    $status = getStatusNum($status);\n  $issticky = ($status == 5);\n\n  //give control to search, if necesary\n  if($q && !$iscustom && !$issticky)\n  {\n    include_once txpath.''/publish/search.php'';\n\n    $s_filter = ($searchall ? filterSearch() : '''');\n    $q        = doSlash($q);\n    $match    = ", match (Title,Body) against (''$q'') as score";\n    $search   = " and (Title rlike ''$q'' or Body rlike ''$q'') $s_filter";\n\n    // searchall=0 can be used to show search results for the current section only\n    if ($searchall) $section = '''';\n    if (!$sort) $sort=''score desc'';\n  }\n  else {\n    $match = $search = '''';\n    if (!$sort) $sort=''Posted desc'';\n  }\n\n  // for backwards compatibility\n  // sortby and sortdir are deprecated\n  if ($sortby) {\n    if (!$sortdir) {\n      $sortdir = ''desc'';\n    }\n    $sort = "$sortby $sortdir";\n  }\n\n  elseif ($sortdir) {\n    $sort = "Posted $sortdir";\n  }\n\n  //Building query parts\n  $frontpage = ($frontpage and (!$q or $issticky)) ? filterFrontPage() : '''';\n  $category  = join("'',''", doSlash(do_list($category)));\n  $category  = (!$category)  ? '''' : " and (Category1 IN (''".$category."'') or Category2 IN (''".$category."''))";\n  $section   = (!$section)   ? '''' : " and Section IN (''".join("'',''", doSlash(do_list($section)))."'')";\n  $excerpted = ($excerpted==''y'')  ? " and Excerpt !=''''" : '''';\n  $author    = (!$author)    ? '''' : " and AuthorID IN (''".join("'',''", doSlash(do_list($author)))."'')";\n  $month     = (!$month)     ? '''' : " and Posted like ''".doSlash($month)."%''";\n  $id        = (!$id)        ? '''' : " and ID = ''".intval($id)."''";\n  switch ($time) {\n    case ''any'':\n      $time = ""; break;\n    case ''future'':\n      $time = " and Posted > now()"; break;\n    default:\n      $time = " and Posted <= now()";\n  }\n  if (!is_numeric($status))\n    $status = getStatusNum($status);\n\n  $custom = '''';\n\n  // DEBUG\n  // dmp($customFields);\n  if ($customFields) {\n    foreach($customFields as $cField) {\n      if (isset($atts[$cField]))\n        $customPairs[$cField] = $atts[$cField];\n    }\n\n    $arr_post_custom = preg_grep("(^custom_\\d+$)", array_keys($_POST));\n    // DEBUG\n    // dmp($arr_post_custom);\n    // dmp($_POST);\n    foreach ( $arr_post_custom as $custom ) {\n      // skip if values are the same - users have left the default\n      if ( ps($custom) == $custom ) continue;\n      // if there is a value, add it for the query\n      if ( ps($custom) ) {\n        // need the digit to find out the custom field name\n        list($rubbish, $digit) = explode("_", $custom);\n        $customPairs[$prefs[''custom_''.$digit.''_set'']] = ps($custom);\n      }\n    }\n    // DEBUG\n    // dmp($customPairs);\n\n    if(!empty($customPairs))\n      $custom =  glz_buildCustomSql($customFields,$customPairs);\n  }\n\n  //Allow keywords for no-custom articles. That tagging mode, you know\n  if ($keywords) {\n    $keys = doSlash(do_list($keywords));\n    foreach ($keys as $key) {\n      $keyparts[] = "FIND_IN_SET(''".$key."'',Keywords)";\n    }\n    $keywords = " and (" . join('' or '',$keyparts) . ")";\n  }\n\n  if ($q and $searchsticky)\n    $statusq = '' and Status >= 4'';\n  elseif ($id)\n    $statusq = '' and Status >= 4'';\n  else\n    $statusq = '' and Status = ''.intval($status);\n\n  $where = "1=1" . $statusq. $time.\n    $search . $id . $category . $section . $excerpted . $month . $author . $keywords . $custom . $frontpage;\n  // DEBUG\n  // dmp($where);\n\n  //do not paginate if we are on a custom list\n  if (!$iscustom and !$issticky) {\n    $grand_total = safe_count(''textpattern'',$where);\n    $total = $grand_total - $offset;\n    $numPages = ceil($total/$pageby);\n    $pg = (!$pg) ? 1 : $pg;\n    $pgoffset = $offset + (($pg - 1) * $pageby);\n    // send paging info to txp:newer and txp:older\n    $pageout[''pg'']       = $pg;\n    $pageout[''numPages''] = $numPages;\n    $pageout[''s'']        = $s;\n    $pageout[''c'']        = $c;\n    $pageout[''grand_total''] = $grand_total;\n    $pageout[''total'']    = $total;\n\n    global $thispage;\n    if (empty($thispage))\n      $thispage = $pageout;\n    if ($pgonly)\n      return;\n  } else {\n    $pgoffset = $offset;\n  }\n\n  $rs = safe_rows_start("*, unix_timestamp(Posted) as uPosted".$match, ''textpattern'',\n  $where.'' order by ''.doSlash($sort).'' limit ''.intval($pgoffset).'', ''.intval($limit));\n  // get the form name\n  if ($q and !$iscustom and !$issticky)\n    $fname = ($searchform ? $searchform : ''search_results'');\n  else\n    $fname = ($listform ? $listform : $form);\n\n  if ($rs) {\n    $count = 0;\n\n    $articles = array();\n    while ( $a = nextRow($rs) ) {\n      ++$count;\n\n      glz_populateArticleData($a);\n      global $thisarticle, $uPosted, $limit;\n\n      $thisarticle[''is_first''] = ($count == 1);\n      $thisarticle[''is_last''] = ($count == numRows($rs));\n\n      if (@constant(''txpinterface'') === ''admin'' and gps(''Form'')) {\n        $articles[] = parse(gps(''Form''));\n      }\n      elseif ($allowoverride and $a[''override_form'']) {\n        $articles[] = parse_form($a[''override_form'']);\n      }\n      else {\n        $articles[] = parse_form($fname);\n      }\n\n      // sending these to paging_link(); Required?\n      $uPosted = $a[''uPosted''];\n      $limit = $limit;\n\n      unset($GLOBALS[''thisarticle'']);\n\n    }\n    if ( count($articles) == "0" ) {\n      header(''Location: ''.hu.$no_results);\n    }\n    else {\n      return join('''',$articles);\n    }\n  }\n}\n\n\n\n##########################\n##### PRE-REQUISITES #####\n##########################\n\n// -------------------------------------------------------------\n// checks if custom_fields table exists\nfunction glz_custom_fields_install() {\n  // if jQuery is not present, die\n  // improvement courtesy of Sam Weiss\n  if ( !file_exists($GLOBALS[''txpcfg''][''txpath''].''/jquery.js'') ) die(glz_custom_fields_gTxt(''jquery_missing''));\n\n  // if we don''t have the custom_fields table, let''s create it\n  // let''s also take all custom field values from `textpattern` table and populates the `custom_fields` table\n  if ( !getRows("SHOW TABLES LIKE ''".PFX."custom_fields''") ) {\n    safe_query("\n      CREATE TABLE `".PFX."custom_fields` (\n        `name` varchar(255) NOT NULL default '''',\n        `value` varchar(255) NOT NULL default '''',\n        INDEX (`name`)\n      ) TYPE=MyISAM\n    ");\n    /*\n      TODO Migration/importing functions\n    */\n  }\n}\n\n// -------------------------------------------------------------\n// uninstalls glz_custom_fields by doing the following things:\n// 1. removes all custom fields above 10 in txp_prefs\n// 2. changes the remaining custom fields back to input\n// 3. removes custom_fields table\nfunction glz_custom_fields_uninstall() {\n  /*\n    TODO Uninstall functions\n  */\n}\n\n// -------------------------------------------------------------\n// adds the css & js we need\nfunction glz_custom_fields_css_js($buffer) {\n  $css =<<<css\n<style type="text/css" media="screen">\n    /* - - - - - - - - - - - - - - - - - - - - -\n\n    ### TEXTPATTERN CUSTOM FIELDS ###\n\n    Title : glz_custom_fields stylesheet\n    Author : Gerhard Lazu\n    URL : http://www.gerhardlazu.com/ & http://www.calti.co.uk\n\n    Created : 14th May 2007\n    Last modified: 7th January 2008\n\n    - - - - - - - - - - - - - - - - - - - - - */\n\n\n    /* CLASSES\n    -------------------------------------------------------------- */\n    .green {\n      color: #6B3;\n    }\n    .red {\n      color: #C00;\n    }\n\n\n    /* TABLE\n    -------------------------------------------------------------- */\n    #glz_custom_fields {\n      width: 50em;\n      margin: 0 auto;\n      border: 1px solid #DDD;\n    }\n\n    #glz_custom_fields thead tr {\n      font-size: 1.2em;\n      font-weight: 700;\n      background: #EEE;\n    }\n\n    #glz_custom_fields tbody tr.alt {\n      background: #FFC;\n    }\n    #glz_custom_fields tbody tr.over {\n      background: #FF6;\n    }\n\n    #glz_custom_fields td {\n      padding: 0.2em 1em;\n      vertical-align: middle;\n    }\n    #glz_custom_fields thead td {\n      padding: 0.3em 0.8em;\n    }\n    #glz_custom_fields td.custom_set {\n      width: 8em;\n    }\n    #glz_custom_fields td.custom_set_name {\n      width: 18em;\n    }\n    #glz_custom_fields td.type {\n      width: 6em;\n    }\n    #glz_custom_fields td.events {\n      width: 12em;\n      text-align: right;\n    }\n\n\n    /* FORMS\n    -------------------------------------------------------------- */\n    input:focus,\n    select:focus,\n    textarea:focus {\n      background: #FFC;\n    }\n\n    #glz_custom_fields td.events form {\n      display: inline;\n    }\n\n    #add_edit_custom_field {\n      width: 50em;\n      margin: 2em auto 0 auto;\n    }\n    #add_edit_custom_field legend {\n      font-size: 1.2em;\n      font-weight: 700;\n    }\n    #add_edit_custom_field label {\n      float: left;\n      width: 10em;\n      font-weight: 700;\n    }\n    #add_edit_custom_field p input,\n    #add_edit_custom_field p select {\n      width: 20em;\n    }\n    #add_edit_custom_field p textarea {\n      width: 30em;\n      height: 10em;\n    }\n    #add_edit_custom_field p em {\n      font-size: 0.9em;\n      font-weight: 500;\n      color: #777;\n    }\n    #add_edit_custom_field input.publish {\n      margin-left: 11em;\n    }\n\n    /* select on write tab for the custom fields */\n    td#article-col-1 #advanced p select.list {\n      width: 100%;\n    }\n\n  </style>\ncss;\n\n  $js =<<<js\n<script type="text/javascript">\n  <!--//--><![CDATA[//><!--\n\n  $(document).ready(function() {\n    // sweet jQuery table striping\n    $(".stripeMe tr").mouseover(function() { $(this).addClass("over"); }).mouseout(function() { $(this).removeClass("over"); });\n    $(".stripeMe tr:even").addClass("alt");\n\n    // disable all custom field references in Advanced Prefs\n    var custom_field_tr = $("tr:has(label[for*=custom_]), tr:has(h2:contains(''Custom Fields''))");\n    if ( custom_field_tr ) {\n      $.each (custom_field_tr, function() {\n        $(this).hide();\n      });\n    };\n\n    // toggle custom field value based on its type\n    if ( $("select#type option[@selected]").attr("value") == "text_input" ) {\n      custom_field_value_off();\n    };\n\n    $("select#type").click( function() {\n      if ( $("select#type option[@selected]").attr("value") != "text_input" && !$("textarea#value").length ) {\n        custom_field_value_on();\n      }\n      else if ( $("select#type option[@selected]").attr("value") == "text_input" && !$("input#value").length ) {\n        custom_field_value_off();\n      }\n    });\n\n\n    // ### RE-USABLE FUNCTIONS ###\n\n    function custom_field_value_off() {\n      $("label[@for=value] em").hide();\n      $("textarea#value").remove();\n      $("label[@for=value]").after(''<input id="value" value="no value allowed" name="value"/>'');\n      $("input#value").attr("disabled", "disabled");\n    }\n\n    function custom_field_value_on() {\n      $("label[@for=value] em").show();\n      $("input#value").remove();\n      $("label[@for=value]").after(''<textarea id="value" name="value"></textarea>'');\n    }\n\n  });\n\n  //--><!]]>\n  </script>\njs;\n\n  $find = ''</head>'';\n  $replace = $js.n.t.$css.n.t.$find;\n\n  return str_replace($find, $replace, $buffer);\n}\n', '//<?php\n/**\nglz_custom_fields plugin, FROM UK WITH LOVE\n\n@author Gerhard Lazu\n@version 1.1.3\n@copyright Gerhard Lazu, 3 March, 2008\n@package TXP 4.0.6 (2805)\n@contributors:  Sam Weiss, redbot, Manfre, Vladimir\n*/\n\n// checks if all tables exists and everything is setup properly\nglz_custom_fields_install();\n\nif (txpinterface == "admin") {\n  // we need some stylesheets & JS\n  ob_start("glz_custom_fields_css_js");\n\n  // Custom Fields tab under Extensions\n  add_privs(''glz_custom_fields'', ''1'');\n  register_tab("extensions", "glz_custom_fields", "Custom Fields");\n  register_callback("glz_custom_fields", "glz_custom_fields");\n\n  // we need to make sure that all custom field values will be converted to strings first - think checkboxes & multi-selects\n  if ( (gps("step") == "edit") || (gps("step") == "create") ) register_callback("glz_custom_fields_before_save", "article", '''', 1);\n  // if we edit or create a new article, save our extra custom fields (> 10)\n  if ( (gps("step") == "edit") || (gps("step") == "create") ) register_callback("glz_custom_fields_save", "article");\n\n  // YES, finally the default custom fields are replaced by the new, pimped ones : )\n  register_callback("glz_custom_fields_replace", "article");\n\n}\n\n// -------------------------------------------------------------\n// everything is happening in this function... generates the content for Extensions > Custom Fields\nfunction glz_custom_fields() {\n  global $event;\n\n  // set message to empty if no events will be present\n  $message = '''';\n\n  // let''s get the data we hold in txp_prefs table for custom fields\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n\n  // we have $_POST, let''s see if there is any CRUD\n  if ( $_POST ) {\n    $incoming = stripPost();\n    extract($incoming);\n\n    // create an empty $value if it''s not set in the $_POST\n    if ( !isset($value) ) $value = '''';\n\n    // we are deleting a new custom field\n    if ( gps(''delete'') ) {\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."txp_prefs");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."txp_lang");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n\n      glz_custom_fields_MySQL("delete", glz_custom_number($custom_set), PFX."textpattern");\n\n      $message = glz_custom_fields_gTxt("deleted", array(''{custom_set_name}'' => $custom_set_name));\n    }\n\n    // we are resetting one of the mighty 10\n    if ( gps(''reset'') ) {\n      glz_custom_fields_MySQL("reset", $custom_set, PFX."txp_prefs");\n      glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n\n      glz_custom_fields_MySQL("reset", glz_custom_number($custom_set), PFX."textpattern");\n\n      $message = glz_custom_fields_gTxt("reset", array(''{custom_set_name}'' => $custom_set_name));\n    }\n\n    // we are adding a new custom field\n    if ( gps("custom_field_number") ) {\n      $custom_set_name = gps("custom_set_name");\n\n      // if no name was specified, abort\n      if ( !$custom_set_name ) {\n        $message = glz_custom_fields_gTxt("no_name");\n      }\n      else {\n        $name_exists = glz_check_custom_set_name($arr_custom_fields, $custom_set_name);\n\n        // if name doesn''t exist\n        if ( $name_exists == FALSE ) {\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."txp_prefs", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''custom_set_type''   => $custom_set_type\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."txp_lang", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''lang''          => $GLOBALS[''prefs''][''language'']\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."textpattern", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''after''         => intval($custom_field_number-1)\n          ));\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."custom_fields", array(\n            ''custom_field_number'' => $custom_field_number,\n            ''value'' => $value\n          ));\n\n          $message = glz_custom_fields_gTxt("created", array(''{custom_set_name}'' => $custom_set_name));\n        }\n        // name exists, abort\n        else {\n          $message = glz_custom_fields_gTxt("exists", array(''{custom_set_name}'' => $custom_set_name));\n        }\n      }\n    }\n\n    // we are editing an existing custom field\n    if ( gps(''save'') ) {\n      if ( !empty($custom_set_name) ) {\n        $name_exists = glz_check_custom_set_name($arr_custom_fields, $custom_set_name, $custom_set);\n\n        // if name doesn''t exist\n        if ( $name_exists == FALSE ) {\n          glz_custom_fields_MySQL("update", $custom_set, PFX."txp_prefs", array(\n            ''custom_set_type''   => $custom_set_type,\n            ''custom_set_name''   => $custom_set_name\n          ));\n          glz_custom_fields_MySQL("delete", $custom_set, PFX."custom_fields");\n          glz_custom_fields_MySQL("new", $custom_set_name, PFX."custom_fields", array(\n            ''custom_set''    => $custom_set,\n            ''value'' => $value\n          ));\n\n          $message = glz_custom_fields_gTxt("updated", array(''{custom_set_name}'' => $custom_set_name));\n        }\n        // name exists, abort\n        else {\n          $message = glz_custom_fields_gTxt("exists", array(''{custom_set_name}'' => $custom_set_name));\n        }\n      }\n      else {\n        $message = glz_custom_fields_gTxt(''no_name'');\n      }\n    }\n\n    // need to re-fetch data since things modified\n    $arr_custom_fields = glz_custom_fields_MySQL("all");\n\n  }\n\n  pagetop("Custom Fields", $message);\n\n  // the table with all custom fields follows\n  echo\n    n.''<table cellspacing="0" id="glz_custom_fields" class="stripeMe">''.n.\n    '' <thead>''.n.\n    ''   <tr>''.n.\n    ''     <td>Custom set</td>''.n.\n    ''     <td>Name</td>''.n.\n    ''     <td colspan="2">Type</td>''.n.\n    ''   </tr>''.n.\n    '' </thead>''.n.\n    '' <tbody>''.n;\n\n  // used a for so that we didn''t have to create a counter separately\n  for ( $i=0; $i < count($arr_custom_fields) ; $i++ ) {\n    extract($arr_custom_fields[$i]);\n\n    // first 10 fields cannot be deleted, just reset\n    if ( $i < 10 ) {\n      // can''t reset a custom field that is not set\n      $reset_delete = ( $custom_set_name ) ? glz_form_buttons("reset", "Reset", $custom_set, $custom_set_name, $custom_set_type, ''return confirm(\\''By proceeding you will RESET ALL data in `textpattern` and `custom_fields` tables for `''.$custom_set_name.''`. Are you sure?\\'');'') : NULL;\n    }\n    else {\n      $reset_delete = glz_form_buttons("delete", "Delete", $custom_set, $custom_set_name, $custom_set_type, ''return confirm(\\''By proceeding you will DELETE ALL data in `textpattern` and `custom_fields` tables for `''.$custom_set_name.''`. Are you sure?\\'');'');\n    }\n\n    echo\n    ''   <tr>''.n.\n    ''     <td class="custom_set">''.$custom_set.''</td>''.n.\n    ''     <td class="custom_set_name">''.$custom_set_name.''</td>''.n.\n    ''     <td class="type">''.glz_custom_fields_gTxt($custom_set_type).''</td>''.n.\n    ''     <td class="events">''.$reset_delete.sp.glz_form_buttons("edit", "Edit", $custom_set, htmlspecialchars($custom_set_name), $custom_set_type).''</td>''.n.\n    ''   </tr>''.n;\n  }\n\n  echo\n    '' </tbody>''.n.\n    ''</table>''.n;\n\n  // the form where custom fields are being added/edited\n  $legend = gps(''edit'') ?\n    ''Edit ''.gps(''custom_set'') :\n    ''Add new custom field'';\n\n  $custom_field = gps(''edit'') ?\n    ''<input name="custom_set" value="''.gps(''custom_set'').''" type="hidden" />'' :\n    ''<input name="custom_field_number" value="''.glz_custom_next($arr_custom_fields).''" type="hidden" />'';\n\n  $custom_set = gps(''edit'') ?\n    gps(''custom_set'') :\n    NULL;\n\n  $custom_name = gps(''edit'') ?\n    gps(''custom_set_name'') :\n    NULL;\n\n  $arr_custom_set_types = glz_custom_set_types();\n\n  $custom_set_types = NULL;\n  foreach ( $arr_custom_set_types as $custom_type ) {\n    $selected = ( gps(''edit'') && gps(''custom_set_type'') == $custom_type ) ? '' selected="selected"'' : NULL;\n    $custom_set_types .= ''<option value="''.$custom_type.''"''.$selected.''>''.glz_custom_fields_gTxt($custom_type).''</option>''.n;\n  }\n  // fetching the values for this custom field\n  if ( gps(''edit'') ) {\n    $arr_values = glz_custom_fields_MySQL("values", $custom_set, '''', array(''custom_set_name'' => $custom_field[''custom_set_name'']));\n    $values = ( $arr_values ) ? implode("\\r\\n", $arr_values) : '''';\n  }\n  else {\n    $values = '''';\n  }\n\n  $action = gps(''edit'') ?\n    ''<input name="save" value="Save" type="submit" class="publish" />'' :\n    ''<input name="add_new" value="Add new" type="submit" class="publish" />'';\n\n  // ok, all is set, let''s build the form\n  echo\n    ''<form method="post" action="index.php" id="add_edit_custom_field">''.n.\n    ''<input name="event" value="glz_custom_fields" type="hidden" />''.n.\n    $custom_field.n.\n    ''<fieldset>''.n.\n    '' <legend>''.$legend.''</legend>''.n.\n    '' <p>\n        <label for="custom_set_name">Name:</label>\n        <input name="custom_set_name" value="''.htmlspecialchars($custom_name).''" id="custom_set_name" />\n      </p>''.n.\n    '' <p>\n        <label for="custom_set_type">Type:</label>\n        <select name="custom_set_type" id="type">\n    ''.      $custom_set_types.''\n        </select>\n      </p>''.n.\n    '' <p>\n        <label for="value">Value:<br /><em>Each value on a separate line</em></label>\n        <textarea name="value" id="value">''.$values.''</textarea>\n      </p>''.n.\n    '' ''.$action.n.\n    ''</fieldset>''.n.\n    ''</form>''.n;\n\n}\n\n// -------------------------------------------------------------\n// replaces the default custom fields under write tab\nfunction glz_custom_fields_replace() {\n  // get all custom fields & keep only the ones which are set\n  $arr_custom_fields = array_filter(glz_custom_fields_MySQL("all"), "glz_check_custom_set");\n\n  // DEBUG\n  // dmp($arr_custom_fields);\n\n  if ( is_array($arr_custom_fields) && !empty($arr_custom_fields) ) {\n    // get all custom fields values for this article\n    $arr_article_customs = glz_custom_fields_MySQL("article_customs", glz_get_article_id(), '''', $arr_custom_fields);\n    if ( is_array($arr_article_customs) ) extract($arr_article_customs);\n\n    // let''s initialize our output\n    $out = NULL;\n\n    // let''s see which custom fields are set\n    foreach ( $arr_custom_fields as $custom_field ) {\n      // get all possible/default value(s) for this custom field from custom_fields table\n      $arr_custom_field_values = glz_custom_fields_MySQL("values", $custom_field[''custom_set''], '''', array(''custom_set_name'' => $custom_field[''custom_set_name'']));\n\n      // DEBUG\n      // dmp($arr_custom_field_values);\n\n      //custom_set without "_set" e.g. custom_1_set => custom_1\n      $custom_set = glz_custom_number($custom_field[''custom_set'']);\n\n      // if current article holds no value for this custom field, make it empty\n      $custom_value = ( !empty($$custom_set) ) ? $$custom_set : '''';\n\n      // the way our custom field value is going to look like\n      switch ( $custom_field[''custom_set_type''] ) {\n        case "text_input":\n          $custom_set_value = fInput("text", $custom_set, $custom_value, "edit", "", "", "22", "", $custom_set);\n          break;\n\n        case "select":\n          $custom_set_value = glz_selectInput($custom_set, $arr_custom_field_values, $custom_value, 1);\n          break;\n\n        case "multi-select":\n          $custom_set_value = glz_selectInput($custom_set, $arr_custom_field_values, $custom_value, '''', 1);\n          break;\n\n        case "checkbox":\n            $custom_set_value = glz_checkbox($custom_set, $arr_custom_field_values, $custom_value);\n          break;\n\n        case "radio":\n          $custom_set_value = glz_radio($custom_set, $arr_custom_field_values, $custom_value);\n          break;\n\n        // if none of the custom_set_types fit - WHICH HINTS TO A BUG - text input is default\n        default:\n          // $custom_set_value = fInput("text", $custom_set, $$custom_set, "edit", "", "", "22", "", $custom_set);\n          $custom_set_value = ''Type not supported yet.'';\n          break;\n      }\n\n      //\n      $out .= graf(\n        "<label for=\\"$custom_set\\">{$custom_field[''custom_set_name'']}</label><br />$custom_set_value", " class=\\"glz_custom_field\\""\n      );\n    }\n    // DEBUG\n    // dmp($out);\n\n    echo\n    ''<script type="text/javascript">\n    <!--//--><![CDATA[//><!--\n\n    $(document).ready(function() {\n      // removes all existing custom fields\n      $("p:has(label[for*=custom-])").remove();\n      $("p:has(label[@for=override-form])").after(\\''''.$out.''\\'');\n    });\n    //--><!]]>\n    </script>'';\n  }\n}\n\n// -------------------------------------------------------------\n// prep our custom fields for the db (watch out for checkboxes, they might have multiple values)\nfunction glz_custom_fields_before_save() {\n  // keep only the custom fields\n  foreach ($_POST as $key => $value) {\n    //check for custom fields with multiple values e.g. arrays\n    if ( strstr($key, ''custom_'') && is_array($value) ) {\n      $value = implode($value, ''|'');\n      // feed our custom fields back into the $_POST\n      $_POST[$key] = $value;\n    }\n  }\n  // DEBUG\n  // dmp($_POST);\n}\n\n// -------------------------------------------------------------\n// save our pimped custom fields (the ones above 10)\nfunction glz_custom_fields_save() {\n  $ID = glz_get_article_id();\n  if ( $ID ) {\n    //initialize $set\n    $set = '''';\n    // see whether we have custom fields > 10\n    foreach ($_POST as $key => $value) {\n      if (strstr($key, ''custom_'')) {\n        list($rubbish, $digit) = explode("_", $key);\n        // keep only the values that are above 10\n        if ( $digit > 10 ) $set[] = "`$key`=''$value''";\n      }\n    }\n    // anything worthy saving?\n    if ( is_array($set) ) {\n      // DEBUG\n      // dmp($set);\n      // ok, update the custom fields\n      safe_update("textpattern", implode($set, '',''), "`ID`=''$ID''");\n    }\n  }\n}\n\n\n\n###################\n##### HELPERS #####\n###################\n\n// -------------------------------------------------------------\n// edit/delete buttons in custom_fields table require a form each\nfunction glz_form_buttons($action, $value, $custom_set, $custom_set_name, $custom_set_type, $onsubmit='''') {\n  $onsubmit = ($onsubmit) ? ''onsubmit="''.$onsubmit.''"'' : '''';\n\n  return\n    ''<form method="post" action="index.php" ''.$onsubmit.''>\n      <input name="custom_set" value="''.$custom_set.''" type="hidden" />\n      <input name="custom_set_name" value="''.$custom_set_name.''" type="hidden" />\n      <input name="custom_set_type" value="''.$custom_set_type.''" type="hidden" />\n      <input name="event" value="glz_custom_fields" type="hidden" />\n      <input name="''.$action.''" value="''.$value.''" type="submit" />\n    </form>'';\n}\n\n// -------------------------------------------------------------\n// the types our custom fields can take\nfunction glz_custom_set_types() {\n  return array(\n    ''text_input'',\n    ''select'',\n    ''multi-select'',\n//    ''textarea'', // not too practical, might need to disable altogether\n    ''checkbox'',\n    ''radio''\n  );\n}\n\n// -------------------------------------------------------------\n// outputs only custom fields that have been set, i.e. have a name assigned to them\nfunction glz_check_custom_set($var) {\n  return !empty($var[''custom_set_name'']);\n}\n\n// -------------------------------------------------------------\n// custom_set without "_set" e.g. custom_1_set => custom_1\nfunction glz_custom_number($custom_set) {\n  return substr($custom_set, 0, -4);\n}\n\n// -------------------------------------------------------------\n// custom_set digit e.g. custom_1_set => 1\nfunction glz_custom_digit($custom_set) {\n  $out = explode("_", $custom_set);\n  // $out[0] will always be custom\n  return $out[1];\n}\n\n// -------------------------------------------------------------\n// removes empty values from arrays - used for new custom fields\nfunction glz_arr_empty_values($value) {\n  if ( !empty($value) ) return $value;\n}\n\n// -------------------------------------------------------------\n// returns the custom set from a custom set name e.g. "Rating" gives us custom_1_set\nfunction glz_get_custom_set($value) {\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n  // go through all custom fields and see if the one we''re looking for exists\n  foreach ( $arr_custom_fields as $custom_field ) {\n    if ( $custom_field[''custom_set_name''] == $value ) {\n      return $custom_field[''custom_set''];\n    }\n  }\n  // if it doesn''t return error message\n  trigger_error(glz_custom_fields_gTxt(''doesnt_exist'', array(''{custom_set_name}'' => $value)));\n}\n\n// -------------------------------------------------------------\n// get the article ID, EVEN IF it''s newly saved\nfunction glz_get_article_id() {\n  return ( !empty($GLOBALS[''ID'']) ? $GLOBALS[''ID''] : gps(''ID'') );\n}\n\n// -------------------------------------------------------------\n// helps with range formatting - just DRY\nfunction glz_format_ranges($arr_values, $custom_set_name) {\n  //initialize $out\n  $out = '''';\n  foreach ( $arr_values as $key => $value ) {\n    $key = $value;\n    $out[$key] = ( strstr($custom_set_name, ''range'') ) ? glz_custom_fields_range($value, $custom_set_name) : $value;\n  }\n  return $out;\n}\n\n// -------------------------------------------------------------\n// acts as a callback for the above function\nfunction glz_custom_fields_range($custom_value, $custom_set_name) {\n  // last part of the string will be the range unit (e.g. $, &pound;, m<sup>3</sup> etc.)\n  $nomenclature = array_pop(explode('' '', $custom_set_name));\n\n  // see whether range unit should go after\n  if ( strstr($nomenclature, ''(after)'') ) {\n    // trim ''(after)'' from the measuring unit\n    $nomenclature = substr($nomenclature, 0, -7);\n    $after = 1;\n  }\n\n  // check whether it''s a range or single value\n  $arr_value = explode(''-'', $custom_value);\n  if ( is_array($arr_value) ) {\n    // initialize $out\n    $out = '''';\n    foreach ( $arr_value as $value ) {\n      // check whether nomenclature goes before or after\n      $out[] = ( !isset($after) ) ? $nomenclature.number_format($value) : number_format($value).$nomenclature;\n    }\n    return implode(''-'',$out);\n  }\n  // our range is a single value\n  else {\n    // check whether nomenclature goes before or after\n    return ( !isset($after) ) ? $nomenclature.number_format($value) : number_format($value).$nomenclature;\n  }\n}\n\n// -------------------------------------------------------------\n// returns the next available number for custom field\nfunction glz_custom_next($arr_custom_fields) {\n  // our custom field cannot be < 10\n  for ( $i=9; $i < count($arr_custom_fields)+1; $i++ ) {\n    if ( !isset($arr_custom_fields[$i]) ) {\n      return intval($i+1);\n    }\n\n    extract($arr_custom_fields[$i]);\n    $arr_custom = explode(''_'', $custom_set);\n\n    if ( intval($i+1) < intval($arr_custom[''1'']) ) {\n      return intval($i+1);\n    }\n  }\n}\n\n// -------------------------------------------------------------\n// checks if the custom field name isn''t already taken\nfunction glz_check_custom_set_name($arr_custom_fields, $custom_set_name, $custom_set='''') {\n  foreach ( $arr_custom_fields as $arr_custom_set ) {\n    if ( ($custom_set_name == $arr_custom_set[''custom_set_name'']) && (!empty($custom_set) && $custom_set != $arr_custom_set[''custom_set'']) ) {\n      return TRUE;\n    }\n  }\n\n  return FALSE;\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default selectInput() because trimming \\t and \\n didn''t work + some other mods\nfunction glz_selectInput($name = '''', $arr_values = '''', $custom_value = '''', $blank_first = '''', $multi = '''') {\n  $out = array();\n\n  foreach ($arr_values as $key => $value) {\n    $selected = glz_selected_checked(''selected'', $key, $custom_value);\n    $out[] = "<option value=\\"$key\\"{$selected}>$value</option>";\n  }\n\n  // we''ll need the extra attributes as well as a name that will produce an array\n  /*\n    TODO Make this user-configurable\n  */\n  if ($multi) {\n    $multi = '' multiple="multiple" size="3"'';\n    $name .= "[]";\n  }\n\n  return "<select id=\\"$name\\" name=\\"$name\\" class=\\"list\\"$multi>".\n    ($blank_first ? "<option value=\\"\\"$selected></option>" : '''').\n    ( $out ? join('''', $out) : '''').\n    "</select>";\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default checkbox() to keep the looping in here and check against existing value/s\nfunction glz_checkbox($name = '''', $arr_values = '''', $custom_value = '''') {\n  $out = array();\n\n  foreach ( $arr_values as $key => $value ) {\n    $checked = glz_selected_checked(''checked'', $key, $custom_value);\n\n    $value = htmlspecialchars($value);\n    $out[] = "<input type=\\"checkbox\\" name=\\"{$name}[]\\" value=\\"$key\\" class=\\"checkbox\\" id=\\"$key\\"{$checked} /><label for=\\"$key\\">$value</label><br />";\n  }\n\n  return join('''', $out);\n}\n\n// -------------------------------------------------------------\n// had to duplicate the default radio() to keep the looping in here and check against existing value\n/*\n  TODO How do we reset radio fields?\n*/\nfunction glz_radio($name = '''', $arr_values = '''', $custom_value = '''') {\n  $out = array();\n\n  foreach ( $arr_values as $key => $value ) {\n    $checked = glz_selected_checked(''checked'', $key, $custom_value);\n\n    $value = htmlspecialchars($value);\n    $out[] = "<input type=\\"radio\\" name=\\"$name\\" value=\\"$key\\" class=\\"radio\\" id=\\"$name-$key\\"{$checked} /><label for=\\"$name-$key\\">$value</label><br />";\n  }\n\n  return join('''', $out);\n}\n\n\n// -------------------------------------------------------------\n// checking if this custom field has selected or checked values\nfunction glz_selected_checked($nomenclature, $value, $custom_value = '''') {\n  // make an array if $custom_value contains multiple values\n  if ( strstr($custom_value, ''|'') ) $arr_custom_value = explode(''|'', $custom_value);\n\n  if ( isset($arr_custom_value) ) {\n    $out = ( in_array($value, $arr_custom_value) ) ? " $nomenclature=\\"$nomenclature\\"" : "";\n  }\n  else {\n    $out = ($value == $custom_value) ? " $nomenclature=\\"$nomenclature\\"" : "";\n  }\n\n  return $out;\n}\n\n// -------------------------------------------------------------\n// PHP4 doesn''t come with array_combine... Thank you redbot!\nfunction php4_array_combine($keys, $values) {\n  $result = array(); // initializing the array\n\n  foreach ( array_map(null, $keys, $values) as $pair ) {\n    $result[$pair[0]] = $pair[1];\n  }\n\n  return $result;\n}\n\n// -------------------------------------------------------------\n// messages that will be available throughout this plugin\nfunction glz_custom_fields_gTxt($get, $atts = array()) {\n  $lang = array(\n    ''no_name''         => ''Ooops! <strong>custom set</strong> must have a name'',\n    ''deleted''         => ''<strong>{custom_set_name}</strong> was deleted'',\n    ''reset''           => ''<strong>{custom_set_name}</strong> was reset'',\n    ''created''         => ''<strong>{custom_set_name}</strong> was created'',\n    ''updated''         => ''<strong>{custom_set_name}</strong> was updated'',\n    ''exists''          => ''Ooops! <strong>{custom_set_name}</strong> already exists'',\n    ''doesnt_exist''    => ''Ooops! <strong>{custom_set_name}</strong> is not set'',\n    ''text_input''      => ''Text Input'',\n    ''select''          => ''Select'',\n    ''multi-select''    => ''Multi-Select'',\n    ''textarea''        => ''Textarea'',\n    ''checkbox''        => ''Checkbox'',\n    ''radio''           => ''Radio'',\n    ''no_do''           => ''Ooops! No action specified for method, abort.'',\n    ''not_specified''   => ''Ooops! {what} is not specified'',\n    ''searchby_not_set'' => ''<strong>searcby</strong> cannot be left blank'',\n    ''jquery_missing''  => ''Upgrade TXP to at least 4.0.5 or put <strong>jquery.js</strong> in your /textpattern folder. <a href="http://jquery.com" title="jQuery website">jQuery website</a>''\n  );\n\n  $out = ( strstr($lang[$get], "Ooops!") ) ? // Ooops! would appear 0 in the string...\n      "<span class=\\"red\\">{$lang[$get]}</span>" :\n      $lang[$get];\n\n  return strtr($out, $atts);\n}\n\n\n\n#################\n##### MYSQL #####\n#################\n\nfunction glz_custom_fields_MySQL($do, $name='''', $table='''', $extra='''') {\n  if ( !empty($do) ) {\n    switch ( $do ) {\n      case ''all'':\n        return glz_all_custom_fields();\n        break;\n\n      case ''values'':\n        return glz_values_custom_field($name, $extra);\n        break;\n\n      case ''all_values'' :\n        return glz_all_existing_custom_values($name, $extra);\n        break;\n\n      case ''article_customs'':\n        return glz_article_custom_fields($name, $extra);\n        break;\n\n      case ''next_custom'':\n         return glz_next_empty_custom();\n        break;\n\n      case ''new'':\n        return glz_new_custom_field($name, $table, $extra);\n        break;\n\n      case ''update'':\n        return glz_update_custom_field($name, $table, $extra);\n        break;\n\n      case ''reset'':\n        glz_reset_custom_field($name, $table);\n        break;\n\n      case ''delete'':\n        glz_delete_custom_field($name, $table);\n        break;\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''no_do''));\n  }\n}\n\nfunction glz_all_custom_fields() {\n  $arr_custom_fields = getRows("\n    SELECT\n      `name` AS custom_set,\n      `val` AS custom_set_name,\n      `html` AS custom_set_type\n    FROM\n      `".PFX."txp_prefs`\n    WHERE\n      `event`=''custom''\n    ORDER BY\n      `position`\n  ");\n  return $arr_custom_fields;\n}\n\nfunction glz_values_custom_field($name, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( !empty($name) ) {\n      $arr_values = getThings("\n        SELECT\n          `value`\n        FROM\n          `".PFX."custom_fields`\n        WHERE\n          `name` = ''{$name}''\n        ORDER BY\n          `value`\n      ");\n\n      // have our values nicely sorted\n      natsort($arr_values);\n\n      // if this is a range, format ranges accordingly\n      return glz_format_ranges($arr_values, $custom_set_name);\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_all_existing_custom_values($name, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( !empty($name) ) {\n      $arr_values = getThings("\n        SELECT DISTINCT\n          `$name`\n        FROM\n          `".PFX."textpattern`\n        WHERE\n          `Status` = ''4''\n        AND\n          `$name` <> ''''\n        ORDER BY\n          `$name`\n        ");\n\n      // have our values nicely sorted\n      natsort($arr_values);\n\n      // prepare our array for checking\n      $values_check = implode(''::'', $arr_values);\n\n      // check if some of the values are multiple ones\n      if ( strstr($values_check, ''|'') ) {\n        // initialize $out\n        $out = array();\n        // put all values in an array\n        foreach ( $arr_values as $value ) {\n          $arr_values = explode(''|'', $value);\n          $out = array_merge($out, $arr_values);\n        }\n        // keep only the unique ones\n        $out = array_unique($out);\n        // keys and values need to be the same\n        return php4_array_combine($out, $out);\n      }\n      // check if this is a range\n      else if ( strstr($values_check, ''-'') && strstr($custom_set_name, ''range'') ) {\n        // keys won''t have the unit ($, £, m<sup>3</sup>, etc.) values will\n        return glz_format_ranges($arr_values, $custom_set_name);\n      }\n      else {\n        // keys and values need to be the same\n        return php4_array_combine($arr_values, $arr_values);\n      }\n\n    }\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_article_custom_fields($name, $extra) {\n  if ( is_array($extra) ) {\n    // see what custom fields we need to query for\n    foreach ( $extra as $custom_set ) {\n      $select[] = glz_custom_number($custom_set[''custom_set'']);\n    }\n    // prepare the select elements\n    $select = implode('','', $select);\n\n    $arr_article_customs = getRow("\n      SELECT\n        $select\n      FROM\n        `".PFX."textpattern`\n      WHERE\n        `ID`=''$name''\n    ");\n\n    return $arr_article_customs;\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_next_empty_custom() {\n  $arr_custom_fields = glz_custom_fields_MySQL("all");\n  foreach ( $arr_custom_fields as $custom ) {\n    if ( empty($custom[''custom_set_name'']) ) {\n      return $custom[''custom_set''];\n    }\n  }\n}\n\nfunction glz_new_custom_field($name, $table, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n    // DRYing up, we''ll be using this variable quiet often\n    $custom_set = ( isset($custom_field_number) ) ? "custom_{$custom_field_number}_set" : $custom_set;\n\n    if ( ($table == PFX."txp_prefs")  ) {\n      $query = "\n        INSERT INTO\n          `".PFX."txp_prefs` (`prefs_id`,`name`,`val`,`type`,`event`,`html`,`position`)\n        VALUES\n          (''1'',''{$custom_set}'',''{$name}'',''1'',''custom'',''{$custom_set_type}'',{$custom_field_number})\n      ";\n    }\n    else if ( $table == PFX."txp_lang" ) {\n      $query = "\n        INSERT INTO\n          `".PFX."txp_lang` (`id`,`lang`,`name`,`event`,`data`,`lastmod`)\n        VALUES\n          ('''',''{$lang}'',''{$custom_set}'',''prefs'',''{$name}'',now())\n      ";\n    }\n    else if ( $table == PFX."textpattern" ) {\n      $query = "\n        ALTER TABLE\n          `".PFX."textpattern`\n        ADD\n          `custom_{$custom_field_number}` varchar(255) NOT NULL DEFAULT ''''\n        AFTER\n          `custom_{$after}`\n      ";\n    }\n    else if ( $table == PFX."custom_fields" ) {\n      $arr_values = array_filter(explode("\\r\\n", $value), ''glz_arr_empty_values'');\n      if ( is_array($arr_values) && !empty($arr_values) ) {\n        // initialize null\n        $insert = '''';\n        foreach ( $arr_values as $value ) {\n          // don''t insert empty values\n          if ( !empty($value) ) {\n            // if this is the last value, query will have to be different\n            $insert .= ( end($arr_values) != $value ) ? "(''{$custom_set}'',''{$value}'')," : "(''{$custom_set}'',''{$value}'')";\n          }\n        }\n        $query = "\n          INSERT INTO\n            `".PFX."custom_fields` (`name`,`value`)\n          VALUES\n            {$insert}\n        ";\n      }\n    }\n    if ( isset($query) && !empty($query) ) safe_query($query);\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_update_custom_field($name, $table, $extra) {\n  if ( is_array($extra) ) {\n    extract($extra);\n\n    if ( ($table == PFX."txp_prefs")  ) {\n      $query = "\n        UPDATE\n          `".PFX."txp_prefs`\n        SET\n          `val` = ''{$custom_set_name}'',\n          `html` = ''{$custom_set_type}''\n        WHERE\n          `name`=''{$name}''\n      ";\n    }\n\n    safe_query($query);\n  }\n  else {\n    trigger_error(glz_custom_fields_gTxt(''not_specified'', array(''{what}'' => "extra attributes")));\n  }\n}\n\nfunction glz_reset_custom_field($name, $table) {\n  if ( $table == PFX."txp_prefs" ) {\n    $query = "\n      UPDATE\n        `".PFX."txp_prefs`\n      SET\n        `val` = '''',\n        `html` = ''text_input''\n      WHERE\n        `name`=''{$name}''\n    ";\n  }\n  else if ( $table == PFX."textpattern" ) {\n    $query = "\n      UPDATE\n        `".PFX."textpattern`\n      SET\n        `{$name}` = ''''\n    ";\n  }\n\n  safe_query($query);\n}\n\nfunction glz_delete_custom_field($name, $table) {\n  // remember, custom fields under 10 MUST NOT be deleted\n  if ( glz_custom_digit($name) > 10 ) {\n    if ( in_array($table, array(PFX."txp_prefs", PFX."txp_lang", PFX."custom_fields")) ) {\n      $query = "\n        DELETE FROM\n          `{$table}`\n        WHERE\n          `name`=''{$name}''\n      ";\n    }\n    else if ( $table == PFX."textpattern" ) {\n      $query = "\n        ALTER TABLE\n          `".PFX."textpattern`\n        DROP\n          `{$name}`\n      ";\n    }\n    safe_query($query);\n  }\n  else {\n    if ( $table == PFX."txp_prefs" ) {\n      glz_custom_fields_MySQL("reset", $name, $table);\n    }\n    else if ( ($table == PFX."custom_fields") ) {\n      $query = "\n        DELETE FROM\n          `{$table}`\n        WHERE\n          `name`=''{$name}''\n      ";\n      safe_query($query);\n    }\n  }\n}\n\n// -------------------------------------------------------------\n// we might have multiple values for custom fields, like must use grep % %\nfunction glz_buildCustomSql($custom,$pairs) {\n  if ($pairs) {\n    $pairs = doSlash($pairs);\n    foreach( $pairs as $k => $v ) {\n      if ( in_array(strtolower($k),$custom) ) {\n        $no = array_keys($custom,strtolower($k));\n        $arr_values = explode(",",$v);\n        if ( is_array($arr_values) ) {\n          if ( count($arr_values) > 1 ) {\n            $custom_query = "AND (custom_{$no[0]} LIKE ''%{$arr_values[0]}%''";\n            for ( $i=1; $i < count($arr_values); $i++ ) {\n              $custom_query .= " OR custom_{$no[0]} LIKE ''%{$arr_values[$i]}%''";\n            }\n            $custom_query .= ")";\n            $out[] = $custom_query;\n          }\n          else {\n            $out[] = "AND custom_{$no[0]} LIKE ''%{$arr_values[0]}%''";\n          }\n        }\n      }\n    }\n  }\n  return (!empty($out)) ? '' ''.join('' '',$out).'' '' : FALSE;\n}\n\n\n\n#####################\n##### FRONT-END #####\n#####################\n\n// -------------------------------------------------------------\n/**\n * DROP-DOWN SEARCH FORM\n Call it like this: <txp:glz_custom_fields_search_form results_page="listings" searchby="Area,City,Price" />\n */\nfunction glz_custom_fields_search_form($atts) {\n  // DEBUG\n  // dmp($GLOBALS[''prefs'']);\n\n  extract(lAtts(array(\n    ''results_page''  => "default",\n    ''searchby''    => "",\n  ),$atts));\n\n  if ( !empty($searchby) ) {\n    // see which customs are searchby values associated to\n    if ( strstr($searchby, ",") ) {\n      // create an array from the searchby values\n      foreach ( explode(",", $searchby) as $custom ) {\n        // get the corresponding custom fields for the searchby values\n        $arr_custom[$custom] = array_search($custom, $GLOBALS[''prefs'']);\n      }\n    }\n    else {\n      // get the custom field for the searchby value\n      $arr_custom[$searchby] = array_search($searchby, $GLOBALS[''prefs'']);\n    }\n    // DEBUG\n    // dmp($arr_custom);\n\n    // start our form\n    $out[] = "<form method=\\"post\\" action=\\"".hu."$results_page\\">".n\n         .hInput("glz_custom_fields_search", 1).n;\n\n    // build our selects\n    foreach ( $arr_custom as $name => $custom ) {\n      // get all existing custom values for live articles\n      $arr_values = glz_custom_fields_MySQL(''all_values'', glz_custom_number($custom), '''', array(''custom_set_name'' => $name));\n\n      if ( is_array($arr_values) ) {\n        // trim ''_set'' from $custom\n        $custom = substr($custom, 0, -4);\n\n        // we are ready to start building the select\n        //"<label for=\\"$custom\\">$name</label>".n.\n        $out[] = "<select name=\\"$custom\\">".n;\n\n        // first value is select name\n        $out[] = "  <option value=\\"$custom\\">Select $name</option>".n;\n        // now let''s go through the values in $arr_values\n        foreach ( $arr_values as $key => $value ) {\n          $out[] = "  <option value=\\"$key\\">$value</option>".n;\n        }\n\n        // end this select\n        $out[] = "</select>".n;\n      }\n      else {\n        return ''<p>No articles with custom fields have been found.</p></form>'';\n      }\n    }\n\n    // end our form\n    $out[] = fInput("submit", "submit", "Submit").n\n         ."</form>".n.n;\n    // DEBUG\n    // dmp(join($out));\n\n    return join($out);\n  }\n  else {\n    return trigger_error(glz_custom_fields_gTxt(''searchby_not_set''));;\n  }\n}\n\n// -------------------------------------------------------------\n// Slightly altered getCustomFields (that silly 10 hard limit...). Internal, no need to call it.\nfunction glz_getCustomFields() {\n  global $prefs;\n  $out = array();\n  $i = 0;\n  $custom_fields_num = count(preg_grep("(^custom_\\d+_set$)", array_keys($prefs)));\n  while ($i < $custom_fields_num) {\n    $i++;\n    if (!empty($prefs[''custom_''.$i.''_set''])) {\n      $out[$i] = strtolower($prefs[''custom_''.$i.''_set'']);\n    }\n  }\n  return $out;\n}\n\n// -------------------------------------------------------------\n// Slightly altered custom_field - multiple values custom fields\nfunction glz_custom_field($atts) {\n  global $thisarticle, $prefs;\n  assert_article();\n\n  extract(lAtts(array(\n    ''name'' => @$prefs[''custom_1_set''],\n    ''escape'' => '''',\n    ''default'' => '''',\n  ),$atts));\n\n  $name = strtolower($name);\n  if (!empty($thisarticle[$name])) {\n    // if this custom field contains |, we need to compare against all the values\n    if ( strstr($thisarticle[$name], ''|'') ) {\n      /*\n        TODO Using a loop here because later on I might want to output a list of values in a specific format (wrap, break etc.)\n      */\n      foreach ( explode(''|'', $thisarticle[$name]) as $value ) {\n        $arr_values[] = $value;\n      }\n      $out = join(", ", $arr_values);\n    }\n    else {\n      $out = $thisarticle[$name];\n    }\n  }\n  else\n    $out = $default;\n\n  return ($escape == ''html'' ? escape_output($out) : $out);\n}\n\n// -------------------------------------------------------------\n// Slightly altered if_custom_field to deal with custom fields > 10 & multiple values\nfunction glz_if_custom_field($atts, $thing) {\n  global $thisarticle, $prefs;\n  assert_article();\n  extract(lAtts(array(\n    ''name'' => @$prefs[''custom_1_set''],\n    ''val'' => NULL,\n  ),$atts));\n  $name = strtolower($name);\n\n  if ($val !== NULL) {\n    $cond = (@$thisarticle[$name] == $val);\n    // if this custom field contains |, we need to compare against all set values\n    if ( strstr($thisarticle[$name], ''|'') ) {\n      foreach ( explode(''|'', $thisarticle[$name]) as $value ) {\n        if ( $val == $value ) {\n          $cond = TRUE;\n          break;\n        }\n      }\n    }\n  }\n  else\n    $cond = !empty($thisarticle[$name]);\n\n  return parse(EvalElse($thing, $cond));\n}\n\n// -------------------------------------------------------------\n/**\n * DEALS WITH ARTICLES - PROPERTIES - LISTS, SLIGHTLY ALTERED TO USE OUR CUSTOM SEARCH VALUES WHEN FILTERING & DISPLAY CUSTOM FIELDS > 10\nCall it like you would article()\nhttp://textpattern.net/wiki/index.php?title=Txp:article_/\n */\nfunction glz_article($atts) {\n  global $is_article_body, $has_article_tag;\n  if ($is_article_body) {\n    trigger_error(gTxt(''article_tag_illegal_body''));\n    return '''';\n  }\n  $has_article_tag = true;\n  return glz_parseArticles($atts);\n}\n\n// -------------------------------------------------------------\n/**\n * DEALS WITH ARTICLES - PROPERTIES - LISTS, SLIGHTLY ALTERED TO USE OUR CUSTOM SEARCH VALUES WHEN FILTERING & DISPLAY CUSTOM FIELDS > 10\nCall it like you would article_cutom()\nhttp://textpattern.net/wiki/index.php?title=Txp:article_custom/\n */\nfunction glz_article_custom($atts) {\n  return glz_parseArticles($atts, ''1'');\n}\n\n// -------------------------------------------------------------\n// this decides what needs to be called, doArticle or doArticles. Using this to call our modified glz_doArticles & glz_doArticle\nfunction glz_parseArticles($atts, $iscustom = '''') {\n  global $pretext, $is_article_list;\n  $old_ial = $is_article_list;\n  $is_article_list = ($pretext[''id''] && !$iscustom)? false : true;\n  article_push();\n  $r = ($is_article_list) ? glz_doArticles($atts, $iscustom) : glz_doArticle($atts);\n  article_pop();\n  $is_article_list = $old_ial;\n\n  return $r;\n}\n\n\n// -------------------------------------------------------------\n// Slightly altered populateArticleData - needed to call glz_getCustomFields instead\nfunction glz_populateArticleData($rs) {\n  extract($rs);\n\n  trace_add("[".gTxt(''Article'')." $ID]");\n  $out[''thisid'']          = $ID;\n  $out[''posted'']          = $uPosted;\n  $out[''modified'']        = $LastMod;\n  $out[''annotate'']        = $Annotate;\n  $out[''comments_invite''] = $AnnotateInvite;\n  $out[''authorid'']        = $AuthorID;\n  $out[''title'']           = $Title;\n  $out[''url_title'']       = $url_title;\n  $out[''category1'']       = $Category1;\n  $out[''category2'']       = $Category2;\n  $out[''section'']         = $Section;\n  $out[''keywords'']        = $Keywords;\n  $out[''article_image'']   = $Image;\n  $out[''comments_count'']  = $comments_count;\n  $out[''body'']            = $Body_html;\n  $out[''excerpt'']         = $Excerpt_html;\n  $out[''override_form'']   = $override_form;\n  $out[''status'']          = $Status;\n\n  $custom = glz_getCustomFields();\n  if ($custom) {\n    foreach ($custom as $i => $name)\n      $out[$name] = $rs[''custom_'' . $i];\n  }\n\n  global $thisarticle;\n  $thisarticle = $out;\n}\n\n// -------------------------------------------------------------\n// using this to "inject" our custom search values\nfunction glz_doArticle($atts) {\n  global $pretext, $prefs, $thisarticle;\n  extract($prefs);\n  extract($pretext);\n\n  extract(gpsa(array(''parentid'', ''preview'')));\n\n  extract(lAtts(array(\n    ''allowoverride'' => ''1'',\n    ''form''          => ''default'',\n    ''status''        => ''4'',\n  ),$atts, 0));\n\n  if ($status and !is_numeric($status))\n  {\n    $status = getStatusNum($status);\n  }\n\n  if (empty($thisarticle) or $thisarticle[''thisid''] != $id)\n  {\n    $thisarticle = NULL;\n\n    $q_status = ($status ? ''and Status = ''.intval($status) : ''and Status in (4,5)'');\n\n    $rs = safe_row("*, unix_timestamp(Posted) as uPosted",\n        "textpattern", ''ID = ''.intval($id)." $q_status limit 1");\n\n    if ($rs) {\n      extract($rs);\n      glz_populateArticleData($rs);\n    }\n  }\n\n  if (!empty($thisarticle) and $thisarticle[''status''] == $status)\n  {\n    extract($thisarticle);\n    $thisarticle[''is_first''] = 1;\n    $thisarticle[''is_last''] = 1;\n\n    $form = ($allowoverride and $override_form) ? $override_form : $form;\n\n    $article = parse_form($form);\n\n    if ($use_comments and $comments_auto_append)\n    {\n      $article .= parse_form(''comments_display'');\n    }\n\n    unset($GLOBALS[''thisarticle'']);\n\n    return $article;\n  }\n}\n\n// -------------------------------------------------------------\n// using this to "inject" our custom search values\nfunction glz_doArticles($atts, $iscustom) {\n  global $pretext, $prefs, $txpcfg;\n  extract($pretext);\n  extract($prefs);\n  $customFields = glz_getCustomFields();\n  $customlAtts = array_null(array_flip($customFields));\n\n  //getting attributes\n  $theAtts = lAtts(array(\n    ''form''      => ''default'',\n    ''listform''  => '''',\n    ''searchform''=> '''',\n    ''limit''     => 10,\n    ''pageby''    => '''',\n    ''category''  => '''',\n    ''section''   => '''',\n    ''excerpted'' => '''',\n    ''author''    => '''',\n    ''sort''      => '''',\n    ''sortby''    => '''',\n    ''sortdir''   => '''',\n    ''month''     => '''',\n    ''keywords''  => '''',\n    ''frontpage'' => '''',\n    ''id''        => '''',\n    ''time''      => ''past'',\n    ''status''    => ''4'',\n    ''pgonly''    => 0,\n    ''searchall'' => 1,\n    ''searchsticky'' => 0,\n    ''allowoverride'' => (!$q and !$iscustom),\n    ''offset''    => 0,\n    ''no_results'' => ''no_results''\n  )+$customlAtts,$atts);\n\n  // if an article ID is specified, treat it as a custom list\n  $iscustom = (!empty($theAtts[''id''])) ? true : $iscustom;\n\n  //for the txp:article tag, some attributes are taken from globals;\n  //override them before extract\n  if (!$iscustom) {\n    if ($c && !empty($theAtts[''category''])) $theAtts[''category''] = $c;\n    $theAtts[''section''] = ($s && $s!=''default'')? $s : '''';\n    $theAtts[''author''] = (!empty($author)? $author: '''');\n    $theAtts[''month''] = (!empty($month)? $month: '''');\n    $theAtts[''frontpage''] = ($s && $s==''default'')? true: false;\n    $theAtts[''excerpted''] = '''';\n  }\n  extract($theAtts);\n\n  $pageby = (empty($pageby) ? $limit : $pageby);\n\n  // treat sticky articles differently wrt search filtering, etc\n  if (!is_numeric($status))\n    $status = getStatusNum($status);\n  $issticky = ($status == 5);\n\n  //give control to search, if necesary\n  if($q && !$iscustom && !$issticky)\n  {\n    include_once txpath.''/publish/search.php'';\n\n    $s_filter = ($searchall ? filterSearch() : '''');\n    $q        = doSlash($q);\n    $match    = ", match (Title,Body) against (''$q'') as score";\n    $search   = " and (Title rlike ''$q'' or Body rlike ''$q'') $s_filter";\n\n    // searchall=0 can be used to show search results for the current section only\n    if ($searchall) $section = '''';\n    if (!$sort) $sort=''score desc'';\n  }\n  else {\n    $match = $search = '''';\n    if (!$sort) $sort=''Posted desc'';\n  }\n\n  // for backwards compatibility\n  // sortby and sortdir are deprecated\n  if ($sortby) {\n    if (!$sortdir) {\n      $sortdir = ''desc'';\n    }\n    $sort = "$sortby $sortdir";\n  }\n\n  elseif ($sortdir) {\n    $sort = "Posted $sortdir";\n  }\n\n  //Building query parts\n  $frontpage = ($frontpage and (!$q or $issticky)) ? filterFrontPage() : '''';\n  $category  = join("'',''", doSlash(do_list($category)));\n  $category  = (!$category)  ? '''' : " and (Category1 IN (''".$category."'') or Category2 IN (''".$category."''))";\n  $section   = (!$section)   ? '''' : " and Section IN (''".join("'',''", doSlash(do_list($section)))."'')";\n  $excerpted = ($excerpted==''y'')  ? " and Excerpt !=''''" : '''';\n  $author    = (!$author)    ? '''' : " and AuthorID IN (''".join("'',''", doSlash(do_list($author)))."'')";\n  $month     = (!$month)     ? '''' : " and Posted like ''".doSlash($month)."%''";\n  $id        = (!$id)        ? '''' : " and ID = ''".intval($id)."''";\n  switch ($time) {\n    case ''any'':\n      $time = ""; break;\n    case ''future'':\n      $time = " and Posted > now()"; break;\n    default:\n      $time = " and Posted <= now()";\n  }\n  if (!is_numeric($status))\n    $status = getStatusNum($status);\n\n  $custom = '''';\n\n  // DEBUG\n  // dmp($customFields);\n  if ($customFields) {\n    foreach($customFields as $cField) {\n      if (isset($atts[$cField]))\n        $customPairs[$cField] = $atts[$cField];\n    }\n\n    $arr_post_custom = preg_grep("(^custom_\\d+$)", array_keys($_POST));\n    // DEBUG\n    // dmp($arr_post_custom);\n    // dmp($_POST);\n    foreach ( $arr_post_custom as $custom ) {\n      // skip if values are the same - users have left the default\n      if ( ps($custom) == $custom ) continue;\n      // if there is a value, add it for the query\n      if ( ps($custom) ) {\n        // need the digit to find out the custom field name\n        list($rubbish, $digit) = explode("_", $custom);\n        $customPairs[$prefs[''custom_''.$digit.''_set'']] = ps($custom);\n      }\n    }\n    // DEBUG\n    // dmp($customPairs);\n\n    if(!empty($customPairs))\n      $custom =  glz_buildCustomSql($customFields,$customPairs);\n  }\n\n  //Allow keywords for no-custom articles. That tagging mode, you know\n  if ($keywords) {\n    $keys = doSlash(do_list($keywords));\n    foreach ($keys as $key) {\n      $keyparts[] = "FIND_IN_SET(''".$key."'',Keywords)";\n    }\n    $keywords = " and (" . join('' or '',$keyparts) . ")";\n  }\n\n  if ($q and $searchsticky)\n    $statusq = '' and Status >= 4'';\n  elseif ($id)\n    $statusq = '' and Status >= 4'';\n  else\n    $statusq = '' and Status = ''.intval($status);\n\n  $where = "1=1" . $statusq. $time.\n    $search . $id . $category . $section . $excerpted . $month . $author . $keywords . $custom . $frontpage;\n  // DEBUG\n  // dmp($where);\n\n  //do not paginate if we are on a custom list\n  if (!$iscustom and !$issticky) {\n    $grand_total = safe_count(''textpattern'',$where);\n    $total = $grand_total - $offset;\n    $numPages = ceil($total/$pageby);\n    $pg = (!$pg) ? 1 : $pg;\n    $pgoffset = $offset + (($pg - 1) * $pageby);\n    // send paging info to txp:newer and txp:older\n    $pageout[''pg'']       = $pg;\n    $pageout[''numPages''] = $numPages;\n    $pageout[''s'']        = $s;\n    $pageout[''c'']        = $c;\n    $pageout[''grand_total''] = $grand_total;\n    $pageout[''total'']    = $total;\n\n    global $thispage;\n    if (empty($thispage))\n      $thispage = $pageout;\n    if ($pgonly)\n      return;\n  } else {\n    $pgoffset = $offset;\n  }\n\n  $rs = safe_rows_start("*, unix_timestamp(Posted) as uPosted".$match, ''textpattern'',\n  $where.'' order by ''.doSlash($sort).'' limit ''.intval($pgoffset).'', ''.intval($limit));\n  // get the form name\n  if ($q and !$iscustom and !$issticky)\n    $fname = ($searchform ? $searchform : ''search_results'');\n  else\n    $fname = ($listform ? $listform : $form);\n\n  if ($rs) {\n    $count = 0;\n\n    $articles = array();\n    while ( $a = nextRow($rs) ) {\n      ++$count;\n\n      glz_populateArticleData($a);\n      global $thisarticle, $uPosted, $limit;\n\n      $thisarticle[''is_first''] = ($count == 1);\n      $thisarticle[''is_last''] = ($count == numRows($rs));\n\n      if (@constant(''txpinterface'') === ''admin'' and gps(''Form'')) {\n        $articles[] = parse(gps(''Form''));\n      }\n      elseif ($allowoverride and $a[''override_form'']) {\n        $articles[] = parse_form($a[''override_form'']);\n      }\n      else {\n        $articles[] = parse_form($fname);\n      }\n\n      // sending these to paging_link(); Required?\n      $uPosted = $a[''uPosted''];\n      $limit = $limit;\n\n      unset($GLOBALS[''thisarticle'']);\n\n    }\n    if ( count($articles) == "0" ) {\n      header(''Location: ''.hu.$no_results);\n    }\n    else {\n      return join('''',$articles);\n    }\n  }\n}\n\n\n\n##########################\n##### PRE-REQUISITES #####\n##########################\n\n// -------------------------------------------------------------\n// checks if custom_fields table exists\nfunction glz_custom_fields_install() {\n  // if jQuery is not present, die\n  // improvement courtesy of Sam Weiss\n  if ( !file_exists($GLOBALS[''txpcfg''][''txpath''].''/jquery.js'') ) die(glz_custom_fields_gTxt(''jquery_missing''));\n\n  // if we don''t have the custom_fields table, let''s create it\n  // let''s also take all custom field values from `textpattern` table and populates the `custom_fields` table\n  if ( !getRows("SHOW TABLES LIKE ''".PFX."custom_fields''") ) {\n    safe_query("\n      CREATE TABLE `".PFX."custom_fields` (\n        `name` varchar(255) NOT NULL default '''',\n        `value` varchar(255) NOT NULL default '''',\n        INDEX (`name`)\n      ) TYPE=MyISAM\n    ");\n    /*\n      TODO Migration/importing functions\n    */\n  }\n}\n\n// -------------------------------------------------------------\n// uninstalls glz_custom_fields by doing the following things:\n// 1. removes all custom fields above 10 in txp_prefs\n// 2. changes the remaining custom fields back to input\n// 3. removes custom_fields table\nfunction glz_custom_fields_uninstall() {\n  /*\n    TODO Uninstall functions\n  */\n}\n\n// -------------------------------------------------------------\n// adds the css & js we need\nfunction glz_custom_fields_css_js($buffer) {\n  $css =<<<css\n<style type="text/css" media="screen">\n    /* - - - - - - - - - - - - - - - - - - - - -\n\n    ### TEXTPATTERN CUSTOM FIELDS ###\n\n    Title : glz_custom_fields stylesheet\n    Author : Gerhard Lazu\n    URL : http://www.gerhardlazu.com/ & http://www.calti.co.uk\n\n    Created : 14th May 2007\n    Last modified: 7th January 2008\n\n    - - - - - - - - - - - - - - - - - - - - - */\n\n\n    /* CLASSES\n    -------------------------------------------------------------- */\n    .green {\n      color: #6B3;\n    }\n    .red {\n      color: #C00;\n    }\n\n\n    /* TABLE\n    -------------------------------------------------------------- */\n    #glz_custom_fields {\n      width: 50em;\n      margin: 0 auto;\n      border: 1px solid #DDD;\n    }\n\n    #glz_custom_fields thead tr {\n      font-size: 1.2em;\n      font-weight: 700;\n      background: #EEE;\n    }\n\n    #glz_custom_fields tbody tr.alt {\n      background: #FFC;\n    }\n    #glz_custom_fields tbody tr.over {\n      background: #FF6;\n    }\n\n    #glz_custom_fields td {\n      padding: 0.2em 1em;\n      vertical-align: middle;\n    }\n    #glz_custom_fields thead td {\n      padding: 0.3em 0.8em;\n    }\n    #glz_custom_fields td.custom_set {\n      width: 8em;\n    }\n    #glz_custom_fields td.custom_set_name {\n      width: 18em;\n    }\n    #glz_custom_fields td.type {\n      width: 6em;\n    }\n    #glz_custom_fields td.events {\n      width: 12em;\n      text-align: right;\n    }\n\n\n    /* FORMS\n    -------------------------------------------------------------- */\n    input:focus,\n    select:focus,\n    textarea:focus {\n      background: #FFC;\n    }\n\n    #glz_custom_fields td.events form {\n      display: inline;\n    }\n\n    #add_edit_custom_field {\n      width: 50em;\n      margin: 2em auto 0 auto;\n    }\n    #add_edit_custom_field legend {\n      font-size: 1.2em;\n      font-weight: 700;\n    }\n    #add_edit_custom_field label {\n      float: left;\n      width: 10em;\n      font-weight: 700;\n    }\n    #add_edit_custom_field p input,\n    #add_edit_custom_field p select {\n      width: 20em;\n    }\n    #add_edit_custom_field p textarea {\n      width: 30em;\n      height: 10em;\n    }\n    #add_edit_custom_field p em {\n      font-size: 0.9em;\n      font-weight: 500;\n      color: #777;\n    }\n    #add_edit_custom_field input.publish {\n      margin-left: 11em;\n    }\n\n    /* select on write tab for the custom fields */\n    td#article-col-1 #advanced p select.list {\n      width: 100%;\n    }\n\n  </style>\ncss;\n\n  $js =<<<js\n<script type="text/javascript">\n  <!--//--><![CDATA[//><!--\n\n  $(document).ready(function() {\n    // sweet jQuery table striping\n    $(".stripeMe tr").mouseover(function() { $(this).addClass("over"); }).mouseout(function() { $(this).removeClass("over"); });\n    $(".stripeMe tr:even").addClass("alt");\n\n    // disable all custom field references in Advanced Prefs\n    var custom_field_tr = $("tr:has(label[for*=custom_]), tr:has(h2:contains(''Custom Fields''))");\n    if ( custom_field_tr ) {\n      $.each (custom_field_tr, function() {\n        $(this).hide();\n      });\n    };\n\n    // toggle custom field value based on its type\n    if ( $("select#type option[@selected]").attr("value") == "text_input" ) {\n      custom_field_value_off();\n    };\n\n    $("select#type").click( function() {\n      if ( $("select#type option[@selected]").attr("value") != "text_input" && !$("textarea#value").length ) {\n        custom_field_value_on();\n      }\n      else if ( $("select#type option[@selected]").attr("value") == "text_input" && !$("input#value").length ) {\n        custom_field_value_off();\n      }\n    });\n\n\n    // ### RE-USABLE FUNCTIONS ###\n\n    function custom_field_value_off() {\n      $("label[@for=value] em").hide();\n      $("textarea#value").remove();\n      $("label[@for=value]").after(''<input id="value" value="no value allowed" name="value"/>'');\n      $("input#value").attr("disabled", "disabled");\n    }\n\n    function custom_field_value_on() {\n      $("label[@for=value] em").show();\n      $("input#value").remove();\n      $("label[@for=value]").after(''<textarea id="value" name="value"></textarea>'');\n    }\n\n  });\n\n  //--><!]]>\n  </script>\njs;\n\n  $find = ''</head>'';\n  $replace = $js.n.t.$css.n.t.$find;\n\n  return str_replace($find, $replace, $buffer);\n}\n', '9d00a6816a47ad0dc81a92c30a4e8334', 1, 5, 0);
INSERT INTO `txp_plugin` (`name`, `status`, `author`, `author_uri`, `version`, `description`, `help`, `code`, `code_restore`, `code_md5`, `type`, `load_order`, `flags`) VALUES
('zem_prblock', 1, 'Threshold State', 'http://thresholdstate.com/', '0.1', 'Applies PR blocking to outgoing links, to reduce the incentive for comment spam', '\n	<p><strong>zem_prblock</strong> reduces the incentive for comment spam by blocking PageRank on outgoing links.</p>\n\n	<p>Comment spam is usually motivated by web site owners trying to improve their ranking in Google search results.  They do this by including links to their web site in comments submitted to many different blogs.  Google interprets these links as meaning that the spammer&#8217;s web site is very popular.</p>\n\n	<p>zem_prblock works by ensuring that Google ignores links to external web sites.  It offers two different methods: javascript redirection, and the rumoured-soon-to-be-announced <a href="http://simon.incutio.com/archive/2005/01/17/relNoFollow">rel=nofollow</a> technique.  The plugin only effects links within a predefined block, and skips local, relative and &#8216;mailto:&#8217; links.  Additionally, it can be used to apply predefined attributes to offsite links, for example to force external links to open in a new window, or to display them in a different colour.</p>\n\n	<p>To use it, edit the Textpattern form named <strong>comments</strong> (not &#8216;comment_form&#8217; &#8211; that&#8217;s the input form), and surround the code with a <code>&lt;txp:zem_prblock&gt; &lt;/txp:zem_prblock&gt;</code> tag, like this:</p>\n\n<code><br />\n&lt;txp:zem_prblock&gt;<br />\n&lt;txp:message /&gt;&lt;br /&gt;<br />\n&lt;small&gt;&amp;#8212; &lt;txp:comment_name /&gt; &amp;#160;&amp;#160; &lt;txp:comment_time /&gt; &amp;#160;&amp;#160; &lt;txp:comment_permlink&gt;#&lt;/txp:comment_permlink&gt;&lt;/small&gt;<br />\n&lt;/txp:zem_prblock&gt;<br />\n</code>\n\n	<p>The tag can also be used in other places, such as an article form, but the benefits are most obvious when used to block links submitted in comments.</p>\n\n	<p><strong>Attributes:</strong></p>\n\n	<ul>\n	<li><strong>type</strong> &#8211; Use <code>type="javascript"</code> to replace links with Javascript redirection; or <code>type="nofollow"</code> to add the <code>rel="nofollow"</code> attribute.  The default is &#8216;nofollow&#8217;.</li>\n		<li><strong>class</strong>, <strong>target</strong>, etc &#8211; These and any other attributes (with the exception of <code>type</code>) will be added to off-site links, overriding the existing link attribute if it was set.  Local and non-http links will not be changed.  For example, this will add <code>target="_blank"</code> to all external links in an article body:</li>\n	</ul>\n\n<code><br />\n&lt;txp:zem_prblock target="_blank"&gt;<br />\n&lt;txp:body /&gt;<br />\n&lt;/txp:zem_prblock&gt;<br />\n</code>\n\n\n\n ', '\n# $Id: zem_prblock.php,v 1.7 2005/01/18 04:36:41 zem Exp $\n\nfunction zem_prblock_replace($attr, $type=''nofollow'') {\n	global $siteurl, $zem_prblock_atts;\n\n	$atts = @splat($attr);\n\n	$link = @$atts[''href''];\n	if ($link) {\n		$url = parse_url($link);\n		$mydomain = preg_replace(''/^www\\./'', '''', $siteurl);\n		$linkdomain = preg_replace(''/^www\\./'', '''', @$url[''host'']);\n\n		if ($linkdomain and @$url[''scheme''] == ''http'' and $linkdomain != $mydomain) {\n			if ($type == ''nofollow'') {\n				$atts[''rel''] = ''nofollow'';\n			}\n			else {\n				$atts[''href''] = ''javascript:;'';\n				$atts[''onClick''] = "window.location.href=''$link''; return false;";\n			}\n\n		if (is_array($zem_prblock_atts))\n			$atts = array_merge($atts, $zem_prblock_atts);\n		}\n	}\n\n	$out = '''';\n	foreach ($atts as $k=>$v) {\n		$out .= '' '' . $k . ''="''.$v.''"'';\n	}\n\n	return ''<a''.$out.''>'';\n}\n\nfunction zem_prblock($atts, $thing) {\n	global $zem_prblock_atts;\n\n	$type = isset($atts[''type'']) ? $atts[''type''] : ''nofollow'';\n	$zem_prblock_atts = $atts;\n	unset($zem_prblock_atts[''type'']);\n\n	$out = parse($thing);\n\n	return preg_replace(''@<a\\s+([^>]+)>@ei'', "zem_prblock_replace(\\"\\\\1\\", \\"$type\\")", $out);\n}\n', '\n# $Id: zem_prblock.php,v 1.7 2005/01/18 04:36:41 zem Exp $\n\nfunction zem_prblock_replace($attr, $type=''nofollow'') {\n	global $siteurl, $zem_prblock_atts;\n\n	$atts = @splat($attr);\n\n	$link = @$atts[''href''];\n	if ($link) {\n		$url = parse_url($link);\n		$mydomain = preg_replace(''/^www\\./'', '''', $siteurl);\n		$linkdomain = preg_replace(''/^www\\./'', '''', @$url[''host'']);\n\n		if ($linkdomain and @$url[''scheme''] == ''http'' and $linkdomain != $mydomain) {\n			if ($type == ''nofollow'') {\n				$atts[''rel''] = ''nofollow'';\n			}\n			else {\n				$atts[''href''] = ''javascript:;'';\n				$atts[''onClick''] = "window.location.href=''$link''; return false;";\n			}\n\n		if (is_array($zem_prblock_atts))\n			$atts = array_merge($atts, $zem_prblock_atts);\n		}\n	}\n\n	$out = '''';\n	foreach ($atts as $k=>$v) {\n		$out .= '' '' . $k . ''="''.$v.''"'';\n	}\n\n	return ''<a''.$out.''>'';\n}\n\nfunction zem_prblock($atts, $thing) {\n	global $zem_prblock_atts;\n\n	$type = isset($atts[''type'']) ? $atts[''type''] : ''nofollow'';\n	$zem_prblock_atts = $atts;\n	unset($zem_prblock_atts[''type'']);\n\n	$out = parse($thing);\n\n	return preg_replace(''@<a\\s+([^>]+)>@ei'', "zem_prblock_replace(\\"\\\\1\\", \\"$type\\")", $out);\n}\n', '63d607226c6054a88c50914af936296f', 0, 5, 0);
